scwin.lfn_getCrInfo = function(sCrNo, sFlg) {
    wconv.clear(DsWs_getCrInfo);
//    SOBJ_getCrInfo.WSDL = gds.gWsdlBasis;
//    if (sFlg == "1") {
//        DsWs_getCrInfo.Xpath = "//*/LTIDA00";
//    } else if (sFlg == "2") {
//        DsWs_getCrInfo.Xpath = "//*/LTIEA00";
//    }
//    var MethodObj = gfn_CreateMethodObj(SOBJ_getCrInfo, "com.inswave.longterm.com.com.task.AXR041Task", "getCrInfo", "", "");
//    gfn_SetParam(MethodObj, "sFlg", sFlg); // 1 설계번호, 2 계약번호
    var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR041Task", "getCrInfo", pageSize, pageNo);
	MethodObj.setId("getCrInfo");
	MethodObj.setParam( "sFlg", sFlg); ;

    if (sFlg == "1") {
//        gfn_SetParam(MethodObj, "sPlanNo", sCrNo);
		MethodObj.setParam( "sPlanNo", sCrNo);
    } else if (sFlg == "2") {
//        gfn_SetParam(MethodObj, "sCrNo", sCrNo);
		MethodObj.setParam( "sCrNo", sCrNo); ;
    }
//    gfn_CallService(SOBJ_getCrInfo, DOBJ_getCrInfo, MethodObj); //trace(replace(gfn_XmlEncoder(DOBJ_getCrInfo.Contents), "><", ">\n<"));

	MethodObj.setUrl("/contract/CBSC102PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/LTIDA00");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_getCrInfo");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

	util.setCompBindRefresh("DsWs_getCrInfo");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_Warning");//바인딩을 해줌
	util.setRowPosition(DsWs_getCrInfo, 0);
};

/**
 * @function	search
 * @desc		고객정보, 주소, 연락처 검색
 * @param		custNo : 주민등록번호/사업자 번호
 * search(custNo, seqNo,nPlrAddrSeq,nPlrTelSeq,nPlrDMAddrSeq);
 **/
scwin.lfn_SearchCstEmail = function(custNo) {
    wconv.clear(DsWs_Cst);
//    SOBJ_Cst.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정 
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Cst, "com.inswave.longterm.cst.cst.task.CAA070Task", "getCustomerInfo", 0, 0);

    // 주민등록번호/사업자번호 세팅
//    gfn_SetParam(MethodObj, "sCustNo", custNo);
//    gfn_CallService(SOBJ_Cst, DOBJ_Cst, MethodObj);
	var MethodObj = new callService("com.inswave.longterm.cst.cst.task.CAA070Task", "getCustomerInfo", pageSize, pageNo);
	MethodObj.setId("getCustomerInfo");
	MethodObj.setParam( "sCustNo", custNo); 

	MethodObj.setUrl("/contract/CBSC102PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/hashtable/data/CUSAA03");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("CUSAA03"), "DsWs_Cst");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

	util.setCompBindRefresh("DsWs_Cst");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_Warning");//바인딩을 해줌
	util.setRowPosition(DsWs_Cst, 0);

	console.log("DsWS_Cst 데이터=========>\n"+
	DsWs_Cst.getAllJSON());
    //trace("\n\n\n\n ***********************" + replace(gfn_XmlEncoder(DOBJ_Cst.Contents), "><", ">\n<") + "\n\n\n\n");
    //trace("\n\n\n\n ***********************" + DsWs_Cst.SaveXML() + "\n\n\n\n");
    var sEmail1Arry;
    if (DsWs_Cst.getCellData(0, "sEmail1") != "") {
        sEmail1Arry = wconv.split(DsWs_Cst.getCellData(0, "sEmail1"), "@");
    } else {
        sEmail1Arry = wconv.split(DsWs_Cst.getCellData(0, "sEmail2"), "@");
    }


    //trace(DsWs_CstInfo.SaveXML());
    if (Div2_edt_CstName.getValue() == "") {
        Div2_edt_CstName.setValue(DsWs_CstInfo.getCellData(0, "sName"));
    }
    console.log("sEmail1Arry 확인============>\n"+sEmail1Arry[0]+sEmail1Arry[0]);
    edt_sEmail.setValue(sEmail1Arry[0]);
    edt_sEmail1.setValue(sEmail1Arry[1]);
};

scwin.lfn_msk_CstNo_OnCharChanged = function(obj, strPreText, strPostText) {
    if (isBizRegNo(obj.getValue()) || wconv.length(obj.getValue()) == 13) {
        scwin.lfn_Search_Async();
    }
};

scwin.Btn_Close_OnClick = function(obj) {
    var ret = "";
    wprom.close(ret);
};

scwin.lfn_Print = function(obj) {

    var promise = Promise.resolve();

    console.log("프린트 출력 rdo_sDmcode 확인 " +rdo_sDmcode);

    if (rdo_sDmcode == "L224") {
        scwin.lfn_RenwlPrem(); //발송전 갱신보험료 입력
    }
    if (chk_Print.getValue() != "1") {
        wprom.alert("프린트 출력을 선택하십시오.");
        return false;
    }

//    scwin.chk_Print();
	promise = promise.then(function(rv) {
		scwin.chk_Print();	//증명서 매칭
	});
	promise = promise.then(function(rtnVal) {
            return scwin.fn_chkPrintView(); //미리보기
        });
    //trace("DS_SAVESENDINFO ==> " + DS_SAVESENDINFO.SaveXML());
//    if (util.getGdsValue("gUserID") == "5160035" || util.getGdsValue("gUserID") == "2030061") {
//	if (util.getGdsValue("gUserID") != "") {
//        promise = promise.then(function(rtnVal) {
//            return scwin.fn_chkPrintView(); //미리보기
//        });
//    } else {
//        scwin.fn_chkPrint();
//    }
    return promise;
};

//---------------------------------------------------------------------------
// function 명 : fn_chkPrint()
// 내용        : 증명서발급
//---------------------------------------------------------------------------
scwin.fn_chkPrint = function(obj) {
    //심사정보복사
    //DS_SAVESENDPRINT.copy(parent.DS_SAVESENDPRINT);	
    //	if (DS_SAVESENDPRINT.GetCount() > 0){
    for (var i = 0; i < DS_SAVESENDPRINT.getRowCount(); i++) {
        var sOdiId = DS_SAVESENDPRINT.getCellData(i, "sFileName");
        var sPlanNo = DS_SAVESENDPRINT.getCellData(i, "sPlanNo");
        var sPsnGroupFlgcd = DS_SAVESENDPRINT.getCellData(i, "sPsnGroupFlgcd");
        var sGdCd = DS_SAVESENDPRINT.getCellData(i, "sGdCd");
        var copies = DS_SAVESENDPRINT.getCellData(i, "copies");
        var sDmCode = DS_SAVESENDPRINT.getCellData(i, "sDmCode");
        var sIssID = DS_SAVESENDPRINT.getCellData(i, "sIssID");
        //var sPlanNo = parent.medt_PlanNo.Value;
        var strParam;
        strParam = "viewer.namespace=KW_Testozviewer\n";
        strParam += "connection.compressedform=true\n";
//        strParam += "connection.servlet=" + gds.gLtiUrl + "/newoz/server\n";
        strParam += "connection.servlet=" + util.getGdsValue("gLtiUrl") + "/newoz/server\n";
        strParam += "connection.reportname=/longterm/" + sOdiId + ".ozr\n";
        //	strParam += "odi.odinames=/longterm/" + sOdiId + "\n";
        strParam += "odi.odinames=" + sOdiId + "\n";
        strParam += "information.debug=true\n";
        strParam += "viewer.isframe=false\n";
        strParam += "toolbar.all=false\n"; //툴바 감춤
        strParam += "viewer.useprogressbar=false\n"; //프로그래스바 감춤
        strParam += "viewer.mode =print"; //미리보기없이 바로 인쇄
        strParam += "print.copies=" + copies + "\n"; //인쇄매수설정
        //strParam += "print.copies ="+v_pageCnt;	//인쇄매수설정
        strParam += "print.once=false\n"; //보고서인쇄를 한번만 할지를 설정
        strParam += "print.printername=DEFAULT_PRINTER\n"; //인쇄할 프린터설정
        strParam += "print.size=A4\n"; //인쇄용지크기	
        strParam += "print.mode=silent\n"; //인쇄창 없이 바로 인쇄	
        if (sDmCode == "L224")
            strParam += "odi." + sOdiId + ".pcount=4\n";
        else
            strParam += "odi." + sOdiId + ".pcount=3\n";
        strParam += "odi." + sOdiId + ".args1=sPlanNo=" + sPlanNo + "\n";
        strParam += "odi." + sOdiId + ".args2=sDmCode=" + sDmCode + "\n";
        strParam += "odi." + sOdiId + ".args3=sIssID=" + sIssID + "\n";
        if (sDmCode == "L224")
            strParam += "odi." + sOdiId + ".args4=sGdCd=" + sGdCd + "\n";


        //alert(strParam);
        //gfn_beforOz(this, strParam);	  
        scwin.lfn_beforOz(this, strParam);
    }
};

//---------------------------------------------------------------------------
// function 명 : fn_chkPrintView()
// 내용        : 증명서발급미리보기
//---------------------------------------------------------------------------
scwin.fn_chkPrintView = function(obj) {


    var taskValue = "";
    var actionValue = "";   
	//기간계 서비스 정보 조회
	var repoService = scwin.getRepoServiceNm(DS_SAVESENDPRINT.getCellData(0, "sFileName")).split("^");

	taskValue = repoService[0];
	actionValue = repoService[1];

	if(taskValue == "" && actionValue == ""){
		wprom.alertF("해당 계약/설계건은 본 화면에서 증명서 발급이 불가 합니다.\n장기 업무팀에 문의 주세요.", {});	
		return;	
	}else{
	    var setParam;
	    var reportInfo  = {};

		for(var i=0; i < DS_SAVESENDPRINT.getRowCount(); i++){

	  		setParam = [
	  			 "sFileName|" 		+ DS_SAVESENDPRINT.getCellData(i, "sFileName")
	 			//,"sOdiId:" 		+ DS_SAVESENDPRINT.getCellData(i, "sOdiName")	  
	 			,"sPlanNo|" 		+ DS_SAVESENDPRINT.getCellData(i,"sPlanNo")	
				,"sGdCd|" 			+ DS_SAVESENDPRINT.getCellData(i, "sGdCd")	 
				,"sDmCode|" 		+ DS_SAVESENDPRINT.getCellData(i, "sDmCode")	   
				,"sIssID|" 			+ DS_SAVESENDPRINT.getCellData(i, "sIssID")		 
//				,"sCrNo|" 			+ DS_SAVESENDPRINT.getCellData(i,"sCrNo")		  
//				,"sGubun|" 			+ DS_SAVESENDPRINT.getCellData(i, "sGubun")
				,"sPsnGroupFlgcd|"	+ DS_SAVESENDPRINT.getCellData(i, "sPsnGroupFlgcd")
				,"copies|" 			+ DS_SAVESENDPRINT.getCellData(i, "copies")  
		 	];
		 	console.log(
		 	 "sFileName|" 		+ DS_SAVESENDPRINT.getCellData(i, "sFileName")+"\n"+
	 			//,"sOdiId:" 		+ DS_SAVESENDPRINT.getCellData(i, "sOdiName")	  
	 			"sPlanNo|" 		+ DS_SAVESENDPRINT.getCellData(i,"sPlanNo")	+"\n"+
				"sGdCd|" 			+ DS_SAVESENDPRINT.getCellData(i, "sGdCd")	 +"\n"+
				"sDmCode|" 		+ DS_SAVESENDPRINT.getCellData(i, "sDmCode")	   +"\n"+
				"sIssID|" 			+ DS_SAVESENDPRINT.getCellData(i, "sIssID")		 +"\n"+
//				"sCrNo|" 			+ DS_SAVESENDPRINT.getCellData(i,"sCrNo")		  
//				"sGubun|" 			+ DS_SAVESENDPRINT.getCellData(i, "sGubun")
				"sPsnGroupFlgcd|"	+ DS_SAVESENDPRINT.getCellData(i, "sPsnGroupFlgcd")+"\n"+
				"copies|" 			+ DS_SAVESENDPRINT.getCellData(i, "copies") 
		 	);


			reportInfo.rptName        	= "longterm/"+ DS_SAVESENDPRINT.getCellData(i, "sFileName");	//레포트 파일명       (필수) ex) "sc/A0001" 
			reportInfo.dataType       	= "xml";					//인터페이스 Task Value      ex) com.inswave.admin.task.JsonConvertorTask
			reportInfo.sInterfaceType 	= "Y";						//인터페이스 Action Value    ex) sample_PrintEndObjectList 
			reportInfo.taskValue 		= taskValue;				// 인터페이스: B640_Rep.ozr(단체명세서	장기보험)
			reportInfo.actionValue 		= actionValue;				//인터페이스 사용유무 (필수) ex) "Y" or "N" 
		 	reportInfo.gUserID        	= util.getGdsValue("gUserID");			 		//레포트 하단에 출력될 발급자명 ex) "홍길동"
			reportInfo.sDirectPrint     = "0";						//바로출력 1: 바로출력 바로출력할경우 EXE설치파일 설치 필요 ex) "1" or "0"
			reportInfo.setParam  		= setParam;					//인테페이스에서 사용하는 파라미터 정보 배열로 파라미터 개수만큼 입력한다.
			reportInfo.sFormParam  		= setParam;					//레포트 에서 사용하는 파라미터 정보 배열로 파라미터 개수만큼 입력한다.
			reportInfo.title    	 	= DS_SAVESENDPRINT.getCellData(i, "sContents");				//팝업창 타이틀
			reportInfo.export

		 	console.log("rptName : " + "longterm/"+ DS_SAVESENDPRINT.getCellData(i, "sFileName")+"\n"
		 		+"dataType : " + "xml"+"\n"
		 		+"sInterfaceType : " + "Y"+"\n"
		 		+"taskValue : " + taskValue+"\n"
		 		+"actionValue : " + actionValue+"\n"
		 		+"gUserID : " + util.getGdsValue("gUserID")+"\n"
		 		+"sDirectPrint : " + "0"+"\n"
		 		+"setParam : " + setParam+"\n"
		 		+"sFormParam : " + setParam+"\n"
		 		+"title : " + DS_SAVESENDPRINT.getCellData(i, "sContents")+"\n"
		 		);	 	
			wprom.reportOpen(reportInfo);	     
		}
	}
};

