  	else if( ozName == "B955_Rep"){ taskValue = " "; actionValue = " ";}//계약변경(배서)신청서   	
  	else if( ozName == "B965_Rep"){ taskValue = " "; actionValue = " ";}//환급금청구서           	
  	else if( ozName == "B970_Rep"){ taskValue = " "; actionValue = " ";}//수익자 변경 신청서     	
  	else if( ozName == "B960_Rep"){ taskValue = " "; actionValue = " ";}//질권설정(해제)신청서   	
  	else if( ozName == "B975_Rep"){ taskValue = " "; actionValue = " ";}//제지급금 지급 동의서   	
  	else if( ozName == "B980_Rep"){ taskValue = " "; actionValue = " ";}//대표 수익자 지정 동의서	
  	else if( ozName == "B985_Rep"){ taskValue = " "; actionValue = " ";}//위임장                 	
  	else if( ozName == "B995_Rep"){ taskValue = " "; actionValue = " ";}//보험계약대출신청서     	  	

	else if( ozName == "L022_Rep"){ taskValue = " "; actionValue = " ";}   //대출약정서                                                                                 
	else if( ozName == "L231_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE240Task"; actionValue = "printL231_RepV01";} //대출원리금납입내역서                       
	else if( ozName == "L232_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE230Task"; actionValue = "printL232_RepV01";} //부채잔액증명의뢰서


	console.log("레포트=====================>\n"
		+"ozName:"+ozName+"/\n"
		+"taskValue:"+taskValue+"/\n"
		+"actionValue:"+actionValue+"/\n"
		+"====================================\n"
		);

	return taskValue+"^"+actionValue;
};




//*************************************
//  oz 컴포넌트  동적으로 binding
//*************************************
scwin.lfn_beforOz = function(awForm, strParam) {
    if (chk_test.Value == "1") {
        wprom.alert(strParam);
        return;
    }
    var oOz = null;
    var oZtransfer = null;

    //마우스 커서 Wait상태설정
    SetWaitCursor(true);
    SetCapture();

    //ZTransferX 컨트롤 생성
    oOz = find("EXOZViewer40_OCX");
    oZtransfer = find("ZtransferX_OCX");
    if (oOz != null) {
        Destroy("EXOZViewer40_OCX");
        oOz = null;
    }
    if (oZtransfer != null) {
        Destroy("ZtransferX_OCX");
        oZtransfer = null;
    }



    //ZTransferX 컨트롤 생성
    Create("ZTransferX", "ZtransferX_OCX", "left=\"0\" top=\"0\" width=\"0\" height=\"0\"");
    oZtransfer = find("ZtransferX_OCX");
    ZtransferX_OCX.InstallNamespace = "KW_Test";
    ZtransferX_OCX.InstallBase = "<PROGRAMS>/Forcs";

    //ZtransferX_OCX.InstallRegistOnly = true;
    if (indexOf(gds.gGenUrl, "http://10.1.1.129") > -1 || indexOf(gds.gGenUrl, "http://10.1.1.131") > -1) {
        ZtransferX_OCX.DownloadServer = "http://10.1.1.129/newozviewer/";
        ZtransferX_OCX.DownloadPort = "8182";
    } else {
        ZtransferX_OCX.DownloadServer = "http://10.1.1.60/newozviewer/";
        ZtransferX_OCX.DownloadPort = "8000";
    }
    ZtransferX_OCX.DownloadInstruction = "ozrviewer.idf";
    ZtransferX_OCX.StartInstall();


    //오즈 컨트롤 존재 여부 확인
    oOz = find("EXOZViewer40_OCX");


    //오즈 컨트롤 생성
    create("EXOZViewer40", "EXOZViewer40_OCX", "left=\"0\" right=\"0\" top=\"0\" width=\"0\" height=\"0\" bottom=\"0\"");
    EXOZViewer40_OCX.CreateReport(strParam);
    EXOZViewer40_OCX.OZPostCommand = "EXOZViewer400_OZPostCommand";


    //오즈 컨트롤 생성시까지 대기
    while (wconv.isEmpty(awForm)) {
        oOz = find("EXOZViewer40_OCX");
        if (oOz != null)
            break;
        Idle();
    }

    //마우스 커서 입력상태로 설정
    ReleaseCapture();
    SetWaitCursor(false);
};

//---------------------------------------------------------------------------
// function 명 : lfn_RenwlPrem
// 내용        : 갱신보험료 산출처리
//---------------------------------------------------------------------------
scwin.lfn_RenwlPrem = function() {

    //데이터셋 초기화
    wconv.clear(DsWs_RenwlPrem);
//    SOBJ_RenwlPrem.WSDL = gds.gWsdlBasis; //기본 웹서비스
//    DsWs_Warning.DataObject = "DOBJ_RenwlPrem";
//    var MethodObj = gfn_CreateMethodObj(SOBJ_RenwlPrem, "com.inswave.longterm.ssr.sst.task.DBB190Task", "getRenwlPremList", "", "");
//    gfn_SetParam(MethodObj, "sPlanNo", parent.parent.medt_PlanNo.Value);
//    gfn_SetParam(MethodObj, "sNewCalc", "Y");
//    gfn_SetParam(MethodObj, "gUserID", gds.gUserID);
//    gfn_SetParam(MethodObj, "gUserIP", gds.gUserIP);
	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB190Task", "getRenwlPremList", pageSize, pageNo);
	MethodObj.setId("getRenwlPremList");
//	MethodObj.setParam( "sPlanNo", parent.parent.medt_PlanNo.getValue()); 
	MethodObj.setParam( "sPlanNo", sPlanNo.getValue());
	MethodObj.setParam( "sNewCalc", "Y");
	MethodObj.setParam( "gUserID", util.getGdsValue("gUserID"));
	MethodObj.setParam( "gUserIP", util.getGdsValue("gUserIP"));

    if (sPsnGroupFlgcd == "1") {
//        gfn_SetParam(MethodObj, "sPsnGroupFlgcd", "20");
		MethodObj.setParam( "sPsnGroupFlgcd", "20");
    } else {
//        gfn_SetParam(MethodObj, "sPsnGroupFlgcd", "10");
		MethodObj.setParam( "sPsnGroupFlgcd", "10");
    }
//    var result = gfn_CallService(SOBJ_RenwlPrem, DOBJ_RenwlPrem, MethodObj);
	MethodObj.setUrl("/contract/CBSC102PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/vector/data/LTIDA19");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA19"), "DsWs_RenwlPrem");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

	util.setCompBindRefresh("DsWs_RenwlPrem");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_Warning");//바인딩을 해줌
	util.setRowPosition(DsWs_RenwlPrem, 0);
    if (result == false) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }
};
