scwin.init = function() {
    if (wprom.isPopup()){
		div_crud.hide();
	}else{	
	    //버튼 처리(입력, 수정, 삭제, 결재, 취소, 인쇄, 엑셀, 닫기)
		gfn_btnEnable(false, false, false, false, false, false, false, true);
	}

	Div2_msk_CstNo.setStyle("display", "none");	
	Div2_btn_sCstSerch.setStyle("display", "none");
	Div2_title3.setStyle("display", "none");
	Div2_edt_CstName.setStyle("display", "none");	

	//2차 오픈 대상건
	//group3.setStyle("display" ,"none");
	//group4.setStyle("display" ,"none");
	grLms.setStyle("display" ,"none");//알림톡 붙이면 제거해야함.

//	rdo_L236.setStyle("display", "none");	
//	rdo_L237.setStyle("display", "none");	
//	rdo_L238.setStyle("display", "none");	
//	rdo_L239.setStyle("display", "none");
//	rdo_L240.setStyle("display", "none");	
//	rdo_L241.setStyle("display", "none");	
//	rdo_L242.setStyle("display", "none");	
//	rdo_L243.setStyle("display", "none");	

	cmb_linkMenu.setStyle("display" ,"none");	
	cmb_sPrintReason.setStyle("display" ,"none");
	cmb_GdList.setStyle("display" ,"none");		
	cmb_GdList.setValue(0);	
	cmd_SearchStd.deleteItem(2);//고객번호 제외 - 정한진주임요청
	gr_Loan.setStyle("display" ,"none");
	gr_ndsm.setStyle("display" ,"none");
	rdo_L228.setStyle("display", "none");	
	rdo_L147.setStyle("display", "none");	

	util.eventAsyncCall(this, scwin.init_start, arguments);
}

scwin.init_start = function() {

    sToday = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 	// global 현재일자
    sTime  = WebSquare.date.getCurrentServerDate("HHmmss");
    gProdYN = util.getGdsValue("gProdYN");
    gUserID = util.getGdsValue("gUserID");
    gUserIP = util.getGdsValue("gUserIP");
    gDoer 	= util.getGdsValue("gDoer");   
    gFaceSalesYN = util.getGdsValue("gFaceSalesYN");   
    gLtiUrl = util.getGdsValue("gLtiUrl");
    gUserEmpName = util.getGdsValue("gUserEmpName");       
    gUserDepartName = util.getGdsValue("gUserDepartName");

    //조회기준 선택 setting
	cmd_SearchStd.setValue("1");


    var maskRegExp = [ /^[A-Z\s]{1}[0-9\s]{4}[0-9\s]{7}$/,
    						/^[0-9\s]{6}$/,
    							/^[0-9\s]{6}[0-9\s]{1}[A-Z0-9\s]{6}$/
    								,/^[0-9A-Z\s]{7}$/
    									,/^[ㄱ-ㅎㅏ-ㅣ가-힣A-Za-z0-9\s]{20}$/ ];
	var maxLength  = [12,6,13,7,20];
	var chkIndex   = 0;	
	Div1_msk_sCrNo.setPlaceholder( "");
	Div1_msk_sCrNo_Event = new wMaskEditObj(Div1_msk_sCrNo, maskRegExp, maxLength, chkIndex);	
	Div1_msk_sCrNo.setPlaceholder("_-____-_______");
	Div1_msk_sCrNo.setDisplayFormat("#-####-#######");
	Div1_msk_sCrNo_Event.setIndex(0);	

    //공통코드조회
	scwin.lfn_getCommonCodes();	


	scwin.lfn_initPrintControl();

	//-----------------------------------------------------
	// 계약조회 연결
	//-----------------------------------------------------	
	if(gds.gDs_LinkArg.getRowCount() != 0) {
		sCrNo= gds.gDs_LinkArg.getCellData(0, "sCrNo");		

		if(wconv.length(sCrNo) ==0){
			alert("계약번호가 없습니다.");			
			return;
		}		
		if(wconv.length(sCrNo) !=12){
			alert("계약번호가 정확하지 않습니다");
			return;
		}

    	//조회기준 선택 setting
		cmd_SearchStd.setValue("1");
		Div1_msk_sCrNo.setValue(sCrNo);

		scwin.cmd_SearchStd_onviewchange();   				
		scwin.btn_Search_onclick(); //  조회
		//wconv.clear(gds.gDs_LinkArg);
		gds.gDs_LinkArg.removeAll();
	}

	//-----------------------------------------------------
	// 청약쪽 연결 
	//-----------------------------------------------------
	tab = "";
 	if (wprom.isPopup()){
		var indata = $w.getParameter( "param" );
		fv_args = JSON.parse(indata);
		tab =  fv_args.tab;		

		if (wconv.trim(tab) == 1 || wconv.trim(tab) == 3) {
			scwin.rdoDisabled();
			sPlanNo = fv_args.sPlanNo;

			if (wconv.trim(sPlanNo) == "" || wconv.trim(sPlanNo) == null) {
				alert("설계번호를 입력하십시요.");
			} else {			 										
					cmd_SearchStd.setValue("2");
					Div1_msk_sCrNo.setValue(sPlanNo);	
					scwin.cmd_SearchStd_onviewchange();
					scwin.btn_Search_onclick(); //  조회   			
			}
		}	 
 	}	
	sSrdoIssuDate = sSrdoIssuDate;
};


//---------------------------------------------------------------------------
// tobe : scwin.chkEssnInpt
// asis : chkEssnInpt
// 내용 : 필수입력 check
//---------------------------------------------------------------------------
scwin.chkEssnInpt = function() {

	if(cmd_SearchStd.getValue() == "1" || cmd_SearchStd.getValue() == "2"){

		var sCrNoLen = wconv.length(Div1_msk_sCrNo.getValue());		
		if(sCrNoLen == 0 && cmd_SearchStd.getValue() == "1") {
		    wprom.alertF("계약번호 를 입력하세요.",{},Div1_msk_sCrNo);
        	return false;	
		}
		if(sCrNoLen == 0 && cmd_SearchStd.getValue() == "2") {
			wprom.alertF("설계번호 를 입력하세요.",{},Div1_msk_sCrNo);			
			return false;
		}	
		if(sCrNoLen < 12 && cmd_SearchStd.getValue() == "1") {
			wprom.alertF(Div1_msk_sCrNo.getValue() + " 계약번호는 12자리 입니다.",{},Div1_msk_sCrNo);
			return false;
		}
		if(sCrNoLen < 12 && cmd_SearchStd.getValue() == "2") {
			wprom.alertF(Div1_msk_sCrNo.getValue() + " 설계번호는 12자리 입니다.",{},Div1_msk_sCrNo);			
			return false;
		}				

		//계약번호 권한체크
		if(cmd_SearchStd.getValue() == "1"){
			if(gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", Div1_msk_sCrNo.getValue()) == false){
				if(nfn_checkAuthByID("ds_code", gUserID , "22030") == false){
					wprom.alertF("해당 계약의 조회 권한이 없습니다.",{},Div1_msk_sCrNo);					
					return false;
				}
			}
		}

		//설계번호 권한체크
		if(cmd_SearchStd.getValue() == "2"){			
			if(gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "1", Div1_msk_sCrNo.getValue()) == false){			
				if(nfn_checkAuthByID("ds_code", gUserID , "22030") == false){
					wprom.alertF("조회 권한이 없습니다.",{},Div1_msk_sCrNo);
					return false;
				}
			}
		}				
	}else if(cmd_SearchStd.getValue() == "3"){								
		if(Div2_msk_CstNo.getValue() == "") {
			wprom.alertF("고객코드가 입력되지 않았습니다.",{},Div1_msk_sCrNo);			
			return false;
		}else if(wconv.length(Div2_msk_CstNo.getValue()) != 10 
				&& wconv.length(Div2_msk_CstNo.getValue()) != 13) {
			wprom.alertF("고객코드가 잘 못 입력되었습니다.",{},Div1_msk_sCrNo);			
			return false;
		}	
	}	
	return true;
};

//---------------------------------------------------------------------------
// tobe : scwin.btn_Search_onclick()
// asis : lfn_Search()
// 내용 : 조회버튼 클릭시
//---------------------------------------------------------------------------	
scwin.btn_Search_onclick = function() {

	scwin.lfn_initPrintControl();
	auth_yn = "Y";

	//필수입력체크
	if(scwin.chkEssnInpt() == false){ 
		return;
	}else{
		var strCode = cmd_SearchStd.getValue();
		if(strCode == "1" || strCode == "2"){  //1:계약번호, 2:설계번호
			scwin.lfn_SearchData();	
		}else if(strCode == "3"){  //3:고객번호
			scwin.lfn_SearchCstEmail(Div2_msk_CstNo.getvalue());	
		}	

//		 if(Ds_LTIEA00.getCellData(0, "sInsurStrtdate") <= "20160101"){
//		 	scwin.lfn_initPrintControl();
//		  	alert("해당 계약/설계건은 본 화면에서 증명서 발급이 불가 합니다.\n" +"장기 업무팀에 문의 주세요.\n" +"개시일 : "+Ds_LTIEA00.getCellData(0, "sInsurStrtdate") );
//		  	
//		  	btn_print.setDisabled(true);
//		  	btn_send.setDisabled(true);
//		  	return false;
//		  	
//		 }else{		  	
			if(auth_yn != "N"){
		  		btn_print.setDisabled(false);
		  		btn_send.setDisabled(false);

				//라디오버튼 초기화
				scwin.rdoInit();
				scwin.lfn_gFaceSalesYN();			
				gfn_LogCreditInfoVt(Ds_LTIEA00, "", "S", "sCrtorCd");
			}else{
				scwin.lfn_initPrintControl();
			}			
//		 }
	}
};

//---------------------------------------------------------------------------
// tobe : scwin.btn_search_sCr_onclick
// asis : btn_search_sCr_OnClick
// 내용 : 조회기준 변경시 div setting
//---------------------------------------------------------------------------		  
scwin.btn_search_sCr_onclick = function() {

    var pro = Promise.resolve();
    //D011_Pop
    if (cmd_SearchStd.getValue() == "1" 
    	|| cmd_SearchStd.getValue() == "2") {     			
		pro = pro.then(function(rv) {			
			var indata  = {};				
			var popInfo = {
				"width"     : 730,
				"height"    : 650,  
				"indata"    : indata,
				"title"     : "계약찾기[CBSC031P]",
				"callback"  : ""
			};					
			return wprom.popupOpen("/ui/cbsc/CBSC031P.xml", popInfo);
		});			
		pro = pro.then(function(rv) {			
			var pObj = wprom.getReturnData();			

			if(wconv.length(pObj)>0){
				var resultArr = pObj.split("^");							
				Div1_msk_sCrNo.setValue(resultArr[0]); //계약번호 setting
				Div1_edt_sGdNm.setValue(resultArr[1]); //상품명 setting
				Div1_edt_sCrtorName.setValue(resultArr[2]); //계약자 setting
			}
			return;											
		});

	//A125_Pop					   
    } else if (cmd_SearchStd.getValue() == "3") {       
    	var indata={
 				  "sPsnCorpFlag": "1"
 				 ,"sCdNameFlag"	: "Cd"
 				 ,"sSerchKey"	: Div2_msk_CstNo.getvalue()
			};  
        pro = pro.then(function(rv) {						
			var popInfo = {
				"width"     : 660,
				"height"    : 552,  
				"indata"    : indata,
				"title"     : "고객찾기[CBSO004P]",
				"callback"  : ""
			};					
			return wprom.popupOpen("/ui/cbso/CBSO004P.xml", popInfo);			            
        });
		pro = pro.then(function(rv) {
			var pObj = wprom.getReturnData();						
			if(wconv.length(pObj)>0){
				var resultArr = pObj.split("^");						
				var custKey = scwin.lfn_getRealNo(resultArr[0]);
				if(!util.isNull(custKey)){						
					Div2_msk_CstNo.setValue(custKey); 
				}else{
					Div2_msk_CstNo.setValue(resultArr[0]); 
				}														
				Div2_edt_CstName.setValue(resultArr[1]);

				if(wconv.length(Div2_msk_CstNo)>0){
					scwin.lfn_Search();
				}									
			}							
			return;
		});	
    }    
    return pro;	
};

//---------------------------------------------------------------------------
// tobe : scwin.lfn_getCommonCodes
// asis : lfn_getCommonCodes
// 내용 : 허용값 조회
//---------------------------------------------------------------------------
scwin.lfn_getCommonCodes = function() {

    //email 데이터 콤보 리스트 조회    
    nfn_getCommonData("Ds_Email", "1010", "cmb_Email", "Y");

	//장기공통코드
	gfn_getCommonCodes("DsWs_DmMethod","DmMethod","cmb_DmMethod","S");		

	//DsWs_DmMethod.Filter("sCd!='02' && sCd!='04'");  ★TODO
	cmb_DmMethod.deleteItem(2);
	cmb_DmMethod.deleteItem(3);	


	// 오류처리
	if(DsWs_Code_Warning.getRowCount() != 0){
		alert(DsWs_Code_Warning.getCellData( 0 , "msg"));
		return;
	}	
};


//---------------------------------------------------------------------------
// tobe : scwin.lfn_SearchData()
// asis : lfn_SearchData()
// 내용 : 설계/계약정보 조회
//---------------------------------------------------------------------------
scwin.lfn_SearchData = function() {	

	wconv.clear(Ds_LTIEA00);
	wconv.clear(Ds_LTIEA00_Copy);
	wconv.clear(DsWs_LTIEA00);
	scwin.dataInit();			

	if(scwin.lfn_chkAccLmt(Div1_msk_sCrNo.getValue()) ){

		var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EBE114TASK", "selectCertiIssueInfo","","");
		MethodObj.setId("selectCertiIssueInfo");	
		MethodObj.setUrl("/sales/CBSC103MController.do");
		MethodObj.setXPath("//*/WARNING");
		MethodObj.addXPath("//*/vector/data/LTIEA00ADDR");
		MethodObj.setParam("sSearchStd", cmd_SearchStd.getValue());//조회기준

		if(cmd_SearchStd.getValue() == "1") {		
			MethodObj.setParam("sCrNo", Div1_msk_sCrNo.getValue());//계약번호		
		} else if(cmd_SearchStd.getValue() == "2") {		
			MethodObj.setParam("sPlanNo", Div1_msk_sCrNo.getValue());//설계번호		
		} 	
		MethodObj.sync();
		MethodObj.send();

		//console.log(JSON.stringify(_tranSync.result));
	   // util.interfaceKey(_tranSync.result.serviceDs);	
		DsWs_LTIEA00.setJSON (_tranSync.getDsJson("LTIEA00ADDR")); // dataset 데이터
		DsWs_Warning.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터

		if(wconv.length(DsWs_Warning.getCellData(0,"msg")) > 0) {
			alert(DsWs_Warning.getCellData(0,"msg"));	
		} else {				
			if(DsWs_LTIEA00.getRowCount() == 0){
				auth_yn = "N";
				alert("해당 계약(설계)번호 정보가 없습니다.");	

				edt_sEmail.setValue("");
				edt_sEmail1.setValue("");			

				if(gProdYN == "Y") {			
					edt_fax1.setValue("");
					edt_fax2.setValue("");
					edt_fax3.setValue("");			
				}		

			} else {
				wconv.copy(Ds_LTIEA00, DsWs_LTIEA00);
				wconv.copy(Ds_LTIEA00_Copy, DsWs_LTIEA00);

				sAddrFlgcd = Ds_LTIEA00.getCellData(0, "sMailRvadAddrFlagCd");  // 주소구분코드
				nChangeDMAddrNo = Ds_LTIEA00.getCellData(0, "nMailRvadChngSeq");// 주소변경회차			
				nChangeDMTelNo = Ds_LTIEA00.getCellData(0, "nTelnoChngSeq");    // 전화번호변경회차

				sPlanNo     = Ds_LTIEA00.getCellData(0, "sPlanNo");//청약번호
				console.log("**********lfn_SearchData - sPlanNo:"+sPlanNo);
				sCrNo_print = Ds_LTIEA00.getCellData(0, "sCrNo");
				//sRealCustNo = Ds_LTIEA00.getCellData(0, "sRealCustNo"); //2016.05.02 주민번호
				//gFaceSalesYN = Ds_LTIEA00.getCellData(0, "sScanObjtYn");    // 대면비대면여부

				//param setting
				edt_ContName.setValue(Ds_LTIEA00.getCellData(0, "sCrtorName"));
				msk_HomeZipCode1.setValue(Ds_LTIEA00.getCellData(0, "sZip1"));
				msk_HomeZipCode2.setValue(Ds_LTIEA00.getCellData(0, "sZip2"));
				edt_HomeVilAddr.setValue(Ds_LTIEA00.getCellData(0, "addrs"));
				edt_HomeDtlAddr.setValue(Ds_LTIEA00.getCellData(0, "sAdrsAdd"));			
				Div1_edt_sGdNm.setValue(Ds_LTIEA00.getCellData(0, "GdNm"));//상품명
				Div1_edt_sCrtorName.setValue(Ds_LTIEA00.getCellData(0, "sCrtorName"));//계약자명		
				edt_hp1.setValue(Ds_LTIEA00.getCellData(0, "sCellPhone1"));//핸드폰번호
				edt_hp2.setValue(Ds_LTIEA00.getCellData(0, "sCellPhone2"));//핸드폰번호
				edt_hp3.setValue(Ds_LTIEA00.getCellData(0, "sCellPhone3"));//핸드폰번호		

				if(wconv.length(Ds_LTIEA00.getCellData(0,"sEmail1"))>0){
					var resultArr = Ds_LTIEA00.getCellData(0,"sEmail1").split("@");
					edt_sEmail.setValue(resultArr[0]); 										
					edt_sEmail1.setValue(resultArr[1]);				
				}else{
					edt_sEmail.setValue(""); 														
					edt_sEmail1.setValue("");
				}			

				if(gProdYN == "Y") {			
					edt_fax1.setValue("");
					edt_fax2.setValue("");
					edt_fax3.setValue("");									
				}		

				//테스트 번호 . 막기
				if(wconv.length(Ds_LTIEA00.getCellData(0,"sFax1")) > 0){
					if(gProdYN == "Y") {
						edt_fax1.setValue(Ds_LTIEA00.getCellData(0,"sFax1"));	
						edt_fax2.setValue(Ds_LTIEA00.getCellData(0,"sFax2"));
						edt_fax3.setValue(Ds_LTIEA00.getCellData(0,"sFax3"));
					} 
				}

				// 체크초기화
				chk_Post.setValue(0);
				chk_Email.setValue(0);
				chk_Fax.setValue(0);
				//edt_nCrChngNo = "";

				//----------------------------------------------------------------------------------------------
				// 안내양식 정보 가져오기.
				//----------------------------------------------------------------------------------------------
				wconv.clear(DsWs_PRIAA02Warning);
				wconv.clear(DsWs_PRIAA02_List);

				var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR020Task", "selectAll","","");
				MethodObj.setId("selectAll");	
				MethodObj.setUrl("/sales/CBSC103MController.do");
				MethodObj.setXPath("//*/WARNING");
				MethodObj.addXPath("//*/vector/data/PRIAA02"); 				
				MethodObj.sync();
				MethodObj.send();		

				//console.log(JSON.stringify(_tranSync.result));
	    		//util.interfaceKey(_tranSync.result.serviceDs);	
				DsWs_PRIAA02_List.setJSON (_tranSync.getDsJson("PRIAA02")); 
				DsWs_PRIAA02Warning.setJSON (_tranSync.getDsJson("WARNING"));

				if(DsWs_PRIAA02Warning.getRowCount() != 0){
					auth_yn = "N";
					alert(DsWs_PRIAA02Warning.getCellData(0, "msg"));
					return;	
				} else {
					if(DsWs_PRIAA02_List.getRowCount() == 0){
						auth_yn = "N";
						alert("조건에 맞는 자료가 없습니다.");
					}else{
						wconv.copy(Ds_PRIAA02_List, DsWs_PRIAA02_List);
					}
				}
			}
		}
	}else{
		auth_yn = "N";
		console.log("***************************");
		console.log("권한없음:"+auth_yn);
		console.log("***************************");

	}
};

//---------------------------------------------------------------------------
// tobe : scwin.Div2_msk_CstNo_onkeydown
// asis : lfn_msk_CstNo_OnCharChanged
// 내용 : 고객번호 입력 후 엔터시
//---------------------------------------------------------------------------
scwin.Div2_msk_CstNo_onkeydown = function(e) {  
	if(e.keyCode == 13) {
		scwin.btn_Search_onclick();
	}	
};

