		//console.log(JSON.stringify(_tranSync.result));
	    //util.interfaceKey(_tranSync.result.serviceDs);
	    Ds_Loan.setJSON (_tranSync.getDsJson("LTIJA01YN")); // dataset 데이터	

		wconv.copy(DsWs_Loan, Ds_Loan);

		var sLoanYn = DsWs_Loan.getCellData(0, "sLoanYN");				
		if(sLoanYn == "Y"){
			rdo_L232.setDisabled(false);	//대출원리금납입내역서
			rdo_L231.setDisabled(false);	//부채잔액증명의뢰서
		}else{
			rdo_L232.setDisabled(true);		//대출원리금납입내역서
			rdo_L231.setDisabled(true);		//부채잔액증명의뢰서
		}
	}		
};


//---------------------------------------------------------------------------
// tobe : scwin.lfn_initPrintControl
// asis : lfn_initPrintControl
// 내용 : 값 초기화
//---------------------------------------------------------------------------	
scwin.lfn_initPrintControl = function() {		

	//우편발송
	chk_Post.setDisabled(true);
	cmb_DmMethod.setDisabled(true);
	chk_vartAddrs.setDisabled(true);
	edt_HomeDtlAddr.setDisabled(true);	
	chk_Post.setValue(0);	
	cmb_DmMethod.setValue(0);
	chk_vartAddrs.setValue(0);
	edt_HomeDtlAddr.setValue("");

	//팩스
	chk_Fax.setDisabled(true);
	edt_fax1.setDisabled(true);
	edt_fax2.setDisabled(true);
	edt_fax3.setDisabled(true);
	chk_Fax.setValue(0);			
	edt_fax1.setValue("");
	edt_fax2.setValue("");
	edt_fax3.setValue("");		

	//수신인이름
	edt_ContName.setDisabled(true);
	edt_ContName.setValue("");

	//이메일	
	chk_Email.setDisabled(true);
	edt_sEmail.setDisabled(true);
	edt_sEmail1.setDisabled(true);
	cmb_Email.setDisabled(true);		
	chk_Email.setValue(0);
	edt_sEmail.setValue("");
	edt_sEmail1.setValue("");

	//핸드폰(알림톡)
	chk_lms.setValue(0);
	edt_hp1.setValue("");
	edt_hp2.setValue("");
	edt_hp3.setValue("");

};

//---------------------------------------------------------------------------
// tobe : scwin.cmd_SearchStd_onviewchange
// asis : lfn_SearchStd_OnChanged
// 내용 : 조회기준 변경시 div setting
//---------------------------------------------------------------------------	
scwin.cmd_SearchStd_onviewchange = function() {

   	wconv.clear(Ds_PRIAA02_List);


    if (cmd_SearchStd.getValue()  == "1" || cmd_SearchStd.getValue()  == "2") {
		Div1_msk_sCrNo.setStyle("display", "");
		Div1_btn_search_sCr.setStyle("display", "");
		Div1_title1.setStyle("display", "");
		Div1_edt_sGdNm.setStyle("display", "");
		Div1_title2.setStyle("display", "");
		Div1_edt_sCrtorName.setStyle("display", "");
		Div2_msk_CstNo.setStyle("display", "none");	
		Div2_btn_sCstSerch.setStyle("display", "none");
		Div2_title3.setStyle("display", "none");
		Div2_edt_CstName.setStyle("display", "none");	

    } else if (cmd_SearchStd.getValue()  == "3") {
		Div1_msk_sCrNo.setStyle("display", "none");
		Div1_btn_search_sCr.setStyle("display", "none");
		Div1_title1.setStyle("display", "none");
		Div1_edt_sGdNm.setStyle("display", "none");
		Div1_title2.setStyle("display", "none");
		Div1_edt_sCrtorName.setStyle("display", "none");
		Div2_msk_CstNo.setStyle("display", "");	
		Div2_btn_sCstSerch.setStyle("display", "");
		Div2_title3.setStyle("display", "");
		Div2_edt_CstName.setStyle("display", "");
    }

    //증명서 활성화/비활성화 처리
    //scwin.lfn_gFaceSalesYN();
};



//---------------------------------------------------------------------------
// tobe : scwin.lfn_getCrInfo()
// asis : lfn_getCrInfo()
// 내용 :  설계/계약정보조회 (설계/계약번호로 설계/계약테이블 정보 조회)
//---------------------------------------------------------------------------
scwin.lfn_getCrInfo = function(sCrNo,sFlg) {

	wconv.clear(DsWs_getCrInfo);	

	var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR041Task", "getCrInfo","","");
	MethodObj.setId("getCrInfo");	
	MethodObj.setUrl("/sales/CBSC103MController.do");	
	if(sFlg == "1"){
		MethodObj.setXPath("//*/LTIEA00");
		MethodObj.setParam("sCrNo", sCrNo);//조회기준
		MethodObj.setParam("sFlg", "2"); //조회기준		
	}else if(sFlg == "2"){
		MethodObj.setXPath("//*/LTIDA00");
		MethodObj.setParam("sPlanNo", sCrNo); //조회기준
		MethodObj.setParam("sFlg", "1"); //조회기준		
	}			 	
	MethodObj.sync();
	MethodObj.send();

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);

	if(sFlg == "1"){
		DsWs_getCrInfo.setJSON (_tranSync.getDsJson("LTIEA00")); // dataset 데이터		
	}else if(sFlg == "2"){
		DsWs_getCrInfo.setJSON (_tranSync.getDsJson("LTIDA00")); // dataset 데이터		
	}	

};



//---------------------------------------------------------------------------
// function 명 : lfn_LapsCnclCrSave
// 내용        : 승환계약 비교안내 대상 저장
// sInrpsCd(피보험자 주민번호)
//---------------------------------------------------------------------------
scwin.lfn_LapsCnclCrSaveN = function(){	

	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD071Task", "updateSearchYnLong","","");
	MethodObj.setId("updateSearchYnLong");		
	MethodObj.setUrl("/sales/CBSC103MController.do");

	MethodObj.setXPath("//*/RESULT");
	MethodObj.addXPath("//*/WARNING");	

	MethodObj.setParam("sInrpsCd", parent_Ds_Pibo.getCellData(0, "sInrpsCd"));//조회기준
	MethodObj.setParam("sStandDt", wconv.substr(gDs_ServerTime.getCellData(0, "sToday"),0,8));//조회기준
	MethodObj.setParam("sSsrtScrnYn", "Y");//조회기준
	MethodObj.setParam("sPlanNo", parent_Ds_Pibo.getCellData(0, "sPlanNo"));//조회기준
	MethodObj.setParam("sCmprCrNotcCrNo", DsWs_LapsCnclCrList.getCellData(0, "sCrNoPast"));//조회기준	
	MethodObj.sync();
	MethodObj.send();

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);
	ds_Save_chk.setJSON (_tranSync.getDsJson("RESULT")); // dataset 데이터
	ds_Wanning.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터

	if(ds_Wanning.getRowCount() != 0){
		alert(ds_Wanning.getCellData(0, "msg"));
		return;
	}

};

