//---------------------------------------------------------------------------
// tobe : scwin.lfn_LapsCnclCrSearch
// asis : lfn_LapsCnclCrSearch
// 내용 : 승환계약 비교안내 대상 체크 
// sInrpsCd(피보험자 주민번호)
//---------------------------------------------------------------------------
scwin.lfn_LapsCnclCrSearch = function(sInrpsCd){

//	wconv.clear(DsWs_LapsCnclCrCnt);
//	wconv.clear(DsWs_CmprCrNotc);
	wconv.clear(DsWs_LapsCnclCrList);
	wconv.clear(warning_LapsCnclCrCnt);


	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD071Task", "selLapsCnclCrList","","");
	MethodObj.setId("selLapsCnclCrList");		
	MethodObj.setUrl("/sales/CBSC103MController.do");	
	//MethodObj.setXPath("//hashtable/data[@hashkey=&apos;LapsCnclCrCnt&apos;]/LTIEA00Cnt");
	MethodObj.addXPath("//*/WARNING");
	//MethodObj.addXPath("//hashtable/data[@hashkey=&apos;CmprCrNotc&apos;]/CmprCrNotc");
	MethodObj.addXPath("//*/LTIEA00");
	//MethodObj.addXPath("//*/LTIDA00");

	MethodObj.setParam("sInrpsCd", sInrpsCd);//조회기준
	MethodObj.setParam("sSsrtScrnYn", "Y");//조회기준
	MethodObj.setParam("sPlanNo", parent_Ds_Pibo.getCellData(0, "sPlanNo"));//조회기준
	MethodObj.setParam("sGdTypeFlagCd", parent_DsWs_PrdAttr.getCellData(0, "GdTypeFlagCd"));//조회기준	
	MethodObj.sync();
	MethodObj.send();

	console.log("승환계약 비교안내 대상 체크=========>\n"+
		 "sInrpsCd:"+sInrpsCd+"\n"
		+"sPlanNo:"+parent_Ds_Pibo.getCellData(0, "sPlanNo")+"\n"
		+"sGdTypeFlagCd:"+parent_DsWs_PrdAttr.getCellData(0, "GdTypeFlagCd")+"\n"
		+"====================================\n"		
	);

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);

	warning_LapsCnclCrCnt.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터	
	DsWs_LapsCnclCrList.setJSON (_tranSync.getDsJson("LTIEA00")); // dataset 데이터	  


	if(warning_LapsCnclCrCnt.getRowCount() > 0){
		alert(warning_LapsCnclCrCnt.getCellData(0, "msg"));
		return 0;
	}

	return DsWs_LapsCnclCrList.getRowCount();
};

//---------------------------------------------------------------------------
// tobe : scwin.lfn_Print
// asis : lfn_Print
// 내용 : 
//---------------------------------------------------------------------------
scwin.lfn_Print = function(){			
	//alert("프린트출력==============>");

	console.log("**********lfn_Print - sPlanNo:"+sPlanNo);

	wconv.clear(DS_SAVESENDPRINT);

	if (wconv.length(rdo_sDmcode)==0){
        alert("출력할 증명서를 선택해주세요.");
        return;               
	}

	if (chk_Print.getValue() != "1"){
        alert("프린트 출력을 선택하십시오.");
        return;               
	}

//	if(rdo_sDmcode == "L224"){
//		scwin.lfn_RenwlPrem(); //2016.06.30 발송전 갱신보험료 입력
//	}
	if(rdo_sDmcode == "L120" || rdo_sDmcode == "L224" 
		|| rdo_sDmcode == "L010" || rdo_sDmcode == "L219"
		|| rdo_sDmcode == "L173" ){
		scwin.lfn_RenwlPrem();
		scwin.lfn_RtnAmt(); //2016.06.30 발송전 갱신보험료 입력
	}

	if(rdo_sDmcode == "LL10" 
		|| rdo_sDmcode == "LL11" 
		|| rdo_sDmcode == "L017" 
		|| rdo_sDmcode == "L044"){
		alert("프린트 출력이 불가능한 증명서입니다.");
        return false;	de

	}else if(rdo_sDmcode == "L144"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			alert("재물보험증권은 프린트 출력이 불가능한 증명서입니다.");
			return false;
	    }
	}

	// 계약변경승인서
	if(rdo_sDmcode == "L018"){		
		if( wconv.length(wconv.trim(edt_nCrChngNo.getValue())) == 0){			
			wprom.alertF("계약변경번호를 입력하십시오",{},edt_nCrChngNo);			
			return false;
		}
	}	

	// 대출원리금 납입내역서 ted
	if(rdo_sDmcode == "L231"){	

		if( wconv.length(wconv.trim(edt_sSrtDate.getValue())) == 0 || wconv.length(wconv.trim(edt_sEndDate.getValue())) == 0){
			wprom.alertF("조회일자를 입력하십시오",{},edt_sSrtDate);			
			return false;
		}
	}

	// 부채잔액증명의뢰서 ted
	if(rdo_sDmcode == "L232"){	

		if( wconv.length(wconv.trim(edt_sCallDate.getValue())) == 0){			
			wprom.alertF("기준일자를 입력하십시오",{},edt_sCallDate);						
			return false;
		}
	}	

	//공직자 재산등록용 잔액증명서
    if(rdo_sDmcode == "L221"){	
		if( wconv.length(wconv.trim(edt_sCallDate.getValue())) == 0){
			wprom.alertF("기준일자를 입력하십시오",{},edt_sCallDate);			
			return false;
		}else{				
		      if( wconv.trim(edt_sCallDate.getValue()) < eval(Ds_LTIEA00.getCellData(0, "sInsurStrtdate")) ||		          
		          wconv.trim(edt_sCallDate.getValue()) > eval(Ds_LTIEA00.getCellData(0, "sInsurEndDate"))
		         ){		          
				wprom.alertF("기준일자를 보험기간 이내로 입력하십시오",{},edt_sCallDate);						
				return false;		      	
		      }else if(Ds_LTIEA00.getCellData(0, "sCrStatCd") == "60"){//철회건		      
				wprom.alertF("철회건은 발행할수 없습니다",{},edt_sCallDate);					
				return false;		      
		      }else{
		      }		      
		}		
	}

	//수납확인서
    if(rdo_sDmcode == "L028"){	

		if( wconv.length(wconv.trim(edt_sSrtDate.getValue())) == 0 
			|| wconv.length(wconv.trim(edt_sEndDate.getValue())) == 0){
			wprom.alertF("조회일자를 입력하십시오",{},edt_sSrtDate);				
			return false;
		}else{
			//2016.04.08 수납사항 건수 체크 
			wconv.clear(DsWs_TotPayCount);
			util.appendRow(DsWs_TotPayCount);
			var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR041Task", "getTotPayCount","","");
			MethodObj.setId("getTotPayCount");		
			MethodObj.setUrl("/sales/CBSC103MController.do");			
			MethodObj.setXPath("//*/LTIFA01PayCount");					
			MethodObj.setParam("sCrNo", Div1_msk_sCrNo.getValue());//조회기준
			MethodObj.setParam("srtDate", edt_sSrtDate.getValue());//조회기준
			MethodObj.setParam("sEndDate", edt_sEndDate.getValue());//조회기준					
			MethodObj.sync();
			MethodObj.send();

			//console.log(JSON.stringify(_tranSync.result));
		    //util.interfaceKey(_tranSync.result.serviceDs);
			DsWs_TotPayCount.setJSON (_tranSync.getDsJson("LTIFA01PayCount"));			
			//console.log("nTotPayCount:"+DsWs_TotPayCount.getCellData(0, "nTotPayCount"));
			if(wconv.toNumber(DsWs_TotPayCount.getCellData(0, "nTotPayCount")) >= 22){
				alert("수납확인서는 22회 수납내역만 출력가능합니다. 22회 이상의 수납내역 발송시 22회 이하로 나누어서 발송 부탁드립니다.");
				return false;		
			}						
		}									
	}

	//년도 체크
	//console.log("cmb_Year.getValue():"+cmb_Year.getValue());	
	if ( (rdo_sDmcode == "L147" || rdo_sDmcode == "L228" )  && cmb_Year.getValue() == 0 ){	
        wprom.alertF("년도를 선택하세요.",{},cmb_Year);       
        return false;	
	}

	//예상해지환급금
	if(rdo_sDmcode == "L229"){ 
		scwin.SendExptGrntPrem();
		if(sExptGrntPrem == "N"){
			return false;
		}
	}

	//console.log("****************scwin.lfn_Print***********\nsPlanNo:"+sPlanNo);
	if ((rdo_sDmcode == "L045" || rdo_sDmcode == "L221" 
			|| rdo_sDmcode == "L028" || rdo_sDmcode == "L022"  
			|| rdo_sDmcode == "L222" ) && util.isNull(sPlanNo)
		){			
		wprom.alert("프린트 출력이 불가능한 증명서입니다.");
        return false;	
	}


	if(rdo_sDmcode == "L019" || rdo_sDmcode == "L031"
		|| rdo_sDmcode == "L032" || rdo_sDmcode == "L054"
		|| rdo_sDmcode == "L057" || rdo_sDmcode == "LL04"
		){
		wprom.alert("프린트 출력이 불가능한 증명서입니다.");
        return false;	
	}

	scwin.chk_Print();				

	scwin.fn_chkPrintView();

};


