	MethodObj.setParam("sPlanNo", Ds_LTIEA00.getCellData(0, "sPlanNo"));
	MethodObj.setParam("sPsnGroupFlgcd", Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd"));						
	MethodObj.setUrl("/sales/CBSC103MController.do");
	//MethodObj.setXPath("//vector/data/LTIGB10");
	MethodObj.setXPath("//*/WARNING");
	MethodObj.sync();
	MethodObj.send();   

	console.log(JSON.stringify(_tranSync.result));
    util.interfaceKey(_tranSync.result.serviceDs);	
	DsWs_Warning_RtnAmt.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터					

	if(DsWs_Warning_RtnAmt != null && wconv.length(DsWs_Warning_RtnAmt.getCellData(0,"msg")) > 0) {
		alert(DsWs_Warning_RtnAmt.getCellData(0,"msg"));
		return;
	}

};

//---------------------------------------------------------------------------
// tobe : scwin.SendExptGrntPrem
// asis : SendExptGrntPrem
// 내용 : 예상 환급금 안내표 호출
//---------------------------------------------------------------------------
scwin.SendExptGrntPrem = function(){ 	

	sExptGrntPrem = "N";	
	crntYMD = sToday;

	var sType = "";

	if(cal_sExptRtnDate.getValue() == "") {
		wprom.alertF("예상환급일을 입력하십시오.",{},cal_sExptRtnDate);		
		return false;
	}

	if(cmb_sInqrUnit.getValue() == "00") {
		wprom.alertF("조회단위를 입력하십시오.",{},cmb_sInqrUnit);		
		return false;
	}

	if ( chk_Post.getValue() == "1"){ //우편
		nInqrSeq = 0;
		sType = "POST";
	}	
	if ( chk_Email.getValue() == "1" ){ //이메일
		nInqrSeq = 0;
		sType = "EMAIL";
	}	
	if ( chk_Print.getValue() == "1"){ //출력
		sType = "PRINT";
	}
	if ( chk_Fax.getValue() == "1") { //팩스
		sType = "FAX";
	}
	if ( sType == "PRINT" ) { //팩스 & 출력
		if(nInqrSeq != 0){

			wconv.clear(DsWs_ExptRtnAmtForBatch);	
			util.appendRow(DsWs_ExptRtnAmtForBatch);
			wconv.clear(DsWs_WarningForBatch);	
			util.appendRow(DsWs_WarningForBatch);

			var MethodObj = new callService("com.inswave.longterm.pay.pin.task.GBG160Task", "selectExptRtnAmtForBatchEnd", "", "");
			MethodObj.setId("selectExptRtnAmtForBatchEnd");	
			MethodObj.setParam("sCrNo", sCrNo_print);
			MethodObj.setParam("nInqrSeq", nInqrSeq);						
			MethodObj.setUrl("/sales/CBSC103MController.do");
			MethodObj.setXPath("//vector/data/LTIGB10");
			MethodObj.addXPath("//*/WARNING");
			MethodObj.sync();
			MethodObj.send();    

			console.log(JSON.stringify(_tranSync.result));
		    util.interfaceKey(_tranSync.result.serviceDs);	
			DsWs_ExptRtnAmtForBatch.setJSON (_tranSync.getDsJson("LTIGB10")); // dataset 데이터			
			DsWs_WarningForBatch.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터

			if(DsWs_WarningForBatch != null && wconv.length(DsWs_WarningForBatch.getCellData(0,"msg")) > 0) {
				alert(DsWs_WarningForBatch.getCellData(0,"msg"));
				return;
			}else{
				sExptGrntPrem = "Y"; //팩스 호출
				return;
			}
		}
	}

	if(sType == "EMAIL" 
		|| sType == "POST" 
		|| (sType == "FAX")  
		|| (sType == "PRINT" && nInqrSeq == 0)){

		wconv.clear(DsWs_batchResult);	
		util.appendRow(DsWs_batchResult);
		wconv.clear(DsWs_batchResultWarning);	
		util.appendRow(DsWs_batchResultWarning);

		var MethodObj = new callService("com.inswave.longterm.pay.pin.task.GBG160Task", "selectExptRtnAmtBatch", "", "");
		MethodObj.setId("selectExptRtnAmtBatch");	
		MethodObj.setParam("oJob", "GBG160BATCH_COM_ONL" );
		MethodObj.setParam("oDate", crntYMD );
		MethodObj.setParam("sCrNo", sCrNo_print);			// 계약번호
		MethodObj.setParam("sStndDate", crntYMD);			// 기준일
		MethodObj.setParam("sExptRtnDate", cal_sExptRtnDate.getValue());	// 예상환급일
		MethodObj.setParam("sInqrUnit", cmb_sInqrUnit.getValue());			// 조회단위		
		MethodObj.setParam("sNwcrRateApplFlag", "02");	
		MethodObj.setParam("nAddPaymPrem", Ds_LTIEA00.getCellData(0, "nAddPaymPrem")); //추가납입
		MethodObj.setParam("sType", sType);  //발송방법
		MethodObj.setParam("sLoginName", gUserEmpName );
		MethodObj.setParam("sToName", Ds_LTIEA00.getCellData(0, "sCrtorName")); 
		if(sType == "EMAIL"){
			MethodObj.setParam("sToEmail", wconv.trim(edt_sEmail.getValue())+"@"+wconv.trim(edt_sEmail1.getValue())); 
		}else if( sType == "FAX" ){
			MethodObj.setParam("sFax1", wconv.trim(edt_fax1.getValue())); 
			MethodObj.setParam("sFax2", wconv.trim(edt_fax2.getValue())); 
			MethodObj.setParam("sFax3", wconv.trim(edt_fax3.getValue())); 
		}
		MethodObj.setParam("sUserID", gUserID); 

		MethodObj.setUrl("/sales/CBSC103MController.do");
		MethodObj.setXPath("//*/EXEC_INFO");
		MethodObj.addXPath("//*/WARNING");
		MethodObj.sync();
		MethodObj.send();  


		console.log(JSON.stringify(_tranSync.result));
    	util.interfaceKey(_tranSync.result.serviceDs);	
		DsWs_batchResult.setJSON (_tranSync.getDsJson("EXEC_INFO")); // dataset 데이터
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_batchResultWarning.getID());
		//DsWs_batchResultWarning.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터


		if(DsWs_batchResultWarning.getRowCount() != 0){
			alert(DsWs_batchResultWarning.getCellData(0, "msg"));
			return;
		}
		if(DsWs_batchResult.getRowCount() != 0){

			nInqrSeq = DsWs_batchResult.getCellData(0, "nInqrSeq"); //이값 저장

			if ( sType == "EMAIL"){
				alert("배치가 정상 실행되었습니다. "+sType+" 발송 완료시완료까지 몇분 될 예정입니다.");
				return;
			}else if (sType == "FAX"){
				alert('배치가 정상 실행되었습니다. '+sType+' 발송 완료시완료까지 몇분 될 예정입니다.');
				return;
			}else if (sType == "PRINT"){
				alert("해지환급금안내표데이터를 생성중입니다. 몇분후출력버튼을 눌러주세요.");
				return;
			}else{
				alert("배치가 실행되었습니다. 완료시까지 5분 가량 소요됩니다.");
				sExptGrntPrem = "Y";
				return;
			}
		}else{
			alert("배치가 정상 실행되지 않았습니다.(nInqrSeq)");
			return;
		}

	}
};

