//---------------------------------------------------------------------------
// function 명 : send_start
// 내용        : 발송 
//---------------------------------------------------------------------------
scwin.send_start = function(obj){	

	wconv.clear(DS_SAVESENDINFO); //발송물정보 	
	util.appendRow(DS_SAVESENDINFO);

	if ( chk_Post.getValue() == "1" ){
		var result = scwin.setPrintInfo();
		if ( !result ){
			return;
		}
	}

	wconv.clear(DS_SAVEEMAILINFO);
	util.appendRow(DS_SAVEEMAILINFO);

	if ( chk_Email.getValue() == "1" ){
	    var result = scwin.setEmailInfo();
		if ( !result ){
			return;
		}			
		if(rdo_sDmcode == "L173" || rdo_sDmcode == "L219") {
			var result = scwin.setEmailGdCdInfo();
			if ( !result ){
				return;
			}
		}
	}

	wconv.clear(DS_SAVEFAXINFO);
	util.appendRow(DS_SAVEFAXINFO);

	if ( chk_Fax.getValue() == "1" ){
	    var result = scwin.setFaxInfo();
		if ( !result ){
