            message += "메일발송중 오류가 발생 했습니다.";
        }		
	}		
	if ( chk_lms.getValue() == "1" ){
		if ( ds_Save_chk.getCellData( 0 , "mailFlag" ) == "1" ){		
			message += "알림톡발송이 요청되었습니다.";
        }else {
            message += "알림톡발송중 오류가 발생 했습니다.";
        }		
	}
	alert(message);	
};

//---------------------------------------------------------------------------
// tobe : scwin.lfn_SearchCstEmail
// asis : lfn_SearchCstEmail
// 내용 : 고객조회
//---------------------------------------------------------------------------
scwin.lfn_SearchCstEmail = function(){

	wconv.clear(DsWs_Cst);
	scwin.dataInit();

	var MethodObj = new callService("com.inswave.longterm.cst.cst.task.CAA070Task", "getCustomerInfo","","");
	MethodObj.setId("getCustomerInfo");	
	MethodObj.setUrl("/sales/CBSC103MController.do");	
	MethodObj.setXPath("//*/hashtable/data/CUSAA03");
	MethodObj.addXPath("//*/hashtable/data/CUSAA01");
	MethodObj.setParam("sCustNo", Div2_msk_CstNo.getvalue());//조회기준	
	MethodObj.sync();
	MethodObj.send();

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_Cst.setJSON (_tranSync.getDsJson("CUSAA03")); // dataset 데이터
	DsWs_CstInfo.setJSON (_tranSync.getDsJson("CUSAA01")); // dataset 데이터	


	//param setting	
	msk_HomeZipCode1.setValue(DsWs_Cst.getCellData(0, "sZip1"));
	msk_HomeZipCode2.setValue(DsWs_Cst.getCellData(0, "sZip2"));
	edt_HomeVilAddr.setValue(DsWs_Cst.getCellData(0, "addrs"));
	edt_HomeDtlAddr.setValue(DsWs_Cst.getCellData(0, "sAdrsAdd"));		
	Div2_edt_CstName.setValue(DsWs_CstInfo.getCellData(0, "sName"));	
	edt_hp1.setValue(Ds_LTIEA00.getCellData(0, "sCellPhone1"));//핸드폰번호
	edt_hp2.setValue(Ds_LTIEA00.getCellData(0, "sCellPhone2"));//핸드폰번호
	edt_hp3.setValue(Ds_LTIEA00.getCellData(0, "sCellPhone3"));//핸드폰번호		

	//★TODO-확인필요
//	if(Div2_edt_CstName.Text == ""){
//	Div2_edt_CstName.setValue(DsWs_CstInfo.getCellData(0, "sName"));
//	}else{
//		Div2_edt_CstName.setValue(DsWs_CstInfo.getCellData(0, ""));
//	}

	if(wconv.length(DsWs_Cst.getCellData(0,"sEmail1"))>0){
		var resultArr = DsWs_Cst.getCellData(0,"sEmail1").split("@");
		edt_sEmail.setValue(resultArr[0]); 										
		edt_sEmail1.setValue(resultArr[1]);				
	}else{
		edt_sEmail.setValue(""); 														
		edt_sEmail1.setValue("");
	}
};

