		edt_sEndDate.setStyle("display" ,"");
		cmb_sPrintReason.setStyle("display", "none");
		cmb_sPrintReason.setDisabled(true);		
	}

	if(rdo_sDmcode == "L232"
		|| rdo_sDmcode == "L221"
	){	
		gr_Loan.setStyle("display" ,"");
		edt_DateNm.setStyle("display" ,"");
		edt_DateNm.setStyle("background-color", "#f2f2f2");
		edt_DateNm.setStyle("border-color", "#f2f2f2");
