//---------------------------------------------------------------------------
// tobe : scwin.fn_YearCheck
// asis : fn_YearCheck
// 내용 : 연도 체크 ( 해당 계약의 보험시작일자 기준 년)
//---------------------------------------------------------------------------
scwin.fn_YearCheck = function(){

	crntYMD = sToday;
	crntTime = sTime;

	var crntYYYY = wconv.parseInt(wconv.substr(crntYMD, 0, 4));	
	var sInsurStrtdate = "";

	if(rdo_sDmcode == "L147" || rdo_sDmcode == "L228" ){ //2016.10.20 추가
		sInsurStrtdate = crntYMD;		
	}else{
		sInsurStrtdate = Ds_LTIEA00.getCellData(0, "sInsurStrtdate");
	}

	var nYear = wconv.parseInt(wconv.substr(sInsurStrtdate, 0, 4));

	if(rdo_sDmcode == "L147" || rdo_sDmcode == "L228" ){ 
		//2017.01.02 테스트용
		nYear = 2014;
	}

	console.log("**********crntYMD:"+crntYMD+"/crntTime:"+crntTime+"/crntYYYY:"+crntYYYY+"/nYear:"+nYear+"/sInsurStrtdate:"+sInsurStrtdate);

	wconv.clear(Ds_Year); //발송물정보 
	util.appendRow(Ds_Year);	
    Ds_Year.setCellData(0, "TEXT", "선택");
	for(i=1; nYear <= crntYYYY ; nYear++, i++){
		 util.appendRow(Ds_Year);
		 Ds_Year.setCellData(i, "CODE", nYear);
		 Ds_Year.setCellData(i, "TEXT", nYear);
	}
	cmb_Year.Index = 0;
};


//---------------------------------------------------------------------------
// tobe : scwin.lfn_yearCheck
// asis : lfn_yearCheck
// 내용 : 
//---------------------------------------------------------------------------
scwin.lfn_yearCheck = function(){		

	if( rdo_sDmcode == "L147" 
		|| rdo_sDmcode == "L228" ){
		scwin.totalNoticeCheck();
	}
};

//---------------------------------------------------------------------------
// tobe : scwin.totalNoticeCheck
// asis : totalNoticeCheck
// 내용 : 통합안내장 발행대상 체크
//---------------------------------------------------------------------------
scwin.totalNoticeCheck = function(){

	console.log("*********cmb_Year.getValue():"+cmb_Year.getValue());	
	if(cmb_Year.getValue() != 0){

		wconv.clear(Ds_TotalListChk);
		util.appendRow(Ds_TotalListChk);
		wconv.clear(DsWs_TotalListChk);
		util.appendRow(DsWs_TotalListChk);

		var MethodObj = new callService("com.inswave.longterm.psv.bat.EYE451Batch", "insurTotInsNoteCheckOnline", "", "");
		MethodObj.setId("insurTotInsNoteCheckOnline");	
		MethodObj.setParam("sStndDate", wconv.toString(wconv.toNumber(cmb_Year.getValue())-1) + "1231");
		if(cmd_SearchStd.getValue() == "1"){ //계약번호
			MethodObj.setParam("sCrNo", Div1_msk_sCrNo.getValue()); 
		}else if(cmd_SearchStd.getValue() == "3" ){ //고객번호			
			MethodObj.setParam("sCrtorCd", Div2_msk_CstNo.getValue()); 
		}				
		MethodObj.setUrl("/sales/CBSC103MController.do");
		MethodObj.setXPath("//*/vector/data/LTIEZ65Chk");
		MethodObj.sync();
		MethodObj.send();    

		console.log("scwin.totalNoticeCheck***************************\n"+wconv.toString(wconv.toNumber(cmb_Year.getValue())-1) + "1231"+"\n"+Div1_msk_sCrNo.getValue());

		console.log(JSON.stringify(_tranSync.result));
	    util.interfaceKey(_tranSync.result.serviceDs);	
		DsWs_TotalListChk.setJSON (_tranSync.getDsJson("LTIEZ65Chk")); // dataset 데이터			

		if(DsWs_Warning.getRowCount() != 0){
			alert(DsWs_Warning.getCellData(0, "msg"));
			return;
		}else {		
			wconv.copy(Ds_TotalListChk, DsWs_TotalListChk);				
		}			

		sSendFlag = Ds_TotalListChk.getCellData(0, "sSendFlag");
		sAnntSendFlag = Ds_TotalListChk.getCellData(0, "sAnntSendFlag");

		if ( (rdo_sDmcode == "L147" && sSendFlag == "X") || rdo_sDmcode == "L228" && sAnntSendFlag == "X" ) {
			alert("발행 대상이 아닙니다.");
			//cmb_Year.setValue("1");
			return;
		}
	}else{
		sSendFlag = "N";
		sAnntSendFlag = "N";
	}

};

//---------------------------------------------------------------------------
// tobe : scwin.OnPrintControl
// asis : OnPrintControl
// 내용 : 안내문의 종류에 따라 [우편발송 . 이메일 , 팩스]  Componunt Event처리
//---------------------------------------------------------------------------
scwin.OnPrintControl = function(rdo_sDmcode){
	//alert("OnPrintControl===============>");
	crntYMD = sToday;

	chk_Post.setValue(0);
	chk_Email.setValue(0);
	chk_Fax.setValue(0);	

    var sDmTpCd1;
    var sDmTpCd2;
    var sDmTpCd3;

    // 라디오버튼 클릭시 우편, 이메일 ,팩스 콤보박스 제어.          
    wconv.filter(Ds_PRIAA02_List, "sDmCode=='" + rdo_sDmcode + "'", true);

    sDmTpCd1 = Ds_PRIAA02_List.getCellData(0, "sDmTpCd1" );
	sDmTpCd2 = Ds_PRIAA02_List.getCellData(0, "sDmTpCd2" );
	sDmTpCd3 = Ds_PRIAA02_List.getCellData(0, "sDmTpCd3" );          

	console.log("********우편/이메일/팩스 발송여부:"+sDmTpCd1+"-"+sDmTpCd2+"-"+sDmTpCd3+"/");

    if(rdo_sDmcode == "L001" && cmd_SearchStd.getValue() == "3"){

		chk_Email.setValue(0);
		chk_Fax.setValue(0);		
		chk_Post.setDisabled(true);
		cmb_DmMethod.setDisabled(true);
		chk_vartAddrs.setDisabled(true);
		edt_HomeDtlAddr.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(false);		
		edt_fax1.setDisabled(false);
		edt_fax2.setDisabled(false);
		edt_fax3.setDisabled(false);
		edt_ContName.setDisabled(false);

		if(rdo_sDmcode == "L001" && crntYMD <= "20120401"){
			chk_Email.setDisabled(true);
			edt_sEmail.setDisabled(true);
			edt_sEmail1.setDisabled(true);
			cmb_Email.setDisabled(true);
		}else{
			chk_Email.setDisabled(false);
			edt_sEmail.setDisabled(false);
			edt_sEmail1.setDisabled(false);
			cmb_Email.setDisabled(false);
		}

		return;
    }else{
		chk_Email.setValue(0);
		chk_Fax.setValue(0);		
		chk_Post.setDisabled(true);
		cmb_DmMethod.setDisabled(true);
		chk_vartAddrs.setDisabled(true);
		edt_HomeDtlAddr.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(true);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);

		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
    }

    if(sDmTpCd1 == "2" && sDmTpCd2 == "2" && sDmTpCd3 == "2")  //   우편발송 , E-MAIL ,  FAX 
	{
		// 우편발송
		chk_Post.setDisabled(false);
		cmb_DmMethod.setDisabled(false);
		//btn_addrSearch.setDisabled(false);
		chk_vartAddrs.setDisabled(false);
		edt_HomeDtlAddr.setDisabled(false);
		// E-Mail
		chk_Email.setDisabled(false);
		edt_sEmail.setDisabled(false);
		edt_sEmail1.setDisabled(false);
		cmb_Email.setDisabled(false);
		// Fax
		chk_Fax.setDisabled(false);				

		if(gProdYN == "Y") {				
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		} else {
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		}		

		edt_ContName.setDisabled(false);
	} 
	else if(sDmTpCd1 == "2" && sDmTpCd2 == "2")  //   우편발송 , E-MAIL
	{		
		chk_Fax.setValue(0);
		// 우편발송
		chk_Post.setDisabled(false);
		cmb_DmMethod.setDisabled(false);
		//btn_addrSearch.setDisabled(false);
		chk_vartAddrs.setDisabled(false);
		edt_HomeDtlAddr.setDisabled(false);
		// E-Mail
		chk_Email.setDisabled(false);
		edt_sEmail.setDisabled(false);
		edt_sEmail1.setDisabled(false);
		cmb_Email.setDisabled(false);
		// Fax
		chk_Fax.setDisabled(true);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);
	}
	else if(sDmTpCd2 == "2" && sDmTpCd3 == "2")  //   E-MAIL ,  FAX 
	{

		chk_Post.setValue(0);
		// 우편발송
		chk_Post.setDisabled(true);
		cmb_DmMethod.setDisabled(true);
		//btn_addrSearch.setDisabled(true);
		chk_vartAddrs.setDisabled(true);
		edt_HomeDtlAddr.setDisabled(true);
		// E-Mail
		chk_Email.setDisabled(false);
		edt_sEmail.setDisabled(false);
		edt_sEmail1.setDisabled(false);
		cmb_Email.setDisabled(false);
		// Fax
		chk_Fax.setDisabled(false);		

		if(gProdYN == "Y") {				
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		} else {
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		}

		edt_ContName.setDisabled(false);

	} 
	else if(sDmTpCd1 == "2") //   우편발송 
	{
		chk_Email.setValue(0);
		chk_Fax.setValue(0);
		// 우편발송
		chk_Post.setDisabled(false);
		cmb_DmMethod.setDisabled(false);
		//btn_addrSearch.setDisabled(false);
		chk_vartAddrs.setDisabled(false);
		edt_HomeDtlAddr.setDisabled(false);
		// E-Mail
		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(true);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);

	}
	else if(sDmTpCd3 == "2") // fax
	{
		chk_Post.setValue(0);				
		chk_Email.setValue(0);
		// 우편발송
		chk_Post.setDisabled(true);
		cmb_DmMethod.setDisabled(true);		
		chk_vartAddrs.setDisabled(true);
		edt_HomeDtlAddr.setDisabled(true);
		// E-Mail
		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(false);		

		if(gProdYN == "Y") {				
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		} else {
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		}

		edt_ContName.setDisabled(false);
	}
	else if(sDmTpCd2 == "2")  //   E-MAIL
	{			
		// 우편발송
		chk_Post.setValue(0);
		chk_Post.setValue(0);
		cmb_DmMethod.setDisabled(true);
		chk_vartAddrs.setDisabled(true);
		edt_HomeDtlAddr.setDisabled(true);

		// E-Mail
		chk_Email.setDisabled(false);
		edt_sEmail.setDisabled(false);
		edt_sEmail1.setDisabled(false);
		cmb_Email.setDisabled(false);

		chk_Fax.setDisabled(false);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);

	}
	else if (rdo_sDmcode == "LL10" || rdo_sDmcode == "LL11")  // 증권팩
	{
		chk_Email.setValue(0);
		chk_Fax.setValue(0);		
		// 우편발송
		chk_Post.setDisabled(false);
		cmb_DmMethod.setDisabled(false);
		//btn_addrSearch.setDisabled(false);
		chk_vartAddrs.setDisabled(false);
		edt_HomeDtlAddr.setDisabled(false);
		// E-Mail
		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(true);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);

		if(rdo_sDmcode == "LL02"){
			// E-Mail
			chk_Email.setDisabled(false);
			edt_sEmail.setDisabled(false);
			edt_sEmail1.setDisabled(false);
			cmb_Email.setDisabled(false);
		}
	}
	else if (rdo_sDmcode == "LL04")  // 변경팩
	{
		chk_Email.setValue(0);
		chk_Fax.setValue(0);		
		// 우편발송
		chk_Post.setDisabled(false);
		cmb_DmMethod.setDisabled(false);
		//btn_addrSearch.setDisabled(false);
		chk_vartAddrs.setDisabled(false);
		edt_HomeDtlAddr.setDisabled(false);
		// E-Mail
		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(true);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);

	}

	  //   우편발송 , FAX 			
	if(sDmTpCd1 == "2" && sDmTpCd2 == "1" && sDmTpCd3 == "2")
	{		
		chk_Email.setValue(0);
		// 우편발송
		chk_Post.setDisabled(false);
		cmb_DmMethod.setDisabled(false);
		//btn_addrSearch.setDisabled(false);
		chk_vartAddrs.setDisabled(false);
		edt_HomeDtlAddr.setDisabled(false);
		// E-Mail
		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
		// Fax
		chk_Fax.setDisabled(false);		

		if(gProdYN == "Y") {				
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		} else {
			edt_fax1.setDisabled(false);
			edt_fax2.setDisabled(false);
			edt_fax3.setDisabled(false);
		}

		edt_ContName.setDisabled(false);
	}	

	  //   E-Mail
	if(sDmTpCd1 == "1" && sDmTpCd2 == "2" && sDmTpCd3 == "1")
	{		
		chk_Email.setValue(0);
		// 우편발송
		chk_Post.setDisabled(true);
		cmb_DmMethod.setDisabled(true);		
		chk_vartAddrs.setDisabled(true);
		edt_HomeDtlAddr.setDisabled(true);
		// E-Mail
		chk_Email.setDisabled(false);
		edt_sEmail.setDisabled(false);
		edt_sEmail1.setDisabled(false);
		cmb_Email.setDisabled(false);
		// Fax
		chk_Fax.setDisabled(true);		
		edt_fax1.setDisabled(true);
		edt_fax2.setDisabled(true);
		edt_fax3.setDisabled(true);
		edt_ContName.setDisabled(true);
	}


	if(rdo_sDmcode == "L001" && crntYMD <= "20120401"){
		chk_Email.setDisabled(true);
		edt_sEmail.setDisabled(true);
		edt_sEmail1.setDisabled(true);
		cmb_Email.setDisabled(true);
	}

	if("21" == Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd")){ //2016.08.22 단체보험
		chk_Post.setDisabled(true);
		chk_Email.setDisabled(true);
		chk_Fax.setDisabled(true);	
	}

	sDmTpCd1 = "";
    sDmTpCd2 = "";
    sDmTpCd3 = "";
};



//---------------------------------------------------------------------------
// tobe : scwin.btn_send_onclick
// asis : btn_send_OnClick
// 내용 : 발송 이벤트
//---------------------------------------------------------------------------
scwin.btn_send_onclick = function(){

	//상품설명서
	if(rdo_sDmcode == "L224"){
		scwin.lfn_RenwlPrem(); 
	} 


//	if(Ds_LTIEA00.getCellData(0, "sInsurStrtdate")<"20160101"){
//		scwin.lfn_initPrintControl();
//		alert("해당 계약/설계건은 본 화면에서 증명서 발급이 불가 합니다.\n"
//				+"장기 업무팀에 문의 주세요.\n"
//				+"개시일 : "+Ds_LTIEA00.getCellData(0, "sInsurStrtdate")
//				);
//		return;
//	
//	}else 

	if(	  rdo_sDmcode == "L236" || rdo_sDmcode == "L237" || rdo_sDmcode == "L238" 
			   || rdo_sDmcode == "L239" || rdo_sDmcode == "L240" || rdo_sDmcode == "L241"
			   || rdo_sDmcode == "L242" || rdo_sDmcode == "L243"
				){		

		alert("해당 증명서는 인쇄만 가능합니다.");
		return;		

	}else{
		//데이터 유효성 확인
		if(scwin.checkValid() == false){
			return;
		}else{
			wconv.clear(ds_Save_chk);
			ds_Save_chk.insertRow();									

			//공직자 재산등록용 잔액증명서
		    if(rdo_sDmcode == "L221"){	

				if( wconv.length(wconv.trim(edt_sCallDate.getValue())) == 0){
					wprom.alertF("기준일자를 입력하십시오.",{},edt_sCallDate);
					return ;
				}else{
				      if( wconv.trim(edt_sCallDate.getValue()) < eval(Ds_LTIEA00.getCellData(0, "sInsurStrtdate")) ||		          
				          wconv.trim(edt_sCallDate.getValue()) > eval(Ds_LTIEA00.getCellData(0, "sInsurEndDate"))
				         ){					          
						wprom.alertF("기준일자를 보험기간 이내로 입력하십시오",{},edt_sCallDate);
						return ;		      	
				      }else if(Ds_LTIEA00.getCellData(0, "sCrStatCd") == "60"){//철회건				      
						wprom.alertF("철회건은 발행할수 없습니다",{},edt_sCallDate);						
						return ;		      
				      }else{      	
				      }			
				}									
			} 

		   //수납확인서	
		   if(rdo_sDmcode == "L028"){	

				if( wconv.length(wconv.trim(edt_sSrtDate.getValue())) == 0 
					|| wconv.length(wconv.trim(edt_sEndDate.getValue())) == 0){
					wprom.alertF("조회일자를 입력하십시오",{},edt_sSrtDate);					
					return ;
				}else{
					wconv.clear(DsWs_TotPayCount);
					util.appendRow(DsWs_TotPayCount);
					var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR041Task", "getTotPayCount","","");
					MethodObj.setId("getTotPayCount");		
					MethodObj.setUrl("/sales/CBSC103MController.do");			
					MethodObj.setXPath("//*/LTIFA01PayCount");					
					MethodObj.setParam("sCrNo", Div1_msk_sCrNo.getValue());//조회기준
					MethodObj.setParam("srtDate", edt_sSrtDate.getValue());//조회기준
					MethodObj.setParam("sEndDate", edt_sEndDate.getValue());//조회기준					
					MethodObj.sync();
					MethodObj.send();

					//console.log(JSON.stringify(_tranSync.result));
				    //util.interfaceKey(_tranSync.result.serviceDs);
					DsWs_TotPayCount.setJSON (_tranSync.getDsJson("LTIFA01PayCount"));			
					//console.log("nTotPayCount:"+DsWs_TotPayCount.getCellData(0, "nTotPayCount"));
					if(wconv.toNumber(DsWs_TotPayCount.getCellData(0, "nTotPayCount")) >= 22){
						alert("수납확인서는 22회 수납내역만 출력가능합니다. 22회 이상의 수납내역 발송시 22회 이하로 나누어서 발송 부탁드립니다.");
						return ;		
					}	
				}					 				
			}

			//예상해지환급금
			if(rdo_sDmcode == "L229"){ //2017.01.13 
				scwin.SendExptGrntPrem();
				if(sExptGrntPrem == "N"){
					return ;
				}
			}

			if(rdo_sDmcode == "L032"   	|| rdo_sDmcode == "L019"
			  || rdo_sDmcode == "L018" 	|| rdo_sDmcode == "LL04"  
			  || rdo_sDmcode == "L054" 	|| rdo_sDmcode == "L028"  
			  || rdo_sDmcode == "L031") {
				if(scwin.fn_send_check2()==false){
					return;
				}

			}

			if(rdo_sDmcode == "L022"){
				if(scwin.fn_send_check3()==false){
					return;
				}
			}

			//발송
			//scwin.send_start();


			//  개별납입영수증 , 신용카드 매출전표  발행
			if((rdo_sDmcode == "L019" && chk_Fax.getValue() == "1")  ||
			   (rdo_sDmcode == "L019" && chk_Email.getValue() == "1")||
			   (rdo_sDmcode == "L019" && chk_Post.getValue() == "1") || 
			   (rdo_sDmcode == "L031" && chk_Fax.getValue() == "1")){


			    var promise_Con = new Promise(function(resolve){						
					var result = scwin.chk_Recp_OnClick();
					resolve(result);
				});
				promise_Con = promise_Con.then(function(result){ 
					if(result){
						util.eventAsyncCall(this, scwin.send_start, arguments);
					}					
				});
				return promise_Con;
			}else{
				util.eventAsyncCall(this, scwin.send_start, arguments);
			}

			/*  한번 발행하면 보험료 재산출 금지 발행물
			가입설계서 : L143
			해지환급금 : L050
			가입설계서일체 : L120
			상품설명서 : L010
			청약서 : L004
			알릴의무 : L006

			보험증권 : L144
			주소 : L044
			증권팩(증권+상품설명서) : LL01
			증권팩(메일약관) : LL02
			 청약서 일체  : L173
		     청약서일체(회사보관용) : L219
			*/

			if(rdo_sDmcode == "L143"  || rdo_sDmcode == "L050" || rdo_sDmcode == "L120" || rdo_sDmcode == "L010" || rdo_sDmcode == "L004" ||
			    rdo_sDmcode == "L006" || rdo_sDmcode == "L144"|| rdo_sDmcode == "L044" || rdo_sDmcode == "LL10" || rdo_sDmcode == "LL11" ||
			    rdo_sDmcode == "L173" || rdo_sDmcode == "L219") {
				scwin.lfn_save();
				//util.eventAsyncCall(this, scwin.lfn_save, arguments);
			}
		}	
	}
};


//---------------------------------------------------------------------------
// tobe : scwin.chk_Recp_OnClick
// asis : chk_Recp_OnClick
// 내용 : 영수증 발행
//---------------------------------------------------------------------------
scwin.chk_Recp_OnClick = function(){

//	var sArg  = "";
//	sArg = sArg + "sCrNo=" + wconv.quote(Div1_msk_sCrNo.getValue());	
//	sArg = sArg + " chkPost=" + wconv.quote(chk_Post.getValue());
//	sArg = sArg + " chkEmail=" + wconv.quote(chk_Email.getValue());
//	sArg = sArg + " chkFax=" + wconv.quote(chk_Fax.getValue());
//	sArg = sArg + " sFax1=" + wconv.quote(edt_fax1.getValue());
//	sArg = sArg + " sFax2=" + wconv.quote(edt_fax2.getValue());
//	sArg = sArg + " sFax3=" + wconv.quote(edt_fax3.getValue());	
//	sArg = sArg + " sContName=" + wconv.quote(edt_ContName.getValue());

	var issuObjFlg = "";
	if(rdo_sDmcode == "L019") {   // 개별납입영수증
		//sArg = sArg + " issuObjFlg=" + wconv.quote("01");
		issuObjFlg = "01";
	}else if(rdo_sDmcode == "L031") { // 신용카드매출전표
		//sArg = sArg + " issuObjFlg=" + wconv.quote("02");
		issuObjFlg = "02";
	} 

	//var result = Dialog("dpt_rec::F230_Pop.xml", sArg, 744, 363);

	var promise = Promise.resolve();	        
         //promise = promise.then(function(rtnVal) {	
         promise = promise.then(function() {
		var indata  = { "sCrNo"  	 : Div1_msk_sCrNo.getValue(),
						"chkPost" 	 : chk_Post.getValue(),
						"chkEmail" 	 : chk_Email.getValue(),
						"chkFax" 	 : chk_Fax.getValue(),
						"sFax1" 	 : edt_fax1.getValue(),
						"sFax2" 	 : edt_fax2.getValue(),
						"sFax3" 	 : edt_fax3.getValue(),
						"sContName"  : edt_ContName.getValue(),
						"issuObjFlg" : issuObjFlg
						};
		var popInfo = {
		    "width"   : 900,
		    "height"  : 420,
		    "indata"    : indata,
		    //"callback"  : scwin.popupCallback,
		    //"title"     : ""
		};
		return wprom.popupOpen("/ui/cbsc/CBSC078P.xml", popInfo);
	});

	// 등록완료 되면 재조회 
	//promise = promise.then(function(rtnVal) {			
	promise = promise.then(function() {
		var pObj = wprom.getReturnData();
		if(wconv.length(pObj)>0){					
			var groupRetArr = pObj.split("^");
			console.log("********** 영수증번호 : "+groupRetArr[0]);		
			edt_sRecpNo.setValue(groupRetArr[0]);//영수증번호 setting
			sRecpNo = groupRetArr[0];    // 영수증번호
			sIsspl = groupRetArr[1];     // 발행부서명
			sIssur = groupRetArr[2];     // 발행자명
			sIssuDate = groupRetArr[3];  // 발행일자
			sIssuHms  = groupRetArr[4];  // 발행시간

			sCrNo             = groupRetArr[5]; 
			sGdCdName         = groupRetArr[6];
			sCrtorCd          = groupRetArr[7];
			sCrtorName        = groupRetArr[8];
			sInrpsCd          = groupRetArr[9];
			sInrpsName        = groupRetArr[10];
			sCardNo           = groupRetArr[11];
			sAdmitNo          = groupRetArr[12];
			sIssueCardComName = groupRetArr[13];
			sValidThru        = groupRetArr[14];
			nReqAmt           = groupRetArr[15];
			sInstall          = groupRetArr[16];
			sAdmitDate        = groupRetArr[17];
			sAdmitTime        = groupRetArr[18];
			return true;	
		}else{
			return false;
		}
	});
	return promise;
};

//---------------------------------------------------------------------------
// tobe : scwin.checkValid
// asis : checkValid
// 내용 : 발송 버튼 이벤트시 유효성검사.
//---------------------------------------------------------------------------
scwin.checkValid = function(){
	//alert("발송전 check ====================>")

	if(rdo_sDmcode == "L001" && cmb_GdList.getSelectedIndex()  == 0){
		wprom.alertF("상품을 선택하십시오.", {}, cmb_GdList);	
		return false;
	}

	if ( cmd_SearchStd.getValue() == "1" && wconv.length(Div1_msk_sCrNo.getValue()) == 0 ){
        wprom.alertF("계약 번호가 없습니다.", {}, Div1_msk_sCrNo);	        
        return false;	
	}	

	if ( cmd_SearchStd.getValue() == "2" && wconv.length(Div1_msk_sCrNo.getValue()) == 0 ){
        wprom.alertF("설계 번호가 없습니다.", {}, Div1_msk_sCrNo);			
        return false;	
	}	

	// 계약 변경승인서
	if ( (rdo_sDmcode == "L018" || rdo_sDmcode == "L161" || rdo_sDmcode == "LL04") && edt_nCrChngNo.getValue() == "")
	{
        wprom.alertF("계약변경 번호를 입력하세요.",{},edt_nCrChngNo);        
        return false;	
	}

	// 계약 변경승인서
	if ((rdo_sDmcode == "L018" || rdo_sDmcode == "L161" || rdo_sDmcode == "LL04") && 
	(
	 wconv.parseInt(Ds_LTIEA00.getCellData(0, "nCrChngNo")) < wconv.parseInt(edt_nCrChngNo.getValue())
	)
	)
	{
		wprom.alertF("존재 하지 않는 계약변경번호 입니다. 계약변경번호를 확인 하십시오.",{},edt_nCrChngNo);		
		return false;
	}

	if ((rdo_sDmcode == "L018" || rdo_sDmcode == "L161") && edt_nCrChngNo.getValue() == 0)
	{
		wprom.alertF("존재 하지 않는 계약변경번호 입니다. 계약변경번호를 확인 하십시오.",{},edt_nCrChngNo);		
		return false;
	}

	// 보험계약변경서면동의서
	if (rdo_sDmcode == "L054" && 
	(
	wconv.parseInt(Ds_LTIEA00.getCellData(0, "nCrChngNo")) < wconv.parseInt(edt_nCrChngNo.getValue())
	)
	)
	{
		wprom.alert("존재 하지 않는 계약변경번호 입니다. 계약변경번호를 확인 하십시오.");
		return false;
	}

	//2015.01.26 (N1501-00121)
	if(scwin.lfn_chkAccLmt(Div1_msk_sCrNo.getValue())==false){
		return false;
	}


	// 년도 필요한 발급물
	if ( (rdo_sDmcode == "L028" || rdo_sDmcode == "L023"||
		  rdo_sDmcode == "L024" || rdo_sDmcode == "L026"||
		  rdo_sDmcode == "L147" || rdo_sDmcode == "L228"
		  ) && cmb_Year.getSelectedIndex() == 0 ){	
        wprom.alertF("년도를 선택하세요.", {}, cmb_Year);	        
        return false; 	
	}


	// 수납확인서
	if ( rdo_sDmcode == "L028" && cmb_Year.getValue() == "" ){
        wprom.alertF("대상년도를 선택하십시오.", {}, cmb_Year);	        
        return false;	
	}

	if ( chk_Post.getValue() == "1" && cmb_DmMethod.getSelectedIndex() <= 0 ){
        wprom.alertF("발송 방법을 선택하십시오.", {}, cmb_DmMethod);	        
        return false;	
	}

	if ( chk_Post.getValue() != "1" 
		&& chk_Email.getValue() != "1" 
		&& chk_Fax.getValue() != "1" 
		&& chk_lms.getValue() != "1"
		){
        wprom.alertF("발송 종류를 선택하십시오.", {}, chk_Post);	
        return false;
	}

	if ( chk_Email.getValue() == "1" ){
		if( wconv.length( edt_sEmail.getValue()) == 0 ) {
			wprom.alertF("Email 주소를 입력하십시오.", {}, chk_Email);	
			return false;
		}
		if( wconv.length( edt_sEmail1.getValue()) == 0 ) {
			wprom.alertF("Email 주소를 선택 하십시오.", {}, edt_sEmail1);	
			return false;
		}
	}
	if ( chk_Post.getValue() == "1" ){
		if( wconv.length( msk_HomeZipCode1.getValue()) == 0  
			&& wconv.length( msk_HomeZipCode2.getValue()) == 0  
			&& wconv.length( edt_HomeVilAddr.getValue()) == 0 ) {
			wprom.alertF("발송지 주소를 정확히 입력하십시오.", {}, chk_Post);	
			return false;
		}
	}

	if ( chk_Fax.getValue() == "1" ){
		if ( wconv.length( edt_fax1.getValue() ) == 0 ){
			wprom.alertF("Fax 정보를 정확히 입력하십시오.", {}, edt_fax1);	
			return false;
		}
		if ( wconv.length( edt_fax2.getValue() ) == 0 ){
			wprom.alertF("Fax 정보를 정확히 입력하십시오.", {}, edt_fax2);	
			return false;
		}
		if ( wconv.length( edt_fax3.getValue() ) == 0 ){
			wprom.alertF("Fax 정보를 정확히 입력하십시오.", {}, edt_fax3);	
			return false;
		}
	}

	if ( chk_lms.getValue() == "1" ){
		if ( wconv.length( edt_hp1.getValue() ) == 0 ){
			wprom.alertF("핸드폰 정보를 정확히 입력하십시오.", {}, edt_hp1);	
			return false;
		}
		if ( wconv.length( edt_hp2.getValue() ) == 0 ){
			wprom.alertF("핸드폰 정보를 정확히 입력하십시오.", {}, edt_hp2);	
			return false;
		}
		if ( wconv.length( edt_hp3.getValue() ) == 0 ){
			wprom.alertF("핸드폰 정보를 정확히 입력하십시오.", {}, edt_hp3);	
			return false;
		}
	}

	if(rdo_sDmcode == "L144" || rdo_sDmcode == "L156" 
		|| rdo_sDmcode == "LL10" || rdo_sDmcode == "LL11" 
		|| rdo_sDmcode == "LL04" || rdo_sDmcode == "LL01_21"
		|| rdo_sDmcode == "L016" || rdo_sDmcode == "L155"
		|| rdo_sDmcode == "L156" || rdo_sDmcode == "LL01"
		|| rdo_sDmcode == "LL02"
		){
		if(cmb_sPrintReason.getValue() == ""){			
			wprom.alertF("발행구분을 선택하십시오.", {}, cmb_sPrintReason);	
			return false;
		}		
	}

	if(rdo_sDmcode == "L231"){		
		if(wconv.length(wconv.trim(edt_sSrtDate.getValue())) == 0 
			|| wconv.length(wconv.trim(edt_sEndDate.getValue())) == 0){					
			wprom.alertF("조회기준일을 선택하십시오.", {}, cmb_sPrintReason);	
			return false;
		}
	}

	if(rdo_sDmcode == "L232"){			
		if( wconv.length(wconv.trim(edt_sCallDate.getValue())) == 0){				
			wprom.alertF("기준일자를 선택하십시오.", {}, edt_sCallDate);	
			return false;
		}
	}

	return true;
};

//---------------------------------------------------------------------------
// tobe : scwin.lfn_chkAccLmt
// asis : lfn_chkAccLmt
// 내용 : 
//---------------------------------------------------------------------------
scwin.lfn_chkAccLmt = function(arg){

	var sType="";

	if(wconv.length((arg)) > 0) {
		if(wconv.substr(arg,0,1) == "M") {
			sType = "1";
		}else{
			sType = "2";
		}

		if(gfn_chekAcesCntrAuth("DsWs_chekCrInqrAuth", sType, arg) == false){
			alert("접근 권한이 없습니다.");
			return false;
		}
		return true;
	}
};


//---------------------------------------------------------------------------
// tobe : scwin.lfn_save
// asis : lfn_save
// 내용 : 청약서발행후 발행일자update
//---------------------------------------------------------------------------
scwin.lfn_save = function(){

	var MethodObj = new callService("com.inswave.longterm.ssr.out.task.DBB410Task", "setPrintSave", 0, 0);
	MethodObj.setId("setPrintSave");
	MethodObj.setParam("sPlanNo", Ds_LTIEA00.getCellData(0, "sPlanNo"));
	MethodObj.setParam("sDmcode", rdo_sDmcode);
	MethodObj.setUrl("/sales/CBSC103MController.do");
	MethodObj.setXPath("//*/RESULT");
	MethodObj.addXPath("//*/WARNING");	
	MethodObj.sync();
	MethodObj.send();    

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);	
	ds_Save_chk.setJSON (_tranSync.getDsJson("RESULT")); // dataset 데이터
	ds_Wanning.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터
	Ds_SAVEFAXINFO_SaveWarning.setJSON (_tranSync.getDsJson("WARNING")); // SchemaInfo="DataObject:DOBJ_AddUser" ★확인필요



	if(ds_Wanning.getRowCount() != 0) {
		alert(DsWs_Warning.getCellData(0, "msg"));
		return;
	}

	//청약서발행일
	sSrdoIssuDate = sToday;
	if (wconv.length(sPlanNo) > 0 ) {
       if (wprom.isPopup()){
       		var parentObjet = wprom.getParamObject();	
       		parentObjet.DsWs_LTIDA00.setCellData(0,"sSrdoIssuDate", sSrdoIssuDate);
       		parentObjet.Ds_LTIDA00.setCellData(0,"sSrdoIssuDate", sSrdoIssuDate);
       		 //wprom.close(sSrdoIssuDate);
        }


		 //parent.DsWs_LTIDA00.setCellData(0,"sSrdoIssuDate", sSrdoIssuDate);
		 //parent.parent.DS_LTIDA00.setCellData(0,"sSrdoIssuDate", sSrdoIssuDate);
	}		
};


//---------------------------------------------------------------------------
// tobe : scwin.cmb_Email_onviewchange
// asis : lfn_eMail_OnChanged
// 이메일 주소 선택시 데이터 셋팅.
//---------------------------------------------------------------------------
scwin.cmb_Email_onviewchange = function(info){	
	if(wconv.substr(info.newValue,0,1) == "@"){		    
		edt_sEmail1.setValue(wconv.substr(info.newValue,1,wconv.length(info.newValue)));
	}
};

