		reportInfo.taskValue 		= taskValue;				// 인터페이스: B640_Rep.ozr(단체명세서	장기보험)
		reportInfo.actionValue 		= actionValue;				//인터페이스 사용유무 (필수) ex) "Y" or "N" 
	 	reportInfo.gUserID        	= gUserID;			 		//레포트 하단에 출력될 발급자명 ex) "홍길동"
