		reportInfo.setParam  		= setParam;					//인테페이스에서 사용하는 파라미터 정보 배열로 파라미터 개수만큼 입력한다.
		reportInfo.sFormParam  		= setParam;					//레포트 에서 사용하는 파라미터 정보 배열로 파라미터 개수만큼 입력한다.
		reportInfo.title    	 	= DS_SAVESENDPRINT.getCellData(i, "sContents");				//팝업창 타이틀
		reportInfo.left				= i+100;
		reportInfo.bottom_arg1		= DS_SAVESENDPRINT.getCellData(i, "bottom_arg1");
		reportInfo.bottom_arg2		= DS_SAVESENDPRINT.getCellData(i, "bottom_arg2");
		reportInfo.bottom_arg3		= DS_SAVESENDPRINT.getCellData(i, "bottom_arg3");

		//setTimeout(function() {wprom.reportOpen(reportInfo)}, i*100);	  
		wprom.reportOpen(reportInfo);   
	}
};


