//---------------------------------------------------------------------------
// tobe : scwin.SendTotalNoticeInsert
// asis : SendTotalNoticeInsert
// 내용 : 
//---------------------------------------------------------------------------
scwin.SendTotalNoticeInsert = function() {

	wconv.clear(Ds_TotalList);	
	util.appendRow(Ds_TotalList);

	var MethodObj = new callService("com.inswave.longterm.psv.com.task.EYE451Task", "insurTotInsNote", 0, 0);
	MethodObj.setId("insurTotInsNote");	
	MethodObj.setParam("sStndDate", wconv.toString(wconv.toNumber(cmb_Year.getValue())-1) + "1231");
	MethodObj.setParam("sStndDate1", Ds_TotalListChk.getCellData(0, "sCloseYm") + util.getLastDay(Ds_TotalListChk.getCellData(0, "sCloseYm")));
	MethodObj.setParam("sUserID", gUserID);
	if(cmd_SearchStd.getValue() == "1"){ //계약번호
		MethodObj.setParam("sCrNo", Div1_msk_sCrNo.getValue()); 
		MethodObj.setParam("sCrtorCd", ""); 
	}else if(cmd_SearchStd.getValue() == "3" ){ //고객번호
		MethodObj.setParam("sCrNo", "");
		MethodObj.setParam("sCrtorCd", Div2_msk_CstNo.getValue()); 
	}
	if(rdo_sDmcode == "L147"){
		MethodObj.setParam("sSendFlag", "I"); 
	}else if(rdo_sDmcode == "L228"){
		MethodObj.setParam("sAnntSendFlag", "I");
	}	

	var sType = "0000";
	// [ 발송 Flag 설정 ] 
	if ( chk_Post.getValue() == "1" ) { sType = "POST";  }	//우편
	if ( chk_Email.getValue() == "1" ){ sType = "EMAIL"; }	//이메일
	if ( chk_Print.getValue() == "1" ){ sType = "PRINT"; }  //출력
	if ( chk_Fax.getValue() == "1")   { sType = "FAX";   }	//팩스
	MethodObj.setParam("sType", sType);		

	MethodObj.setUrl("/sales/CBSC103MController.do");
	MethodObj.setXPath("//*/vector/data/LTIEZ65");
	MethodObj.sync();
	MethodObj.send();    


	console.log(JSON.stringify(_tranSync.result));
    util.interfaceKey(_tranSync.result.serviceDs);	
	DsWs_TotalList.setJSON (_tranSync.getDsJson("LTIEZ65")); // dataset 데이터

	if(DsWs_Warning.getRowCount() != 0){
		alert(DsWs_Warning.getCellData(0, "msg"));
		return;
	}else {				
		wconv.copy(Ds_TotalList, DsWs_TotalList);	
	}			
	nDmNo = Ds_TotalList.getCellData(0, "nDmNo");

};


