scwin.setFaxInfo = function() {

	//alert("FAX==========================>");				

	var sFax1 = edt_fax1.getValue();
	var sFax2 = edt_fax2.getValue();
	var sFax3 = edt_fax3.getValue();	

	if(rdo_sDmcode == "L054"){
		if(edt_nCrChngNo.getValue() == ""){
			DS_SAVEFAXINFO.setCellData( 0 , "nChgNum" ,  0);   	// 계약변경번호
		} else {
			DS_SAVEFAXINFO.setCellData( 0 , "nChgNum" ,  edt_nCrChngNo.getValue());   	// 계약변경번호
		}
	} else {
		DS_SAVEFAXINFO.setCellData( 0 , "nChgNum" ,  edt_nCrChngNo.getValue());   	// 계약변경번호
	}	

	DS_SAVEFAXINFO.setCellData( 0 , "sBizCode" , Div1_msk_sCrNo.getValue()  );		// 계약번호 
	DS_SAVEFAXINFO.setCellData( 0 , "sIssuYear" , cmb_Year.getValue()  );		        // 대상년도 정보			
	DS_SAVEFAXINFO.setCellData( 0 , "sType" , "1"  );	
