	DS_SAVEFAXINFO.setCellData( 0 , "nSeqNo" , "0"  );	
	DS_SAVEFAXINFO.setCellData( 0 , "sGdCd" , Ds_LTIEA00.getCellData(0, "sGdCd") ); // 상품코드	
