    DS_SAVEFAXINFO.setCellData( 0 , "sSrtDate" , edt_sSrtDate.getValue());
    DS_SAVEFAXINFO.setCellData( 0 , "sEndDate" , edt_sEndDate.getValue());

	if(rdo_sDmcode == "L010"){ //상품설명서 

    	if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , "L216"  );
