		}

    }else if(rdo_sDmcode == "L120"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , "L158"  );  //재물보험 전용 대면 가입설계서일체
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , "L186"  );  //
		}else{
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		} 
	}else if(rdo_sDmcode == "L144"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , "L156"  );  //재물보험 대면 보험증권
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk			
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , "L181"  );  //
		}else{
			DS_SAVEFAXINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}	

	}else if(rdo_sDmcode == "L173" || rdo_sDmcode == "L219" ){
