	//[계약관리] 부활질문서 ●
	else if(rdo_sDmcode == "L057")
	{
		if(wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "21"
			|| wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "26"){
			DS_SAVEEMAILINFO.setCellData( 0 , "sFileName" ,  "B473_Rep" );		
			DS_SAVEEMAILINFO.setCellData( 0 , "sContents" , "치아보험전용부활질문서"  );	
		}else{
			DS_SAVEEMAILINFO.setCellData( 0 , "sFileName" ,  "B421_Rep" );		
			DS_SAVEEMAILINFO.setCellData( 0 , "sContents" , "부활질문서"  );		
		}
	}

	//[계약관리] 연금납입확인서 ●
	else if(rdo_sDmcode == "L222")	
	{
		DS_SAVEEMAILINFO.setCellData( 0 , "sFileName" ,  "C800_Rep" );		
		DS_SAVEEMAILINFO.setCellData( 0 , "sContents" , "연금납입확인서"  );
		DS_SAVEEMAILINFO.setCellData( 0 , "sRealCustNo" , sRealCustNo );//2016.05.03
	}

	//[계약관리] 예상해지환급금 ●
	else if(rdo_sDmcode == "L229") //2017.01.17 
	{
		console.log("nInqrSeq:"+nInqrSeq);
		DS_SAVEEMAILINFO.setCellData( 0 , "sFileName" ,  "B952_Rep" );		
		DS_SAVEEMAILINFO.setCellData( 0 , "sContents" , "예상환급금 안내표"  );
		DS_SAVEEMAILINFO.setCellData( 0 , "sDmCode"   , rdo_sDmcode  );								
		DS_SAVEEMAILINFO.setCellData( 0 , "nInqrSeq"  , nInqrSeq	   );					
		DS_SAVEEMAILINFO.setCellData( 0 , "sIssuDate" , wconv.substr(sysDate,0,8));		
	}							
	// 신용카드매출전표	
	else if(rdo_sDmcode == "L031")
	{
		DS_SAVEEMAILINFO.setCellData( 0 , "sFileName" ,  "F240_Rep" );		
		DS_SAVEEMAILINFO.setCellData( 0 , "sContents" , "신용카드매출전표"  );	

		DS_SAVEEMAILINFO.setCellData( 0 , "sGdCdName" , sGdCdName );
		DS_SAVEEMAILINFO.setCellData( 0 , "sCrtorCd" , sCrtorCd );
		DS_SAVEEMAILINFO.setCellData( 0 , "sCrtorName" , sCrtorName );
		DS_SAVEEMAILINFO.setCellData( 0 , "sInrpsCd" , sInrpsCd );
		DS_SAVEEMAILINFO.setCellData( 0 , "sInrpsName" , sInrpsName );
		DS_SAVEEMAILINFO.setCellData( 0 , "sCardNo" , sCardNo );
		DS_SAVEEMAILINFO.setCellData( 0 , "sAdmitNo" , sAdmitNo );
		DS_SAVEEMAILINFO.setCellData( 0 , "sIssueCardComName" , sIssueCardComName );
		DS_SAVEEMAILINFO.setCellData( 0 , "sValidThru" , sValidThru );
		DS_SAVEEMAILINFO.setCellData( 0 , "nReqAmt" , nReqAmt );
		DS_SAVEEMAILINFO.setCellData( 0 , "sInstall" , sInstall );
		DS_SAVEEMAILINFO.setCellData( 0 , "sAdmitDate" , sAdmitDate );
		DS_SAVEEMAILINFO.setCellData( 0 , "sAdmitTime" , sAdmitTime );

	}


	var repoService = scwin.getRepoServiceNm(DS_SAVEEMAILINFO.getCellData(0, "sFileName")).split("^");

	taskValue = repoService[0];
	actionValue = repoService[1];

	DS_SAVEEMAILINFO.setCellData( 0 , "sTaskValue" ,  taskValue );
	DS_SAVEEMAILINFO.setCellData( 0 , "sActionValue" , actionValue );
	DS_SAVEEMAILINFO.setCellData( 0 , "sRptName" ,  "longterm/"+ DS_SAVEEMAILINFO.getCellData(0, "sFileName"));//오즈명
	DS_SAVEEMAILINFO.setCellData( 0 , "sDatatype" ,  "jason" );

	DS_SAVEEMAILINFO.setCellData( 0 , "sBottom_arg1" ,  Ds_LTIEA00.getCellData(0, "sEmpName") );
	DS_SAVEEMAILINFO.setCellData( 0 , "sBottom_arg2" ,  Ds_LTIEA00.getCellData(0, "sFrstDoerCentrCdNm") );
	DS_SAVEEMAILINFO.setCellData( 0 , "sBottom_arg3" ,  Ds_LTIEA00.getCellData(0, "sEmpTel") );	


	console.log("이메일 발송 =================>\n"
				+"sTaskValue:"+DS_SAVEEMAILINFO.getCellData(0,"sTaskValue")+"\n"
				+"sActionValue:"+DS_SAVEEMAILINFO.getCellData(0,"sActionValue")+"\n"
				+"sRptName:"+DS_SAVEEMAILINFO.getCellData(0,"sRptName")+"\n"
				+"sFileName:"+DS_SAVEEMAILINFO.getCellData(0,"sFileName")+"\n"
				+"sContents:"+DS_SAVEEMAILINFO.getCellData(0,"sContents")+"\n"
				+"sBizCode:"+DS_SAVEEMAILINFO.getCellData(0,"sBizCode")+"\n"
				+"sType:"+DS_SAVEEMAILINFO.getCellData(0,"sType")+"\n"
				+"sCustNo:"+DS_SAVEEMAILINFO.getCellData(0,"sCustNo")+"\n"
				+"sGdCd:"+DS_SAVEEMAILINFO.getCellData(0,"nSeqNo")+"\n"
				+"sGdCd:"+DS_SAVEEMAILINFO.getCellData(0,"sGdCd")+"\n"
				+"sCrNo:"+DS_SAVEEMAILINFO.getCellData(0,"sCrNo")+"\n"
				+"sPlanNo:"+DS_SAVEEMAILINFO.getCellData(0,"sPlanNo")+"\n"
				+"sPsnGroupFlgcd:"+DS_SAVEEMAILINFO.getCellData(0,"sPsnGroupFlgcd")+"\n"
				+"imgURL:"+DS_SAVEEMAILINFO.getCellData(0,"imgURL")+"\n"
				+"sContName:"+DS_SAVEEMAILINFO.getCellData(0,"sContName")+"\n"
				+"nBussPrem:"+DS_SAVEEMAILINFO.getCellData(0,"nBussPrem")+"\n"
				+"sApplyType:"+DS_SAVEEMAILINFO.getCellData(0,"sApplyType")+"\n"
				+"sApplyKind:"+DS_SAVEEMAILINFO.getCellData(0,"sApplyKind")+"\n"
				+"sApplySer:"+DS_SAVEEMAILINFO.getCellData(0,"sApplySer")+"\n"
				+"bottom_arg1:"+DS_SAVEEMAILINFO.getCellData(0,"sBottom_arg1")+"\n"
				+"bottom_arg2:"+DS_SAVEEMAILINFO.getCellData(0,"sBottom_arg2")+"\n"
				+"bottom_arg3:"+DS_SAVEEMAILINFO.getCellData(0,"sBottom_arg3")+"\n"
				+"sCallDate:"+DS_SAVEEMAILINFO.getCellData(0,"sCallDate")+"\n"
				+"sSrtDate:"+DS_SAVEEMAILINFO.getCellData(0,"sSrtDate")+"\n"
				+"sEndDate:"+DS_SAVEEMAILINFO.getCellData(0,"sEndDate")+"\n"
				);				

	return true;
};

