	else if( ozName == "B560_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE210Task"; actionValue = "printB560_RepV01";}                        
	else if( ozName == "B900_Rep"){ taskValue = "com.inswave.longterm.psv.ens.task.EBE230Task"; actionValue = "printB900_RepV01";}                                               


	console.log("레포트=====================>\n"
		+"ozName:"+ozName+"/\n"
		+"taskValue:"+taskValue+"/\n"
		+"actionValue:"+actionValue+"/\n"
		+"====================================\n"
		);

	return taskValue+"^"+actionValue;
};





//---------------------------------------------------------------------------
// tobe : scwin.chk_vartAddrs_onviewchange
// asis : chk_vartAddrs_OnClick
// 내용 : 주소등록 ( 변경 ) 체크 시에   비활성 , 활성
//---------------------------------------------------------------------------
scwin.chk_vartAddrs_onviewchange = function(info){
	if (info.value == 1){
		 var result;
		 var parentInfo= "";

		 Ds_long_mPlr.removeAll();	

		 if(Ds_LTIEA00.getRowCount() > 0 && wconv.length(Ds_LTIEA00.getCellData(0, "nMailRvadChngSeq")) > 0){		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sCrtorCstId")+"^";//고객ID		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sCrtorCd")+"^";//주민번호		
		  parentInfo += "0^";//순번		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sCstFlgcd")+"^";//고객유형 내국인		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sAddrFlgcd")+"^";//주소구분코드 자택		
		  parentInfo += Ds_LTIEA00.getCellData(0, "nAddrChngSeq")+"^";//주소변경회차		
		  parentInfo += Ds_LTIEA00.getCellData(0, "nTelnoChngSeq")+"^";//전화회차		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sMailRvadAddrFlagCd")+"^";//발송지주소구분		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sMailRvadAddrFlagCd")+"^";//발송지회차		
		  parentInfo += "Ds_long_mPlr";//데이터셋명 		
		  parentInfo += " screenId=long";//스크린id 
		 }else{		
		  parentInfo += "^";//고객ID		
		  parentInfo += Ds_LTIEA00.getCellData(0, "sCrtorCd") +"^";//주민번호		
		  parentInfo += "0^";//순번		
		  parentInfo += "^";//고객유형 내국인		
		  parentInfo += "^";//주소구분코드 자택		
		  parentInfo += "^";//주소변경회차		
		  parentInfo += "^";//전화회차		
		  parentInfo += "^";//발송지주소구분		
		  parentInfo += "^";//발송지회차		
		  parentInfo += "Ds_long_mPlr";//데이터셋명 		
		  parentInfo += " screenId=long";//스크린id		
		 }				 

    	//고객화면 오픈  
		//대면/비대면체크  Y :  대면 , N: 비대면	
		// (gFaceSalesYN = "N")  //  비대면 
		var pro = Promise.resolve();
		if(gFaceSalesYN = "N"){  				
	   		var indata={
	 				  "parentInfo": parentInfo
				};  

	        pro = pro.then(function(rv) {

				var popInfo = {
					"width"     : 820,
					"height"    : 612,  
					"indata"    : indata,
					"title"     : "개인고객등록[CBSO002P]",
					"callback"  : ""
				};					
				return wprom.popupOpen("/ui/cbso/CBSO002P.xml", popInfo);			            
	        });

			pro = pro.then(function(rv) {
				var pObj = wprom.getReturnData();						
				if(wconv.length(pObj)>0){
					//var resultArr = pObj.split("^");		

				}							
				return;
			});	

		}else{		
	   		var indata={
	 				  "parentInfo": parentInfo
				};  

	        pro = pro.then(function(rv) {

				var popInfo = {
					"width"     : 820,
					"height"    : 612,  
					"indata"    : indata,
					"title"     : "개인고객등록[CBSO002P]",
					"callback"  : ""
				};					
				return wprom.popupOpen("/ui/cbso/CBSO002P.xml", popInfo);			            
	        });

			pro = pro.then(function(rv) {
				var pObj = wprom.getReturnData();						
				if(wconv.length(pObj)>0){
					//var resultArr = pObj.split("^");		

				}							
				return;
			});	
		}		

		chk_vartAddrs.setValue(0);

		if(wconv.length(Ds_long_mPlr.getCellData(0,"nAdrsChangeNo")) > 0)
		{			
			msk_HomeZipCode1.setValue(wconv.substr((Ds_long_mPlr.getCellData(0, "sAdrsZco"),0,3)));
			msk_HomeZipCode2.setValue(wconv.substr((Ds_long_mPlr.getCellData(0, "sAdrsZco"),3,6)));
			edt_HomeVilAddr.setValue(Ds_long_mPlr.getCellData(0, "sAdrs1"));
			edt_HomeDtlAddr.setValue(Ds_long_mPlr.getCellData(0, "sAdrs2"));

			nChangeDMAddrNo = Ds_long_mPlr.getCellData(0, "nAdrsChangeNo");  // 주소변경번호 
			sAddrFlgcd = Ds_long_mPlr.getCellData(0, "sDMAdrsTyp");           // 주소구분
		}


		if(wconv.length(Ds_long_mPlr.getCellData(0,"nAdrsChangeNo"))> 0){		
			if(chk_Email.getDisabled() == false) {			
				if(wconv.length(Ds_long_mPlr.getCellData(0,"sEmail1"))>0){
					var resultArr = Ds_long_mPlr.getCellData(0,"sEmail1").split("@");
					edt_sEmail.setValue(resultArr[0]); 										
					edt_sEmail1.setValue(resultArr[1]);				
				}else{
					edt_sEmail.setValue(""); 														
					edt_sEmail1.setValue("");
				}				
			}					

			if(chk_Fax.getDisabled() == false) {
				if(gProdYN == "Y") {				
				edt_fax1.setValue(Ds_long_mPlr.getCellData(0, "sFax1"));
				edt_fax2.setValue(Ds_long_mPlr.getCellData(0, "sFax2"));
				edt_fax3.setValue(Ds_long_mPlr.getCellData(0, "sFax3"));
				} else {
				//edt_fax1.setValue("");
				//edt_fax2.setValue("");
				//edt_fax3.setValue("");
				}
			}

			nChangeDMTelNo = Ds_long_mPlr.getCellData(0, "nTelChangeNo");			
		}
	}
	return pro;
};


//---------------------------------------------------------------------------
// tobe : scwin.fn_send_check3
// asis : fn_send_check3
// 내용 : 발송전 체크 (지급 발송 여부 체크)
//---------------------------------------------------------------------------
scwin.fn_send_check3 = function(obj){	
	// 지급 쪽 발송 가능 불가능 체크	
	// 지급 모듈 호출
	wconv.clear(Ds_LTIJA01);	
	wconv.clear(DsWs_LTIJA01);

	var sIssuGoodId = "";

	if(rdo_sDmcode == "L052" ) {
		sIssuGoodId = "G650_Rep";
	} else if(rdo_sDmcode == "L053" ) {
		sIssuGoodId = "G630_Rep";
	} else if(rdo_sDmcode == "L022" ) {
		sIssuGoodId = "L022_Rep";
	} else if(rdo_sDmcode == "L025" ) {
		sIssuGoodId = "H350_Rep";
	} else if(rdo_sDmcode == "L023" ) {
		sIssuGoodId = "H310_Rep";
	} else if(rdo_sDmcode == "L024" ) {
		sIssuGoodId = "H320_Rep";
	} else if(rdo_sDmcode == "L233" ) {
		sIssuGoodId = "B770_Rep";
	}	


	var MethodObj = new callService("com.inswave.longterm.lon.out.task.JCR001Task", "selectRepYn", 0, 0);
	MethodObj.setId("selectRepYn");
	if(cmd_SearchStd.getValue() == "1") {
		MethodObj.setParam("sCrNo", Div1_msk_sCrNo.getValue());      // 계약번호 
	} else {
		MethodObj.setParam("sPlanNo", Div1_msk_sCrNo.getValue());    // 설계번호 
	}
	MethodObj.setParam("sIssuGoodId", sIssuGoodId);
	MethodObj.setParam("sStndYear",  "");
	MethodObj.setUrl("/sales/CBSC103MController.do");
	MethodObj.setXPath("//*/vector/data/LTIJA01");
	MethodObj.addXPath("//*/WARNING");
	MethodObj.sync();
	MethodObj.send();    

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);	

	DsWs_LTIJA01.setJSON (_tranSync.getDsJson("LTIJA01")); // dataset 데이터
	DsWs_Warning_JA01.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터

	if(wconv.length(DsWs_Warning_JA01.getCellData(0,"msg")) > 0) {
		alert(DsWs_Warning_JA01.getCellData(0,"msg"));	
	} else {		
			//Ds_LTIJA01.Copy("DsWs_LTIJA01");		
			wconv.copy(Ds_LTIJA01, DsWs_LTIJA01)				
	}			


	// 년도 필요
	if(rdo_sDmcode == "L023" || rdo_sDmcode == "L024") {
		if ((Ds_LTIJA01.getCellData(0, "sIssuAvaYn") = "N") ||
			(Ds_LTIJA01.getCellData(0, "sIssuAvaYn") == "") ||
			(Ds_LTIJA01.getCellData(0, "sIssuAvaYn") == null)){
			alert("해당 계약번호는 출력가능 하지 않습니다.");
			return false;
		} else if (Ds_LTIJA01.getCellData(0, "sStndYear") == "" 
			|| Ds_LTIJA01.getCellData(0, "sStndYear") == null){			
			return false;
//		} else if (Ds_LTIJA01.getCellData(0, "sStndYear") != cmb_Year.getValue()){			
//			return false;
		}   
	}

	if(rdo_sDmcode == "L052" || rdo_sDmcode == "L053" || rdo_sDmcode == "L022" || rdo_sDmcode == "L025") {
		if ((Ds_LTIJA01.getCellData(0, "sIssuAvaYn") == "N") ||
			(Ds_LTIJA01.getCellData(0, "sIssuAvaYn") == "") ||
			(Ds_LTIJA01.getCellData(0, "sIssuAvaYn") == null)){			
			alert("해당 계약번호는 출력가능 하지 않습니다.");
			return false;
		} 
	}

	return true;
};

//---------------------------------------------------------------------------
// tobe : scwin.fn_send_check1
// asis : fn_send_check1
// 내용 : 발송전 체크 
//---------------------------------------------------------------------------
scwin.fn_send_check1 = function(obj){

	// 해지환급금예시안내  / 가입설계서 일체
	// 청약 모듈 호출
	wconv.clear(DsWs_RtnAmt);

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB200Task", "getExptRtnAmtList", 0, 0);
	MethodObj.setId("getExptRtnAmtList");
	MethodObj.setParam("sPlanNo", Ds_LTIEA00.getCellData(0, "sPlanNo"));
	MethodObj.setParam("sPsnGroupFlgcd",  Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd"));

	MethodObj.setUrl("/sales/CBSC103MController.do");
	MethodObj.setXPath("//*/WARNING");
	MethodObj.sync();
	MethodObj.send();    

	//console.log(JSON.stringify(_tranSync.result));
    //util.interfaceKey(_tranSync.result.serviceDs);	
	DsWs_Warning_RtnAmt.setJSON (_tranSync.getDsJson("WARNING")); // dataset 데이터

	if( _tranSync.result == false ){
		alert(DsWs_Warning_RtnAmt.getCellData(0, "msg"));
		return false;
	} else {
		// 발송 //★확인필요
		scwin.send_start();
	}
};

//---------------------------------------------------------------------------
// tobe : scwin.fn_send_check2
// asis : fn_send_check2
// 내용 : 발송전 체크 ( 정상 , 실효 만 발송 가능 여부 체크	)
//---------------------------------------------------------------------------
scwin.fn_send_check2 = function(obj){


	// 정상 , 실효 만 발송 가능한 것들.	
	var sCrStatCdChk = Ds_LTIEA00.getCellData(0, "sCrStatCd");	

	// 장기업무팀은 정상 실효 상관없이 모두 발송 가능.
	if (nfn_checkAuthByID("ds_code", gUserID , "22030") == false){		
		return true;
	} else if((nfn_checkAuthByID("ds_code", gUserID , "22030") != false) &&
			  sCrStatCdChk != "00" && sCrStatCdChk != "10"){			  
			alert("선택한 발행물은 계약상태가 정상/실효 인것만 발송 가능합니다.");			
			return false;					
	} else {	
		return true;
	}
};


//---------------------------------------------------------------------------
// tobe : scwin.setPrintInfo
// asis : setPrintInfo
// 내용 : 우편발송 정보 셋팅.
//---------------------------------------------------------------------------
scwin.setPrintInfo = function(){

	var sRelKey  = "";
	var sKeyType = "";
	var sPrintReason = ""; //발행구분 : 01 자동발행, 02 단순재발행, 04 배서재발행	

	//  sRelKey  키를 하나로 연결. 
	// 청약화면 연결시
	if((tab == 1) || (tab == 3))
	{
		if(rdo_sDmcode == "L120" || rdo_sDmcode == "L143" || rdo_sDmcode == "L152" || rdo_sDmcode == "L050" || rdo_sDmcode == "L158") {
			sRelKey = sPlanNo + sPsnGroupFlgcd + sGdCd;
			sKeyType = 1;
		} else if(rdo_sDmcode == "L133"){
			sRelKey = sPlanNo;
			sKeyType = 1;
		}
	}	

	// 가입설계서 
	if((tab == "" || (tab == null)) 
		&& (rdo_sDmcode == "L120" || rdo_sDmcode == "L143" || rdo_sDmcode == "L152" || rdo_sDmcode == "L050" || rdo_sDmcode == "L158" )) {	
		sRelKey = Ds_LTIEA00.getCellData(0, "sPlanNo") + Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd") + Ds_LTIEA00.getCellData(0, "sGdCd");
		sKeyType = 1;

	// TM 계약 서면동의서 , TM 계약 개인신용정보 동의서(L012, L116) , TM계약 특별조건부 부담보 청약서
	} else if ((tab == "" || (tab == null)) 
	&&  (rdo_sDmcode == "L133") || (rdo_sDmcode == "L004") || (rdo_sDmcode == "L140") || (rdo_sDmcode == "L010") 
	 || (rdo_sDmcode == "L135") || (rdo_sDmcode == "L150") || (rdo_sDmcode == "L154")
	 || (rdo_sDmcode == "L136") || (rdo_sDmcode == "L137") || (rdo_sDmcode == "L138") || (rdo_sDmcode == "L139") 
	 || (rdo_sDmcode == "L142") || (rdo_sDmcode == "L171") || (rdo_sDmcode == "L172") || (rdo_sDmcode == "L173") 
	 || (rdo_sDmcode == "L219") || (rdo_sDmcode == "L045") || (rdo_sDmcode == "L054") || (rdo_sDmcode == "L057")
	 ) {	 
		sRelKey = Ds_LTIEA00.getCellData(0, "sPlanNo");
		sKeyType = 1;
	// 증권
	} else if (rdo_sDmcode == "L144" || rdo_sDmcode == "L156") {	
		sRelKey = Ds_LTIEA00.getCellData(0, "sCrNo") + Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd");
		sKeyType = 2;
	//대출약정서					
	} else if(rdo_sDmcode == "L022"){
		sRelKey = Ds_LTIEA00.getCellData(0, "sCrNo");
		sKeyType = 2;
	//TM 상품설명서, 가입증명서		
	} else if(rdo_sDmcode == "L032"){
		sRelKey = Ds_LTIEA00.getCellData(0, "sCrNo") + Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd");
		sKeyType = 2;
	// 공직자 재산등록용 잔액증명서
	} else if (rdo_sDmcode == "L221") {		
		sRelKey = Ds_LTIEA00.getCellData(0, "sPlanNo")+edt_sCallDate.getValue();
		sKeyType = 2;
	// 수납확인서 
	} else if (rdo_sDmcode == "L028" ) { 		
		sRelKey = Ds_LTIEA00.getCellData(0, "sCrNo");
		sKeyType = 2;		
	// 연금납입서
	} else if (rdo_sDmcode == "L222") { 
		sRelKey = Ds_LTIEA00.getCellData(0, "sCrNo"); //2016.05.02
		sKeyType = 2;	
	//  변경승인서
	} else if (rdo_sDmcode == "L018"  || rdo_sDmcode == "L161" || rdo_sDmcode == "LL04") {

		if(edt_nCrChngNo.getValue() == ""){
			edt_nCrChngNo.setValue(0);
		}
		sRelKey = Ds_LTIEA00.getCellData(0, "sCrNo") + edt_nCrChngNo.getValue();
		sKeyType = 2;
	//계약관리 안내장, 연금저축 수익률 보고서
	}else if (rdo_sDmcode == "L147" || rdo_sDmcode == "L228" ) {
		scwin.SendTotalNoticeInsert();
		sRelKey =  nDmNo;
	}

	DS_SAVESENDINFO.setCellData( 0 , "sScrrnId" ,  "CBSC103M");                         // 화면ID	
	if(cmd_SearchStd.getValue() == "1" || cmd_SearchStd.getValue() == "2"){
		DS_SAVESENDINFO.setCellData( 0 , "sBizCode" ,  Div1_msk_sCrNo.getValue());           // 계약번호
	}else{
		DS_SAVESENDINFO.setCellData( 0 , "sBizCode" ,  Div2_msk_CstNo.getvalue());           // 계약번호
	}	
	DS_SAVESENDINFO.setCellData( 0 , "nChgNum" ,  edt_nCrChngNo.getValue());              // 계약변경번호
	DS_SAVESENDINFO.setCellData( 0 , "sRelKey" , sRelKey );                		  //
	DS_SAVESENDINFO.setCellData( 0 , "sKeyType" , sKeyType );         

	if(rdo_sDmcode == "LL10"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL12"  );
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL16"  );
		}else{	
			if(wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "21" || wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "26"){
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL10"  );
			}else{
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
			}
		}
	//재물보험인경우 가입설계서, 청약서, 서면동의서, 상품설명서, 보험증권 양식코드 분리
	}else if(rdo_sDmcode == "LL11"){
			if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL13"  );
			}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk				
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL17"  );
			}else{	
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
			}
	}else if(rdo_sDmcode == "L049"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L152"  );
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L189"  );
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}
	}else if(rdo_sDmcode == "L050"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){   //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L193"  );
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}
	}	
	else if(rdo_sDmcode == "L144"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L156"  );
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L181"  );
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}
	}else if(rdo_sDmcode == "L233"){
		DS_SAVESENDINFO.setCellData(0, "sDmCode", "L233");
	}else{

		DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );

	}

	//[계약관리] - 변경팩(변경승인서+증권)
	if(rdo_sDmcode == "LL04"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL08"  ); //재물보험 변경팩
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL19"  ); //
		}else{
			if(wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "21"|| wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "26"){ //갱신형 상품
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "LL05"  );
			}else{
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
			}
		}
	//[계약관리] - 계약변경승인서
	}else if(rdo_sDmcode == "L018"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L161"  );
		}else{

			if(wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "21"|| wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "26"){ //갱신형 상품
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L129"  );
			}else{
				DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
			}
		}
	//[계약관리] - 부활질문서
	}else if(rdo_sDmcode == "L057"){
		if(wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "21"|| wconv.substr(Ds_LTIEA00.getCellData(0, "sGdCd"), 0, 2) == "26"){ //갱신형 상품
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L128"  );
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}
	//[계약관리] - 가입증명서
	}else if(rdo_sDmcode == "L032"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L160"  ); //재물보험 비대면 가입증명서
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L195"  );
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}	
	//[계약관리] - 보험계약(변경) 서면동의서
	}else if(rdo_sDmcode == "L054"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L162"  ); //재물보험 비대면 보험계약(변경)서면동의서
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}
	//[계약관리] - 부활청약서
	}else if(rdo_sDmcode == "L045"){
		if(Ds_LTIEA00.getCellData(0, "sBunsMetdClsfCd") == "1"){
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L163"  ); //재물보험 비대면 부활청약서(음성녹음서)
		}else if(Ds_LTIEA00.getCellData(0, "sBunsMetdDocuClsfCd") == "7"){ //연금 edit_by_sk
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , "L196"  ); //
		}else{
			DS_SAVESENDINFO.setCellData( 0 , "sDmCode" , rdo_sDmcode  );
		}
	}

	DS_SAVESENDINFO.setCellData( 0 , "sCallSawon" , gUserID );		
	DS_SAVESENDINFO.setCellData( 0 , "sCrNo" , Ds_LTIEA00.getCellData(0, "sCrNo") );  	// 계약번호
	DS_SAVESENDINFO.setCellData( 0 , "sPlanNo" , Ds_LTIEA00.getCellData(0, "sPlanNo") ); // 설계번호
	DS_SAVESENDINFO.setCellData( 0 , "sSsrtDate" , Ds_LTIEA00.getCellData(0, "sInsurStrtdate") ); // 보험시작일자
	DS_SAVESENDINFO.setCellData( 0 , "sPsnGroupFlgcd" , Ds_LTIEA00.getCellData(0, "sPsnGroupFlgcd")  );	// 개인단체구분코드	
	DS_SAVESENDINFO.setCellData( 0 , "sGdCd" , Ds_LTIEA00.getCellData(0, "sGdCd")  );	// 상품코드	
	DS_SAVESENDINFO.setCellData( 0 , "sPrintReason" , cmb_sPrintReason.getValue());	
	DS_SAVESENDINFO.setCellData( 0 , "nBussPrem" , Ds_LTIEA00.getCellData(0, "nBussPrem") );
	DS_SAVESENDINFO.setCellData( 0 , "sCallDate" , edt_sCallDate.getValue());
    DS_SAVESENDINFO.setCellData( 0 , "sSrtDate" , edt_sSrtDate.getValue());
    DS_SAVESENDINFO.setCellData( 0 , "sEndDate" , edt_sEndDate.getValue());	

	//장기보험고도화 - 설계번호로 조회시 ea09cnt는 count를 조회, 계약번호로 조회시 ea09cnt는 빈값이므로 0일때랑 length 둘다 체크
	if(wconv.length(Ds_LTIEA00.getCellData(0, "ea09cnt")) == 0){
		DS_SAVESENDINFO.setCellData( 0 , "L007Yn" , "N"  );	// 부담보 데이터 여부 ( L007 발송 여부 체크)	
	} else {
		DS_SAVESENDINFO.setCellData( 0 , "L007Yn" , "Y"  );	// 부담보 데이터 여부 ( L007 발송 여부 체크)	
	}

    //이륜자동차운전중상해 부담보 특약 신청서
    if(Ds_LTIEA00.getCellData(0, "sTwhvcSbcvrYncnt") == "0") {
		DS_SAVESENDINFO.setCellData( 0 , "L133Yn" , "N"  );	// 이륜자동차운전중상해 부담보 특약 신청 여부 ( L007 발송 여부 체크)	
	}else{
		DS_SAVESENDINFO.setCellData( 0 , "L133Yn" , "Y"  );	
	}

	//보험계약 이동(승환계약)에 따른 비교안내 확인서 L134 
	if(Ds_LTIEA00.getCellData(0, "sCmprCrNotcSsrt") == "Y") {
		DS_SAVESENDINFO.setCellData( 0 , "L134Yn" , "Y"  );	
	}else{
		DS_SAVESENDINFO.setCellData( 0 , "L134Yn" , "N"  );	
	}

	// 약관
	if(rdo_sDmcode == "L044") {
		DS_SAVESENDINFO.setCellData( 0 , "sRelKey" , Ds_LTIEA00.getCellData(0, "sPlanNo") );  
		DS_SAVESENDINFO.setCellData( 0 , "sKeyType" , "1" );
		DS_SAVESENDINFO.setCellData( 0 , "sAdrEx" , "N" );
	}

	// 개별납입영수증
	if(rdo_sDmcode == "L019"){
		DS_SAVESENDINFO.setCellData( 0 , "sRelKey" , sRecpNo );              		// 영수증번호
		DS_SAVESENDINFO.setCellData( 0 , "sKeyType" , "6" );		
	}	

	//연금납입확인서
	if(rdo_sDmcode == "L222"){
		DS_SAVESENDINFO.setCellData( 0 , "sRealCustNo" , Ds_LTIEA00.getCellData(0, "sRealCustNo") ); //주민번호
	}

	//예상해지환급금안내표
	if(rdo_sDmcode == "L229"){ 
		DS_SAVESENDINFO.setCellData( 0 , "nInqrSeq" , nInqrSeq ); 		
		DS_SAVESENDINFO.setCellData( 0 , "sRelKey" , Ds_LTIEA00.getCellData(0, "sCrNo")+nInqrSeq );  
	}


	DS_SAVESENDINFO.setCellData( 0 , "sCustNo" , Ds_LTIEA00.getCellData(0, "sCrtorCd") ); // 계약자코드
	DS_SAVESENDINFO.setCellData( 0 , "sDmMethod" , cmb_DmMethod.getValue() );         	 // 발송방법
	DS_SAVESENDINFO.setCellData( 0 , "sAdrEx" , "Y" );
	DS_SAVESENDINFO.setCellData( 0 , "nSeqNo", "0" );
	DS_SAVESENDINFO.setCellData( 0 , "sLoginName" , gUserEmpName );	
	DS_SAVESENDINFO.setCellData( 0 , "sApplyType" , wconv.substr(Div1_msk_sCrNo.getValue(),0,1));		
	DS_SAVESENDINFO.setCellData( 0 , "sApplyKind" , wconv.substr(Div1_msk_sCrNo.getValue(),1,4));		
	DS_SAVESENDINFO.setCellData( 0 , "sApplySer" , wconv.substr(Div1_msk_sCrNo.getValue(),5,7));		
	//DS_SAVESENDINFO.setCellData( 0 , "nEndorseNo" , edt_nCrChngNo.getValue()  );	  // 계약변경번호

	//주소
	DS_SAVESENDINFO.setCellData( 0 , "sNewBizCode" , Div1_msk_sCrNo.getValue() );	
	DS_SAVESENDINFO.setCellData( 0 , "nAdrsChangeNo" , nChangeDMAddrNo );
	DS_SAVESENDINFO.setCellData( 0 , "nTelnoChngSeq" , nChangeDMTelNo );
	DS_SAVESENDINFO.setCellData( 0 , "sAdrsType" , sAddrFlgcd );	// 주소구분 	   

	DS_SAVESENDINFO.setCellData( 0 , "bottom_arg1" ,  Ds_LTIEA00.getCellData(0, "sEmpName") );
	DS_SAVESENDINFO.setCellData( 0 , "bottom_arg2" ,  Ds_LTIEA00.getCellData(0, "sFrstDoerCentrCdNm") );
	DS_SAVESENDINFO.setCellData( 0 , "bottom_arg3" ,  Ds_LTIEA00.getCellData(0, "sEmpTel") );	

	DS_SAVESENDINFO.setCellData( 0 , "sBottom_arg1" ,  Ds_LTIEA00.getCellData(0, "sEmpName") );
	DS_SAVESENDINFO.setCellData( 0 , "sBottom_arg2" ,  Ds_LTIEA00.getCellData(0, "sFrstDoerCentrCdNm") );
	DS_SAVESENDINFO.setCellData( 0 , "sBottom_arg3" ,  Ds_LTIEA00.getCellData(0, "sEmpTel") );

	return true;
};

//---------------------------------------------------------------------------
// tobe : scwin.lfn_ProvisionSearch
// asis : lfn_ProvisionSearch
// 내용 : 이메일 약관발송
//----------------------------------------------------------------------------
scwin.lfn_ProvisionSearch = function(sGdCd,sGnrzCd){

	wconv.clear(DsWs_LTIBA01);//리스트 내용삭제
	wconv.clear(Ds_LTIBA01);		

	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD010Task", "selProvisionList", 0, 0);
	MethodObj.setId("selProvisionList");
	MethodObj.setParam("sAttrFlagCd", "01");
	if(sGnrzCd=="02"){
		MethodObj.setParam("sAttrName",  "ClasKey2");
	}else{
		MethodObj.setParam("sAttrName",  "ClasKey");
	}
	MethodObj.setParam("ClasKey2",  sGdCd);	
	MethodObj.setUrl("/sales/CBSC103MController.do");
	MethodObj.setXPath("//*/LTIBA01");	
	MethodObj.sync();
	MethodObj.send();    

	console.log(JSON.stringify(_tranSync.result));
    util.interfaceKey(_tranSync.result.serviceDs);	
	DsWs_LTIBA01.setJSON (_tranSync.getDsJson("LTIBA01")); // dataset 데이터	

	if(warning.getRowCount() != 0){
	// 오류처리
		alert(warning.getCellData(0, "msg"));
		return;	
	}else{
		//Ds_LTIBA01.Copy("DsWs_LTIBA01");
		wconv.copy(Ds_LTIBA01, DsWs_LTIBA01);
	}
};


