//---------------------------------------------------------------------------
// tobe : scwin.chk_Post_OnClick
// asis : chk_Post_OnClick
// 내용 : 
//--------------------------------------------------------------------------
scwin.chk_Post_OnClick = function(obj,strValue){
	if(rdo_sDmcode == "LL02" && strValue == true) {
		chk_Email.setValue(1);
	}else if(rdo_sDmcode == "LL02" && strValue == false) {
		chk_Email.setValue(0);
	}
};

//---------------------------------------------------------------------------
// tobe : scwin.chk_Email_OnClick
// asis : chk_Email_OnClick
// 내용 : 
//--------------------------------------------------------------------------
scwin.chk_Email_OnClick = function(obj,strValue){
	if(rdo_sDmcode == "LL02" && strValue == true) {
		chk_Post.setValue(1);
	}else if(rdo_sDmcode == "LL02" && strValue == false) {
		chk_Post.setValue(0);
	}
};


