//구비서류안내 화면 오픈	
scwin.btn_CBSC100M_onclick = function() {
	return gfn_OpenLinkPage("CBSC100M"); 
};


scwin.chk_Post_onviewchange = function(info) {
	chk_Email.setValue(0);
	chk_lms.setValue(0);
	chk_Print.setValue(0);
	chk_Fax.setValue(0);
};

scwin.chk_Email_onviewchange = function(info) {
	chk_Post.setValue(0);	
	chk_lms.setValue(0);
	chk_Print.setValue(0);
	chk_Fax.setValue(0);
};

scwin.chk_lms_onviewchange = function(info) {
	chk_Post.setValue(0);
	chk_Email.setValue(0);	
	chk_Print.setValue(0);
	chk_Fax.setValue(0);
};

scwin.chk_Print_onviewchange = function(info) {
	chk_Post.setValue(0);
	chk_Email.setValue(0);	
	chk_Fax.setValue(0);
	chk_lms.setValue(0);
};

scwin.chk_Fax_onviewchange = function(info) {
	chk_Post.setValue(0);
	chk_Email.setValue(0);	
	chk_Print.setValue(0);
	chk_lms.setValue(0);

	if(gProdYN != "Y") {							
		//테스트 번호
		//edt_fax1.setValue("02");
		//edt_fax2.setValue("6670");
		//edt_fax3.setValue("8169"); // 10층 현업용
		//edt_fax1.setValue("02");
		//edt_fax2.setValue("6670");
		//edt_fax3.setValue("8079"); // 10층 현업용
	}	
};


