scwin.init = function() {
var pro = Promise.resolve();

window.top.util.winTopShowModalChk = "T";
util.winTopShowModal();



pro = pro.then(function(rv) {
    return util.winTopShowModalPromise();
});



pro = pro.then(function(rv) {

     //init 부분 함수 입력하는곳
    wprom.setPopupInit(696,209, "증명서신청");

	wprom.windowOpenInit(D191);
	var indata = $w.getParameter("param");
	try {
			fv_args = JSON.parse(indata);
			console.log(fv_args);

		    if (wconv.length(fv_args.sPlanNo) > 0) {
		        sPlanNo = fv_args.sPlanNo;
		    }
		    if (wconv.length(fv_args.sPsnGroupFlgcd) > 0) {
		        sPsnGroupFlgcd = fv_args.sPsnGroupFlgcd;
		    }
		    if (wconv.length(fv_args.sGdCd) > 0) {
		        sGdCd = fv_args.sGdCd;
		    }
		    if (wconv.length(fv_args.sGdCdNm) > 0) {
		        sGdCdName = fv_args.sGdCdNm;
		    }
		    if (wconv.length(fv_args.sPlanProgRankCd) > 0) {
		        sPlanProgRankCd = fv_args.sPlanProgRankCd;
		    }
		    if (wconv.length(fv_args.sInrpsName) > 0) {
		        sInrpsName = fv_args.sInrpsName;
		    }
		    if (wconv.length(fv_args.sRdchCrYn) > 0) {
		        sRdchCrYn = fv_args.sRdchCrYn;
		    }
		    if (wconv.length(fv_args.tab) > 0) {
		        tab = fv_args.tab;
		    }
		    if (wconv.length(fv_args.sCmprCrNotcSsrt) > 0) {
		        sCmprCrNotcSsrt = fv_args.sCmprCrNotcSsrt;
		    }
		    if (wconv.length(fv_args.sSimpleYn) > 0) {
		        sSimpleYn = fv_args.sSimpleYn;
		    }

	} catch(e) {
	};

    /** 링크 콤보 데이터를 가저온다. 
     반드시 링크 데이터셋 ID = Ds_LinkList, 링크콤보 ID = cmb_linkMenu !!!!!
     링크 버튼 ID = btn_linkMenu !!!!!*/
//    gfn_GetLinkList("D191");
    //버튼 활성,비활성화
//    gfn_ButtonEnable(true, false, false, false, false, false, false, false, true);
//    nfn_getCommonData(SOBJ_Common, DOBJ_Common, "Ds_Email", "1010", "cmb_Email");
//이메일 주소 띄우기--------------------------------------------
    if (Ds_Email.getRowCount() > 1) {
        var insRow = util.insertRow(Ds_Email, 0);
        /*수작업 변환 개발자 가이드(테스트 중)
        dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
        Ds_Email.setCellData(0, "sCodeId", "");
        Ds_Email.setCellData(0, "sCodeName", "선택");
        cmb_Email.setSelectedIndex( 0 ); //dataTab.tab1.Cmb_Email2.index=0;\
    }
	gds.gDs_Email_Create();
	cmb_Email.setNodeSet("data:gDs_Email","TEXT","CODE");
//---------------------------------------------------------------
    //lfn_getCommonCodes();
    //lfn_getGdList();
    cmb_GdList.setSelectedIndex(0);

    /*if(gProdYN == "Y") {			
    	edt_fax1.Text = "";
    	edt_fax2.Text = "";
    	edt_fax3.Text = "";			
    }else{
    	//테스트 번호
    	edt_fax1.Text = "02";
    	edt_fax2.Text = "6670";
    	edt_fax3.Text = "8600"; // 10층 현업용
    	if(gProdYN <> "Y" && (gUserID == "5100030" || gUserID == "5110006") ){
    		edt_fax3.Text = "8079"; //2층 IT용
    	}
    }*/
    // 청약쪽 연결 
    //cmd_SearchStd.Value = "2";
    //Div1.msk_sCrNo.Value = sPlanNo;		
    scwin.lfn_Search(); //  조회

    //init 함수를 종료하고자 할때 사용한다.
//    return Promise.reject("오류관련처리");
});


pro = pro.then(function(rv) {
    util.winTopHideModal();
});



pro = pro.catch(function(rj) {
    util.winTopHideModal();
    //alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
});



return pro;
};

