//            parentInfo += Ds_LTIEA00.getCellData(0, "sMailRvadAddrFlagCd") + "^"; //발송지회차		
//            parentInfo += "Ds_long_mPlr"; //데이터셋명 		
//            parentInfo += " screenId=long"; //스크린id 
			parentInfo.sCrtorCstId = Ds_LTIEA00.getCellData(0, "sCrtorCstId");
			parentInfo.sCrtorCd = Ds_LTIEA00.getCellData(0, "sCrtorCd");
			parentInfo.soonbun = "0";//순번?
			parentInfo.sCstFlgcd = Ds_LTIEA00.getCellData(0, "sCstFlgcd");	
			parentInfo.sAddrFlgcd = Ds_LTIEA00.getCellData(0, "sAddrFlgcd");
			parentInfo.nAddrChngSeq = Ds_LTIEA00.getCellData(0, "nAddrChngSeq");
			parentInfo.nTelnoChngSeq = Ds_LTIEA00.getCellData(0, "nTelnoChngSeq");
			parentInfo.sMailRvadAddrFlagCd = Ds_LTIEA00.getCellData(0, "sMailRvadAddrFlagCd"); //발송지주소구분
			parentInfo.sMailRvadAddrFlagCd = Ds_LTIEA00.getCellData(0, "sMailRvadAddrFlagCd"); //발송지회차
			parentInfo.Ds_long_mPlr = "Ds_long_mPlr";
			parentInfo.screenId = "long";
        } else {
//            parentInfo += "^"; //고객ID		
//            parentInfo += Ds_LTIEA00.getCellData(0, "sCrtorCd") + "^"; //주민번호		
//            parentInfo += "0^"; //순번		
//            parentInfo += "^"; //고객유형 내국인		
//            parentInfo += "^"; //주소구분코드 자택		
//            parentInfo += "^"; //주소변경회차		
//            parentInfo += "^"; //전화회차		
//            parentInfo += "^"; //발송지주소구분		
//            parentInfo += "^"; //발송지회차		
//            parentInfo += "Ds_long_mPlr"; //데이터셋명 		
//            parentInfo += " screenId=long"; //스크린id	
			parentInfo.sCrtorCstId = "";
			parentInfo.sCrtorCd = Ds_LTIEA00.getCellData(0, "sCrtorCd");
			parentInfo.soonbun = "0";//순번?
			parentInfo.sCstFlgcd = "";
			parentInfo.sAddrFlgcd = "";
			parentInfo.nAddrChngSeq = "";
			parentInfo.nTelnoChngSeq = "";
			parentInfo.sMailRvadAddrFlagCd = "";
			parentInfo.sMailRvadAddrFlagCd ="";
			parentInfo.Ds_long_mPlr = "Ds_long_mPlr";
			parentInfo.screenId = "long";	
        }
		var popInfo = {
			"width":820,
			"height":612,
			"indata":parentInfo,
			"callback"  : scwin.popupCallback,
			"title"     : "주소등록"
		 };

        //trace(Ds_long_mPlr.SaveXml());
        //고객화면 오픈  
        //대면/비대면체크  Y :  대면 , N: 비대면	
        // (gFaceSalesYN = "N")  //  비대면 
//        if (gds.gFaceSalesYN == "N") {
        if (util.getGdsValue("gFaceSalesYN") == "N") {
        	promise = promise.then(function(rtnVal) {
//                return wprom.promise.popupOpen("PROJECT_PATH_TEMP/cst_cst/CBSO002P.xml", "parentInfo=" + parentInfo /*wprom.popupOpen(가이드참조)*/ , 820, 612, "TaskBar=true Resize=true");
				return wprom.popupOpen("/ui/cbsc/CBSO002P.xml", popInfo);//상품
			});
        } else {
            promise5 = promise5.then(function(rtnVal) {
//                return wprom.promise.popupOpen("PROJECT_PATH_TEMP/cst_cst/CBSO002P.xml", "parentInfo=" + parentInfo /*wprom.popupOpen(가이드참조)*/ , 820, 612, "TaskBar=true Resize=true");
				return wprom.popupOpen("/ui/cbsc/CBSO002P.xml", popInfo);//상품
            });
        }
        promise = promise.then(function(rtnVal) {
            //trace(Ds_long_mPlr.SaveXml());
            var nChIndex = pos(Ds_long_mPlr.getCellData(0, "sEmail1"), "@");
            if (wconv.length(Ds_long_mPlr.getCellData(0, "nAdrsChangeNo")) > 0) {
                if (chk_Email.Enable == true) {
                    if (nChIndex > 0) {
                        //이메일
//                        edt_sEmail.setValue(wconv.mid(Ds_long_mPlr.getCellData(0, "sEmail1"), 0, nChIndex));
//                        edt_sEmail1.setValue(wconv.mid(Ds_long_mPlr.getCellData(0, "sEmail1"), nChIndex + 1, wconv.length(Ds_long_mPlr.getCellData(0, "sEmail1"))));
						edt_sEmail.setValue(wconv.substr(Ds_long_mPlr.getCellData(0, "sEmail1"), 0, nChIndex));
                        edt_sEmail1.setValue(wconv.substr(Ds_long_mPlr.getCellData(0, "sEmail1"), nChIndex + 1, wconv.length(Ds_long_mPlr.getCellData(0, "sEmail1"))));
                    } else {
                        edt_sEmail.setValue("");
                        edt_sEmail1.setValue("");
                    }
                }
                if (chk_Fax.Enable == true) {
                    if (gds.gProdYN == "Y") {
                        edt_fax1.setValue(Ds_long_mPlr.getCellData(0, "sFax1"));
                        edt_fax2.setValue(Ds_long_mPlr.getCellData(0, "sFax2"));
                        edt_fax3.setValue(Ds_long_mPlr.getCellData(0, "sFax3"));
                    } else {}
                }
                nChangeDMTelNo = Ds_long_mPlr.getCellData(0, "nTelChangeNo");
            }
        });
    }
    return promise;
};

// 이메일 주소 선택시 데이터 셋팅.
scwin.lfn_eMail_OnChanged = function() {

    eMailDomainAppend(edt_sEmail1, cmb_Email);

//    if (wconv.mid(cmb_Email.getValue(), 0, 1) == "@") {
//    edt_sEmail1.setValue(wconv.mid(cmb_Email.getValue(), 1, wconv.length(cmb_Email.getValue())));
//    }
// 사용안함이 이전 값에 붙어서 나오게 되어 수정
//wconv.mid(cmb_Email.getValue(), 0, 1) == "@"
    if (wconv.substr(cmb_Email.getValue(), 0, 1) == "@") {
//    edt_sEmail1.setValue(wconv.mid(cmb_Email.getValue(), 1, wconv.length(cmb_Email.getValue())));
    edt_sEmail1.setValue(wconv.substr(cmb_Email.getValue(), 1, wconv.length(cmb_Email.getValue())));
    edt_sEmail1.setDisabled(false);
    edt_sEmail.setDisabled(false);
    }else{
    edt_sEmail1.setValue(cmb_Email.getValue());
    edt_sEmail1.setDisabled(true);
    edt_sEmail.setDisabled(true);
    }
};

