		if ( ds_Save_chk.getCellData( 0 , "faxFlag" ) == "1" ){	
			message += "팩스발송이 요청되었습니다.\n";
        }else {
            message += "팩스발송중 오류가 발생 했습니다.\n";
        }
	}	
	if ( chk_Email.getValue() == "1" ){
		if ( ds_Save_chk.getCellData( 0 , "mailFlag" ) == "1" ){		
			message += "메일발송이 요청되었습니다.";
        }else {
            message += "메일발송중 오류가 발생 했습니다.";
        }		
	}	
/*	
	if ( chk_lms.getValue() == "1" ){
		if ( ds_Save_chk.getCellData( 0 , "mailFlag" ) == "1" ){		
			message += "알림톡발송이 요청되었습니다.";
        }else {
            message += "알림톡발송중 오류가 발생 했습니다.";
        }		
	}
*/
	alert(message);	
};

//---------------------------------------------------------------------------
// function 명 : Div_common_OnChanged
// 내용        : Div  안에 모든 Radio  버튼 공?으로 제어.
//---------------------------------------------------------------------------
scwin.Div_common_OnChanged = function(obj, strCode, strText, nOldIndex, nNewIndex) {
    rdo_sDmcode = obj.CodeColumn;

    // 우편발송 , 이메일 , 팩스 제어.
    OnPrintControl();
    Static5.Visible = true;
    cmb_GdList.hide();
};

//  발송 버튼 이벤트시 유효성검사.
scwin.checkValid = function() {
    if (rdo_sDmcode == "L001" && cmb_GdList.getSelectedIndex() == 0) {
        wprom.alert("상품을 선택하십시오.");
        //cmb_GdList.SetFocus();
        return false;
    }
    if (chk_Email.getValue() != "1" && chk_Fax.getValue() != "1") {
        wprom.alert("발송 종류를 선택하십시오.");
        return false;
    }
    if (chk_Email.getValue() == "1") {
        if (wconv.length(edt_sEmail.getValue()) == 0) {
            wprom.alert("Email 주소를 입력하십시오.");
            return false;
        }
        if (wconv.length(edt_sEmail1.getValue()) == 0) {
            wprom.alert("Email 주소를 선택 하십시오.");
            return false;
        }
    }
    if (chk_Fax.getValue() == "1") {
        if (wconv.length(edt_fax1.getValue()) == 0) {
            wprom.alert("Fax 정보를 정확히 입력하십시오.");
            return false;
        }
        if (wconv.length(edt_fax2.getValue()) == 0) {
            wprom.alert("Fax 정보를 정확히 입력하십시오.");
            return false;
        }
        if (wconv.length(edt_fax3.getValue()) == 0) {
            wprom.alert("Fax 정보를 정확히 입력하십시오.");
            return false;
        }
    }
    return true;
};

