var pageSize = "10"; 
var pageCount = "10"; 
var nPageNo = 0;    // 페이지수 =>  // 추가(async로 수정: 페이지수 받아오기): 2017.12.21, by 진태만 
var sToday = ""; 
var n30006Auth;		// 전역변수 
var n30007Auth;	// 기존실장(조회만 허용) 
var n22040Auth;	// 기존슈바(조회만 허용) 
//var n200190Auth; //신채널실무(장기인수)
var sAgncYn = "N";		// 대리점 여부
var sAuthByflag = "";	// 장기업무 권한 일때 성명 마스킹 예외 처리 2014.03.06 N1403-00033(장기보험 화면 개인정보 처리 요청) 
var openFlag; 
var slevel = ""; 
var medt_Str_Event;	// medt_Str(InputBox) MaskEdit 정규식 입력
var sDs_SqrtListAllCnt = "";    // Grid 전체 조회수

//----------------------------------------------------------------------------------------------------
// function 명 : init
// 내용        : 화면 로딩 처리
//----------------------------------------------------------------------------------------------------
scwin.init = function() {
	console.log("[/CBSC111M.xml] [init()] ==> [화면 로딩] [TEST_01]" );  

	edt_empCode.hide();		// 사원코드 숨기기 설정 
	btn_Doer1.hide();			// 조회 기준(사원찾기, 조직 찾기) 팝업 버튼 숨기기 설정

	// medt_Str(InputBox) MaskEdit 정규식 입력  
	var maskRegExp = [/^[0-9\s]{7}[A-Z0-9\s]{6}$/			// 01. 피보험자코드 이면("######-#ZZZZZZ") : 13자리
								, /^[0-9\s]{7}[A-Z0-9\s]{6}$/			// 02. 계약자코드 이면("######-#ZZZZZZ")  : 13자리
								, /^[A-Z\s]{1}[0-9\s]{11}$/				// 03. 청약번호 이면( "M-####-#######") : 12자리
								, /^[A-Z0-9\s]{7}$/							// 04. 취급자코드 이면"@@@@@@@") : 7자리
								, /^[A-Z0-9\s]{7}$/							// 05. 사용인 이면"@@@@@@@") : 7자리
								, /^[A-Z0-9\s]{7}$/							// 06. 조직코드 이면"@@@@@@@") : 7자리
							 	, /^[A-Z\s]{1}[0-9\s]{11}$/]; 			// 07. 청약번호 이면 : 12자리
	var maxLength  = [13, 13, 12, 7, 7, 7, 12];
	var chkIndex   = 0;
	medt_Str_Event = new wMaskEditObj(medt_Str, maskRegExp, maxLength, chkIndex);

	//대리점인지 여부(30:대리점,33:사용인,35:TM사용인) 
    if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35") {
		sAgncYn = "Y";	// 대리점 여부
 	}

   	// 공통 버튼 권한 설정 - |1.입력|2.수정|3.삭제|4.결재|5.취소|6.인쇄|7.엑셀   총 7개
	gfn_btnEnable(false, false, false, false, false, false, false);
	sToday = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 		// global 현재일자

   // TM실 운영권한
 	n30006Auth = nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "30006"); 

   // TM실 관리권한
 	n30007Auth = nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "30007");

 	// 장기보험 인수권한
	n22040Auth = nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "22040");

	// 신채널실무(장기인수)
   // n200190Auth = nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "200190");
    // console.log("[/CBSC111M.xml] [init()] ==> [화면 로딩] [TEST_06]" );

    //2014.03.06 N1403-00033(장기보험 화면 개인정보 처리 요청)
    sAuthByflag = nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "5000014");		// 권한 체크(TRUE: 권한 있음, FALSE 권한 없음)

	// 공통 콤보 세팅
    scwin.lfn_commCodeSetting();

	scwin.lfn_init();  //초기화

     if (sAgncYn == "Y") {      // 대리점 이면
         grd_List.SetCellProp("body", 2, "mask", "");
         grd_List.SetCellProp("body", 4, "mask", "");
         grd_List.SetCellProp("body", 2, "Expr", "lfn_InrpsCd_display(sCrtorCd)");
         grd_List.SetCellProp("body", 4, "Expr", "lfn_InrpsCd_display(sInrpsCd)");
     } 
};

//----------------------------------------------------------------------------------------------------
// function 명 : lfn_commCodeSetting
// 내용        : 공통코드 조회
//----------------------------------------------------------------------------------------------------
scwin.lfn_commCodeSetting = function() {
	// console.log("[/CBSC111M.xml] [lfn_commCodeSetting()] ==> [공통코드 조회] [TEST_01]" );  

	var dataSets = "Ds_JugmAuthDept";   
	var dataSets = "DsWs_JugmAuthDept"; 
    var columns = "JugmAuthDept";
    var objects = "cmd_JugmAuthDept ";			// 심사권한부서 콤보(cmd_JugmAuthDept)
    var titleTypes = "A"; //A:전체, S:선택, N:안넣기
   	// console.log("[/CBSC111M.xml] [lfn_commCodeSetting()] ==> [공통코드 조회] [TEST_03]" );  

    gfn_getCommonCodes(dataSets, columns, objects, titleTypes);
};

//----------------------------------------------------------------------------------------------------
// function 명 : lfn_init
// 내용        : 초기화
//----------------------------------------------------------------------------------------------------
scwin.lfn_init = function() {
	//console.log("[/CBSC111M.xml] [lfn_init()] ==> [일정관리 화면 초기화] [TEST_01]" );  

	//console.log("\n\n 		[/CBSC111M.xml] [lfn_init()] ==> [01. 심의상태 콤보 조회] [TEST_01] #############] =====> ");  

	//var gDoer = util.getGdsValue("gDoer"); 	  //취급자 

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB160Task", "setCommon");  	// 01. 심의상태 콤보 조회 Obj 
	MethodObj.setId("setCommon");		// ID 셋팅
	MethodObj.setParam("sAgentCode", util.getGdsValue("gDoer"));			// 취급자 
	MethodObj.setUrl("/contract/CBSC111MController.do");  // URL 설정 
	MethodObj.setXPath("//hashtable/data[@hashkey='com_ConuResltCd']/vector/data/com_ConuResltCd");		// XPath
	MethodObj.addXPath("//hashtable/data[@hashkey='aDMAC03Doc']/ADMAC03");	// XPath  
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	// console.log("[/CBSC111M.xml] [lfn_init()] ==> [01. 심의상태 콤보 조회_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

 	Ds_ConuResltCd.setJSON(_tranSync.getDsJson("com_ConuResltCd"));		// DS 셋팅(조회 결과)  
	DsWs_Warning.setJSON(_tranSync.getDsJson("WARNING"));		// DS 셋팅(조회 결과)  

	var insRow = Ds_ConuResltCd.insertRow(0);
	Ds_ConuResltCd.setCellData(0, "sCd", "");
	Ds_ConuResltCd.setCellData(0,  "sHnglCdName", "- 전체 -");   
	cmb_ConuResltCd.setSelectedIndex(0); 		// 심의상태 콤보 셋팅(0: 신사업추진 실무)
 	// console.log("[/CBSC111M.xml] [lfn_init()] ==> [DS_갯수_3]"+ Ds_ConuResltCd.getRowCount() +"[Ds_ConuResltCd]"+ WebSquare.xml.serialize(Ds_ConuResltCd.getAllXML()) ); 

    //2015.06.02(N1506-00035) 조회시작일자 당일로 수정
    cal_From.setValue(sToday);  
    cal_To.setValue(sToday);

	// console.log("[/CBSC111M.xml] [lfn_init()] ==> [01. 심의상태 콤보 조회_완료] [TEST_end]");   
};

//----------------------------------------------------------------------------------------------------
// function 명 : cmb_Gubun_OnClick()
// 내용        : 조회기준 콤보값 변경시 처리
//----------------------------------------------------------------------------------------------------
scwin.cmb_Gubun_OnCharChanged = function() { 
	//console.log("[/CBSC111M.xml] [cmb_Gubun_OnCharChanged()] ==> [조회기준 콤보값 변경 이벤트] [TEST_01]" );  

    medt_Str.show();
    medt_Str.setDisabled(false);
    medt_Str.setValue(""); 
	btn_Doer1.hide();
	// console.log("[/CBSC111M.xml] [cmb_Gubun_OnCharChanged()] ==> [TEST_03] [cmb_Gubun.getValue()]"+ cmb_Gubun.getValue() );   

	switch (cmb_Gubun.getValue()) {
        case "1":	// 피보험자코드 이면	######-#ZZZZZZ
        	edt_Str.hide();
			medt_Str.show();    
			medt_Str_Event.setIndex(0);  	// medt_Str(InputBox) MaskEdit 정규식 입력 0번째(01. 피보험자코드)
			medt_Str.setPlaceholder("______-_______");		// Placeholder 설정   
            medt_Str.setDisplayFormat("######-#######"); 
			//console.log("[/CBSC111M.xml] [cmb_Gubun_OnCharChanged()] ==> [조회기준 콤보값 변경 이벤트] [TEST_03]" );    
            break;

        case "3":	// 계약자코드 이면
            edt_Str.hide();
            medt_Str.show();  
            medt_Str_Event.setIndex(1);  	// medt_Str(InputBox) MaskEdit 정규식 입력 2번째(02. 계약자코드)
			medt_Str.setPlaceholder("______-_______");		// Placeholder 설정   
            medt_Str.setDisplayFormat("######-#######"); 
            break;

        case "6":	// 청약번호 이면
            edt_Str.hide();
            medt_Str.show(); 
            medt_Str_Event.setIndex(2);  	// medt_Str(InputBox) MaskEdit 정규식 입력 0번째(03. 청약번호)     	 
         	medt_Str.setPlaceholder("_-____-_______");		// Placeholder 설정
       		medt_Str.setDisplayFormat("#-####-#######");   //	M-____-_______": 1-4-7
            break;

        case "7":	// 취급자코드 이면
          	btn_Doer1.show();
            edt_Str.hide();
            medt_Str.show();
            medt_Str.focus();		// focus 설정
        	medt_Str_Event.setIndex(3);  	// medt_Str(InputBox) MaskEdit 정규식 입력 0번째(04. 취급자코드)     	 
         	medt_Str.setPlaceholder("_______");		// Placeholder 설정
        	medt_Str.setDisplayFormat("#######");

         	var sAuthFlag = getAuthFlag200196();  // 권한코드(200196)에 대한 권한 여부 조회 처리 결과값 받기 
            if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35" || util.getGdsValue("gUserDuty") == "48" 
            	|| (util.getGdsValue("gUserDuty") == "49" && sAuthFlag == "N") || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" 
            	|| util.getGdsValue("gUserDuty") == "65" || util.getGdsValue("gUserDuty") == "64" || util.getGdsValue("gUserDuty") == "66") {	   //대리점인지
                medt_Str.setValue(util.getGdsValue("gDoer"));
                medt_Str.setDisabled(true);
            } 
            break; 

        case "8":		// 사용인 이면
            edt_Str.hide();
            medt_Str.show();
          	medt_Str_Event.setIndex(4);  	// medt_Str(InputBox) MaskEdit 정규식 입력 0번째(05. 사용인)     	 
         	medt_Str.setPlaceholder("_______");		// Placeholder 설정
         	medt_Str.setDisplayFormat("#######");
            break;

        case "10":		// 조직코드 이면 
       		btn_Doer1.show();
            edt_Str.hide();
            medt_Str.show();
        	medt_Str_Event.setIndex(5);  	// medt_Str(InputBox) MaskEdit 정규식 입력 0번째(06. 조직코드)     	 
         	medt_Str.setPlaceholder("_______");		// Placeholder 설정
         	medt_Str.setDisplayFormat("#######");
            // console.log("[/CBSC111M.xml] [cmb_Gubun_OnCharChanged()] ==> [조회기준 콤보값 변경 이벤트] [10. 조직코드 이면_02]" );    
            break;

        default:
            edt_Str.setValue("");
            edt_Str.show();
            medt_Str.hide();
            break;
    }

    medt_Str.focus();
    edt_Str.focus();
}; 

//----------------------------------------------------------------------------------------------------
// function 명 : getJobCode()
// 내용        : 로그인 ID에 해당하는 잡코드 조회 처리
//---------------------------------------------------------------------------------------------------- 
scwin.getJobCode = function() { 

	//console.log("\n\n 		[/CBSC111M.xml] [getJobCode()] ==> [01. 로그인 ID에 해당하는 잡코드 조회] [TEST_01] #############] =====> ");  

	wconv.clear(Ds_JobCode);

	var MethodObj = new callService("com.inswave.longterm.dpt.com.task.FAF000Task", "getEmpName");  	// 01. 로그인 ID에 해당하는 잡코드  조회 Obj 
	MethodObj.setId("getEmpName");		// ID 셋팅 
	MethodObj.setParam("sEmpNo", util.getGdsValue("gUserID"));			// 조직구조 
	MethodObj.setUrl("/contract/CBSC111MController.do");  // URL 설정  
	MethodObj.addXPath("//*/ADMAB01");	// XPath
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	// console.log("[/CBSC111M.xml] [getJobCode()] ==> [01. 로그인 ID에 해당하는 팀코드 조회_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display    -> dsTest.setJSON(e.serviceDs.data[0].ADMAC01);

 	Ds_JobCode.setJSON(_tranSync.getDsJson("ADMAB01"));		// DS 셋팅(조회 결과)  
	DsWs_Warning.setJSON(_tranSync.getDsJson("WARNING"));		// DS 셋팅(조회 결과)
	// console.log("[/CBSC111M.xml] [getJobCode()] ==> [DS_갯수]"+ Ds_JobCode.getRowCount() +"[Ds_JobCode]"+ WebSquare.xml.serialize(Ds_JobCode.getAllXML()) ); 

    var sJobCode = Ds_JobCode.getCellData(0, "sJobCode");		// 잡코드
	// console.log("[/CBSC111M.xml] [getJobCode()] ==>  [TEST_end] [sJobCode]"+ sJobCode );  

	return sJobCode;
};

//----------------------------------------------------------------------------------------------------
// function 명 : getTeamCd()
// 내용        : 로그인 ID에 해당하는 팀코드 조회 처리
//---------------------------------------------------------------------------------------------------- 
scwin.getTeamCd = function() { 

	//console.log("\n\n 		[/CBSC111M.xml] [getTeamCd()] ==> [01. 로그인 ID에 해당하는 팀코드 조회] [TEST_01] #############] =====> ");  

	wconv.clear(Ds_Team);

	var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR930Task", "getAgentList");  	// 01. 로그인 ID에 해당하는 팀코드 조회 Obj 
	MethodObj.setId("getAgentList");		// ID 셋팅 
	MethodObj.setParam("sAgentCode", util.getGdsValue("gUserID"));			// 조직구조 
	MethodObj.setUrl("/contract/CBSC111MController.do");  // URL 설정  
	MethodObj.addXPath("//*/ADMAC03");	// XPath
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	// console.log("[/CBSC111M.xml] [getTeamCd()] ==> [01. 로그인 ID에 해당하는 팀코드 조회_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

 	Ds_Team.setJSON(_tranSync.getDsJson("ADMAC03"));		// DS 셋팅(조회 결과)  
	DsWs_Warning.setJSON(_tranSync.getDsJson("WARNING"));		// DS 셋팅(조회 결과)  
	// console.log("[/CBSC111M.xml] [getTeamCd()] ==> [DS_갯수_3]"+ Ds_Team.getRowCount() +"[Ds_Team]"+ WebSquare.xml.serialize(Ds_Team.getAllXML()) ); 

    var sTeamCd = Ds_Team.getCellData(0, "sTeamCode");		// 팀코드
	//console.log("[/CBSC111M.xml] [getTeamCd()] ==>  [TEST_end] [sTeamCd]"+ sTeamCd );  

	return sTeamCd;
};      

//----------------------------------------------------------------------------------------------------
// function 명 : getAuthFlag200196()
// 내용        : 권한코드(200196)에 대한 권한 여부 조회 처리
//----------------------------------------------------------------------------------------------------
scwin.getAuthFlag200196 = function() { 
	//console.log("\n\n 		[/CBSC111M.xml] [getAuthFlag200196()] ==> [01. 조직구조 조회] [TEST_01] #############] =====> ");  

	wconv.clear(Ds_TMAuth);

	var MethodObj = new callService("com.inswave.longterm.pay.pdg.task.GAG021Task", "selectForAuthCheck");  	// 01. 권한코드(200196)에 대한 권한 여부 조회 Obj 
	MethodObj.setId("selectForAuthCheck");		// ID 셋팅
	MethodObj.setParam("nAuthType", "1");	
	MethodObj.setParam("nAuthID",  "200196");	 	// 권한ID
	MethodObj.setParam("sUserID", util.getGdsValue("gUserID"));			// 취급자 
	MethodObj.setUrl("/contract/CBSC111MController.do");  // URL 설정  
	MethodObj.addXPath("//*/GAG021");	// XPath
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	// console.log("[/CBSC111M.xml] [getAuthFlag200196()] ==> [01. 조직구조 조회_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

 	Ds_TMAuth.setJSON(_tranSync.getDsJson("GAG021"));		// DS 셋팅(조회 결과)  
	DsWs_Warning.setJSON(_tranSync.getDsJson("WARNING"));		// DS 셋팅(조회 결과)  

    var sAuthFlag = Ds_TMAuth.getCellData(0, "sAuthFlag");	// 권한여부
	//console.log("[/CBSC111M.xml] [getAuthFlag200196()] ==> [TEST_92] [sAuthFlag]"+ sAuthFlag );   

    return sAuthFlag;
};     

//----------------------------------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼 클릭시 처리
//----------------------------------------------------------------------------------------------------
scwin.btn_search_onclick = function() {
	console.log("[/CBSC111M.xml] [btn_search_onclick()] ==> [조회버튼 클릭시 처리] [TEST_01]" );

	// 특정그룹 설계금지
    if (gfn_isCA(util.getGdsValue("gUserID"), util.getGdsValue("gUserDuty"))) {
        wprom.alert("현재 상담그룹은 청약 심사대상건 조회가 불가합니다.");
        return;
    }

   // 2015.06.02(N1506-00035) 조회기간 7일->30일로 수정
    //if (gfn_betweenDays(cal_From.getValue(), cal_To.getValue()) > 30) {
    if (WebSquare.date.dateDiff(cal_From.getValue(), cal_To.getValue()) > 30 ) { 
        wprom.alert("조회기준일을 30일이내로 지정하셔야 합니다");
        return false;
    }

    wconv.clear(Ds_SqrtList);
    wconv.clear(Ds_SqrtListCnt);

	scwin.Search(1);		// 조회버튼 클릭시 처리(처음 조회시)

  	// 전체 건수 얻어오기(Paging 처리)
	//gfn_MakePagingData(Ds_Page, Ds_SqrtListCnt.getCellData(0, "nCount"), pageSize, pageCount); 	// 수정(주석 처리: async는 nCount 못 읽어 옴): 2017.12.21, by 진태만

    //Cmb_Page.setValue("1"); //----- End  	// 수정(주석 처리: async로 수정): 2017.12.21, by 진태만
};

//----------------------------------------------------------------------------------------------------
// function 명 : Search
// 내용        : 조회 버튼 클릭시 처리(처음 조회 버튼, 페이징 버튼 클릭 조회시)
//----------------------------------------------------------------------------------------------------
scwin.Search = function(pageNo) {  
	//console.log("[/CBSC111M.xml] [Search()] ==> [TEST_01] [pageNo]"+ pageNo );   

    if (medt_Str.getValue() != "") {   // 조회기준 text가 medt_Str 이면
        sStr = medt_Str.getValue();
    } else {   // 조회기준 text가 edt_Str 이면
        sStr = edt_Str.getValue();
    } 

 	nPageNo = pageNo;    // 페이지수 =>  // 추가(async로 수정: 페이지수 받아오기): 2017.12.21, by 진태만

    if (cmb_Gubun.getValue() != "99") { 
        if(util.isNull(sStr)) {
			wprom.alert("조회조건을 입력하세요");
			return false;
        }
    }

     if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35" || util.getGdsValue("gUserDuty") == "48" 
       	|| (util.getGdsValue("gUserDuty") == "49" && sAuthFlag == "N") || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" 
       	|| util.getGdsValue("gUserDuty") == "65" || util.getGdsValue("gUserDuty") == "64" || util.getGdsValue("gUserDuty") == "66") {	   // 대리점인지  
        if (cmb_Gubun.getValue() == "7" || cmb_Gubun.getValue() == "8") {		// 조회기준이 '취급자' 또는' 사용인' 이면
        	 if (gfn_chekDoerInqrAuth("DsWs_chekCrInqrAuth", sStr) == false) {	        // 취급자 조회시 권한 체크 
                wprom.alert("조회 권한이 없습니다.");
                return;
            }
        } 

        // 청약번호 조회 권한 체크
        if (cmb_Gubun.getValue() == "6") {	// 조회기준이 '청약번호' 이면
            if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "1", sStr) == false) {		// 청약번호 권한 체크  
                wprom.alert("조회 권한이 없습니다.");
                return;
            }
        }
    } 

 	var sTeamCd = scwin.getTeamCd();  	// 로그인 ID에 해당하는 팀코드 조회 처리 결과값 받기  
 	var sJobCode = scwin.getJobCode();  	// 로그인 ID에 해당하는 잡코드 조회 처리 결과값 받기  
    var sAuthFlag = scwin.getAuthFlag200196();  	// 권한코드(200196)에 대한 권한 여부 조회 처리 결과값 받기

	//console.log("\n\n 		[/CBSC111M.xml] [lfn_SearchData()] ==> [M. 청약심사대상조회] [TEST_01] #############] =====> ");  

	var sGroupFlag = "0";
 	if(util.isNull(chk_Group.getValue())) { 
 	} else {
 		sGroupFlag = "1";		// 개인단체구분
 	} 

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB160Task", "getRqstList",  pageSize, pageNo);	// 청약심사대상조회 Obj
 	MethodObj.setId("getRqstList");	// ID 셋팅  
	MethodObj.setParam("gubun", cmb_Gubun.getValue());	// 조회기간  콤보
	MethodObj.setParam("JugmAuthDept",	cmd_JugmAuthDept.getValue());		// 심사권한부서
	MethodObj.setParam("sGroupFlag",	sGroupFlag);		// 개인단체구분
	MethodObj.setParam("sStr",	sStr);		// 조회기준
	MethodObj.setParam("sRqstStartDate", cal_From.getValue());	// 조회기간  
	MethodObj.setParam("sRqstEndDate",	cal_To.getValue());	 
	MethodObj.setParam("sConuResltCd",	cmb_ConuResltCd.getValue());		// 심의상태(품의결과코드)
	MethodObj.setParam("sGdCd",	 medt_sGdCd.getValue())		// 상품코드
	MethodObj.setParam("slevel",	slevel);

	// console.log("[/CBSC111M.xml] [lfn_init()] ==> [TEST_02] [심의요청부서]"+ edt_deptName.getValue() );   
	if (edt_deptName.getValue() != "") {		// 심의요청부서명이 존재하면
		MethodObj.setParam("level",	edt_level.getValue());	// 부서레벨
		MethodObj.setParam("deptCd", edt_deptCd.getValue());	// 심의요청부서
		// console.log("[/CBSC111M.xml] [lfn_init()] ==> [TEST_03] [edt_level]"+ edt_level.getValue() +"[edt_deptCd]"+ edt_deptCd.getValue()  );   
	}

	if (edt_empName.getValue() != "") {	// 승인권자가 존재하면
		MethodObj.setParam("sAppmn", edt_empCode.getValue());		//승인자코드 
	} 

	if(util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35" || 
	   util.getGdsValue("gUserDuty") == "48" || util.getGdsValue("gUserDuty") == "49" || util.getGdsValue("gUserDuty") == "50" 
	   || util.getGdsValue("gUserDuty") == "62" || util.getGdsValue("gUserDuty") == "65" || util.getGdsValue("gUserDuty") == "64" 
	   || util.getGdsValue("gUserDuty") == "66") {	//대리점인지 여부(30:대리점,33:사용인,35:TM사용인)	//설계자 여부 (48:DPM, 49:DP, 50:재정전문위원)

		//대리점 관리자
		if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "21013")) { 
			MethodObj.setParam("sKeyDoer", util.getGdsValue("gDoer")); 
		}
		//DPM권한 
		else if(nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "34009") && (sAuthFlag == "Y")){ 
			MethodObj.setParam("sFrstDoerTeamCd", sTeamCd);
		}
		//DPM권한
		else if(nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "34009")){ 
			MethodObj.setParam("sKeyDoer", "");
		}	
		//DP권한
		else if(nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "34011")){ 
			MethodObj.setParam("sKeyDoer", util.getGdsValue("gDoer"));
		}	
		//재정전문위원권한
		else if(nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "34030") && (sJobCode == "200205")){ 
			MethodObj.setParam("sKeyDoer", util.getGdsValue("gDoer"));  
		}
		//재정전문위원권한
		else if(nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "34030")){ 
			MethodObj.setParam("sKeyDoer", util.getGdsValue("gDoer")); 
		}
		//사용인
		else{ 
			gfn_SetParam(MethodObj, "sKeyDoer", util.getGdsValue("gDoer")); 
		}
		//대면총무
		if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID") , "34024")) { 
			MethodObj.setParam("sFrstDoerTeamCd", sTeamCd);
		}
	}	

	if(chk_Emergency.getValue() == "1") { 		// 긴급심사여부  
		MethodObj.setParam("sEmergencyYn", "Y");
	} else { 
		MethodObj.setParam("sEmergencyYn", "N"); 
	}

	MethodObj.setUrl("/contract/CBSC111MController.do");  // 서버단 호출 URL   
 	MethodObj.MsgShow();							// PROGRASSBAR 표시 
	MethodObj.setMsg("조회 중");    		 		// 동기, 비동기시 PROGRASSBAR에 표시할 메시지 셋팅  
	MethodObj.setXPath("//*/LTIDA00");			// XPath
	MethodObj.addXPath("//*/LTIDA00CNT");		// XPath 
	MethodObj.addXPath("//*/WARNING");		// XPath
	MethodObj.callBack("scwin.subCallBackfunction");		// Callback 처리 ==> 추가(비동기 호출: callBack 처리): 2017.12.21, by 진태만(sync 일 경우: 사용 안함)
	MethodObj.send();		// 최종 전송 

    //고객정보 조회 이력쌓기
	gfn_LogCreditInfoVt(Ds_SqrtList, "", "S", "sInrpsCd"); 		// 피보험자코드
	gfn_LogCreditInfoVt(Ds_SqrtList, "", "S", "sCrtorCd"); 		// 계약자코드	 
};

//----------------------------------------------------------------------------------------------------
// function 명 : subCallBackfunction
// 내용        : Callback 처리
//----------------------------------------------------------------------------------------------------
scwin.subCallBackfunction = function(e) {
	Ds_SqrtList.setJSON(_tranSync.getDsJson("LTIDA00"));		// 조회 DS 셋팅(조회 결과) 
	Ds_SqrtListCnt.setJSON(_tranSync.getDsJson("LTIDA00CNT")); 			// 조회 전체 건수 DS 셋팅(조회 결과)  
 	Ds_Warning.setJSON(_tranSync.getDsJson("WARNING"));  	// Warning DS 셋팅(조회 결과)    
	//console.log("[/CBSC111M.xml] [subCallBackfunction()] ==> [I/F 조회 완료] [Ds_SqrtList, 총갯수]"+ Ds_SqrtList.getTotalRow() +"[Ds_SqrtList]"+ WebSquare.xml.serialize(Ds_SqrtList.getAllXML()) ); 
	//console.log("[/CBSC111M.xml] [subCallBackfunction()] ==> [I/F 조회 완료] [Ds_SqrtListCnt, 총갯수]"+ Ds_SqrtListCnt.getTotalRow() +"[Ds_SqrtListCnt]"+ WebSquare.xml.serialize(Ds_SqrtListCnt.getAllXML()) ); 

	if (Ds_Warning.getRowCount() != 0) {
		wprom.alert(Ds_Warning.getCellData(0, "msg"));
       	return;
    }

    if (Ds_SqrtList.getRowCount() < 1) { 
        wprom.alert("청약심의대상건이 없습니다.");
    }	 

     if(nPageNo == 1) { 		// Page No가 1 이면  	 ==> // 추가(async로 수정: Page No가 1 이면): 2017.12.21, by 진태만
		 // 전체 건수 얻어오기(Paging 처리)
		gfn_MakePagingData(Ds_Page, Ds_SqrtListCnt.getCellData(0, "nCount"), pageSize, pageCount);		// 추가(async로 수정: Grid 전체 조회수(nCount) 받아옴): 2017.12.21, by 진태만
		Cmb_Page.setValue("1");		 //----- End   		// 추가(async로 수정): 2017.12.21, by 진태만
	}
};

//----------------------------------------------------------------------------------------------------
// function 명 : grd_List_OnCellClick
// 내용        : Grid 클릭 이벤트
//----------------------------------------------------------------------------------------------------
scwin.grd_List_OnCellClick = function(row, col) {
	//console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [Grid 클릭 이벤트] [TEST_01] [col]"+ col +"[grd_List.getColumnID(col)]"+ grd_List.getColumnID(col) ); 

	//2015.01.23 (N1501-00121)
    if (gfn_chekAcesCntrAuth("DsWs_chekCrInqrAuth", "1", Ds_SqrtList.getCellData(row, "sPlanNo")) == false) {
        wprom.alert("접근 권한이 없습니다.");
        return;
    }
    // console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [grd 클릭 이벤트] [TEST_02]" );

	if(grd_List.getColumnID(col) == "sConuResltName") {		// 심의상태 이면
		// console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [심의상태 이면] [Ds_SqrtList.sPlanNo]"+ Ds_SqrtList.getCellData(row, "sPlanNo") );

		var chkJudgeEmpNo = scwin.chkJudge(Ds_SqrtList.getCellData(row, "sPlanNo"));

      	if (chkJudgeEmpNo == "") { 
       		scwin.saveJudge(Ds_SqrtList.getCellData(row, "sPlanNo"));
   		} else {
              var judgeEmpArr = wconv.split(chkJudgeEmpNo, "(", true);
              var sJudgeEmpNo = judgeEmpArr[1];

              if (util.getGdsValue("gUserID") != sJudgeEmpNo.substr(0, 7)) {
                  wprom.alert("심사중입니다!!\n\n" + chkJudgeEmpNo);
                  return;
              }
		}
		// console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [심의상태 이면] [TEST_51] [chkJudgeEmpNo]"+ chkJudgeEmpNo );

  		openFlag = false;
   		var arg = {};//
   		arg.sPlanNo=Ds_SqrtList.getCellData(row, "sPlanNo");
		arg.sJugmAuthCd40=Ds_SqrtList.getCellData(row, "sJugmAuthCd40");  
        arg.rqstFlag="56";	//심사권한 
		arg.sPsnGroupFlgcd=Ds_SqrtList.getCellData(row, "sPsnGroupFlgcd");	   //요청승인구분(52:인수요청  56:인수승인)

      	//2011.04.21 김상운 수정(확정건은 조회만가능)
  		if (Ds_SqrtList.getCellData(row, "sPlanProgRankCd") == "00") {
  			arg.sOnlySrarch="1";
   		}

  		var strHandlerID ="";
		if(!util.isNull(util.getGdsValue("gUserID"))) {   //  로그인사번 존재하면
  			strHandlerID = util.getGdsValue("gUserID");
   		}
		if(!util.isNull(Ds_SqrtList.getCellData(row, "sDoerUser"))) {   //  취급자ID(사용인코드) 존재하면
  			strHandlerID = Ds_SqrtList.getCellData(row, "sDoerUser");
   		}
  		if(!util.isNull(Ds_SqrtList.getCellData(row, "sAplc"))) {   //  요청자사번(사용인ID)이 존재하면
  			strHandlerID = Ds_SqrtList.getCellData(row, "sAplc");
   		}		
		arg.sHandlerID = strHandlerID;		// 우선순위(사용인ID, 취급자ID, 로그인사번): 추가(2017.11.24)
  		//console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [심의상태 이면] [TEST_91] [팝업 전] [arg.sHandlerID]"+ arg.sHandlerID );


		var popInfo = {
			"width"     : 1300,
			"height"    : 769,
			"indata"    : arg,		 //indata
			"callback"  : function() {scwin.Search(Cmb_Page.getValue());},
			"title"     : "인수심사결재"
		};
		//wprom.popupOpen("/ui/cbsc/CBSC084P.xml", popInfo);		// 인수심사결재 팝업 처리 ==> 인수심사결재[O210_Pop1]
		wprom.windowOpen("/ui/cbsc/CBSC084P.xml", popInfo);

/*			
	    });	 // end of promise()

	    promise = promise.then(function(rst) {
	    	//console.log("[/CBSC092M.xml] [uf_sUserIDPop1()] ==> [promise.then] [TEST] [rst]"+ rst );

	    	scwin.Search(1); //goo 

	        if (wconv.length(rst) != 0) {
//	            var arrRst = (rst + "").split("^"); 
//	         	var sTab = arrRst[2];  
	        }
	    });	 // end of promise()
		*/


    } else if(grd_List.getColumnID(col) == "sPlanNo") {		// 청약번호 이면  		 
		var sBunsMetdDocuClsfCd = Ds_SqrtList.getCellData(row, "sBunsMetdDocuClsfCd");	// 사업방법서 구분 
        var sBunsMetdClsfCd = Ds_SqrtList.getCellData(row, "sBunsMetdClsfCd");
        //console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [청약번호 이면] [util.getGdsValue('gUserDuty')]"+ util.getGdsValue("gUserDuty") );

        if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35" || util.getGdsValue("gUserDuty") == "48" 
        	|| util.getGdsValue("gUserDuty") == "49" || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" || util.getGdsValue("gUserDuty") == "65" 
        	|| util.getGdsValue("gUserDuty") == "64" || util.getGdsValue("gUserDuty") == "66") {	    // 대리점일때 권한 체크 
            if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "1", Ds_SqrtList.getCellData(row, "sPlanNo")) == false) {	   // 청약번호 권한 체크
                wprom.alert("조회 권한이 없습니다.");
                return;
            }
        }
	  // console.log("[/CBSC111M.xml] [grd_List_OnCellClick()] ==> [청약번호 이면] [TEST_21] [chk_Group.getValue()]"+ chk_Group.getValue() +"[util.isNull(chk_Group.getValue())]"+ util.isNull(chk_Group.getValue()) );

        if(!util.isNull(chk_Group.getValue())) { 	// 단체 이면(check Box 선택시)  
            if (Ds_SqrtList.getCellData(row, "sPsnGroupFlgcd") == "21") {	  //단체  
				var oDsJson=[{"PrigId":"CBSC111M", "sInsuredNo": Ds_SqrtList.getCellData(row, "sPlanNo")} ];  		// 필드 Json에 담기(gDs_LinkArg 함수 사용) 
        		window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");  

				gfn_OpenLinkPage("CBSC042M");		// 단체청약설계로 URL 이동 

            } else { 
                var oDsJson=[{"PrigId":"CBSC111M", "sInsuredNo": Ds_SqrtList.getCellData(row, "sPlanNo")} ];  		// 필드 Json에 담기(gDs_LinkArg 함수 사용) 
        		window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");   

                if (sBunsMetdDocuClsfCd == "3" || sBunsMetdClsfCd == "1") {	 // 재물 추가 
                    gfn_OpenLinkPage("CBSC094M");		// 재물청약설계로 URL 이동 
                } else {               
                    gfn_OpenLinkPage("CBSC015M");		// 개인청약설계로 URL 이동 
                }
            }

        } else { 		// 개인 이면(check Box 미 선택시) 
            var sVvalue = Ds_SqrtList.getCellData(row, "sPlanNo"); //	 청약번호

            //window.top.
            if (parent.Id == "global") { 
                if (sBunsMetdDocuClsfCd == "3" || sBunsMetdClsfCd == "1") {	//재물 추가
                    UserNotify(2, sVvalue);
                } else if (sBunsMetdDocuClsfCd == "7") { 
                    UserNotify(3, sVvalue);	     //장기보험고도화-연금청약화면 이동
                } else {
                    UserNotify(0, sVvalue);
                }
            } else {
                var arg = "sBizCode=" + wconv.quote(sVvalue); 
                var oDsJson=[{"PrigId":"CBSC111M", "sInsuredNo": Ds_SqrtList.getCellData(row, "sPlanNo")} ];  		// 필드 Json에 담기(gDs_LinkArg 함수 사용)
             	 window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");          

                if (sBunsMetdDocuClsfCd == "3" || sBunsMetdClsfCd == "1") {	   //재물 추가 
                    gfn_OpenLinkPage("CBSC094M");		// 재물청약설계로 URL 이동 
                } else if (sBunsMetdDocuClsfCd == "7") {  
              		gfn_OpenLinkPage("CBSC075M");		// 연금청약설계로 URL 이동  
                } else {             
               		gfn_OpenLinkPage("CBSC015M");		// 개인청약설계로 URL 이동 
                }
            }
        }
    }                 
 };                   

//----------------------------------------------------------------------------------------------------
// function 명 : btn_Goods_OnClick
// 내용        : 상품군 찾기 팝업 처리
//----------------------------------------------------------------------------------------------------
scwin.btn_Goods_OnClick = function(e) {
    var promise = Promise.resolve();

    promise = promise.then(function(result) {
	    var sArg = {"sGoods":medt_sGdCd.getValue()
	               ,"sFromDate":cal_From.getValue()
	               ,"sToDate":cal_To.getValue()
	               };
		var popInfo = {
				"width"     : 835,
				"height"    : 470,
				//"top"       : 100,
				//"left"      : 100,
				"indata"    : sArg,
				"callback"  : "",
				"title"     : "상품리스트조회"
			};
			return wprom.popupOpen("/ui/cbsc/CBSC065P.xml", popInfo);		// 상품군 찾기 팝업 처리
    });

    promise = promise.then(function(result) { 

        if (result.length > 0) {
        	var arrRst = result.split("^");

            medt_sGdCd.setValue(arrRst[0]);		// 상품코드 

        } else {
            medt_sGdCd.setValue(""); 
        }
    }); 
};

