//----------------------------------------------------------------------------------------------------
// function 명 : btn_grup_onclick
// 내용        : 조직 찾기 팝업 처리
//---------------------------------------------------------------------------------------------------- 
scwin.btn_grup_onclick = function(e) {
	//console.log("[/CBSC111M.xml] [btn_Doer_onclick()] ==> [조직 찾기] [TEST_01]" );  

    var promise = Promise.resolve();

    promise = promise.then(function(result) {
	    var sArg = {};
		var popInfo = {
				"width"     : 730,
				"height"    : 500, 
			//	"indata"    : sArg,
				"callback"  : "",
				"title"     : "조직 찾기",
			};
			return wprom.popupOpen("/ui/cbso/CBSO020P.xml", popInfo);		// 조직 찾기 팝업 처리
    	});

    promise = promise.then(function(result) { 

		if(result.length > 0 ) {
			var arrRst = result.split("^"); 

	        if(wconv.toNumber(arrRst[2]) <= 2) {
	        	// console.log("[/CBSC111M.xml] [btn_Doer_onclick()] ==> [조직 찾기] [arrRst[2]]"+ wconv.toNumber(arrRst[2])  );  
				alert("조직은 팀/센터이후부터 선택하세요");
			} else{	
				edt_deptCd.setValue(arrRst[0]);		// 부서코드
				edt_deptName.setValue(arrRst[1]);	// 부서명
				edt_level.setValue(arrRst[2]); 			// 직위
			}
		}
    });
 };

//----------------------------------------------------------------------------------------------------
// function 명 : btn_Doer1_onclick
// 내용        : 버튼 클릭 이벤트: 조회 기준(사원찾기, 조직 찾기) 팝업
//----------------------------------------------------------------------------------------------------
scwin.btn_Doer1_onclick = function(e) {
 	//console.log("[/CBSC111M.xml] [btn_Doer_onclick()] ==> [버튼 클릭 이벤트: 조회 기준(사원찾기, 조직 찾기) 팝업] [TEST_01] [cmb_Gubun.getValue()]"+ cmb_Gubun.getValue() );  

    if (cmb_Gubun.getValue() == "10") {  	// 조회 기준(사원찾기, 조직 찾기) 콤보가 '조직찾기' 이면 

   		var promise = Promise.resolve();

   		var sTeamCd = scwin.getTeamCd();  	// 로그인 ID에 해당하는 팀코드 조회 처리 결과값 받기 
    	var sAuthFlag = scwin.getAuthFlag200196();  // 권한코드(200196)에 대한 권한 여부 조회 처리 결과값 받기 

 	  	promise = promise.then(function(result) {
		    var sArg = {};
			var popInfo = {
					"width"     : 730,
					"height"    : 500,  
					"callback"  : "",
					"title"     : "조직 찾기",
				};
				return wprom.popupOpen("/ui/cbso/CBSO020P.xml", popInfo);		// 조직 찾기 팝업 처리
   		});

    	promise = promise.then(function(result) { 
	  		// console.log("[/CBSC111M.xml] [btn_Doer_onclick()] ==> [조직 찾기] [wconv.length(arrRst)]"+ wconv.length(arrRst) );  

	 		if(result.length > 0 ) {				 
				var arrRst = result.split("^");

				if (wconv.length(arrRst) != 0) {	                
	                if (sAuthFlag == "Y" && (sTeamCd != arrRst[0])) {
	                    wprom.alert("직영TM 실장의 경우, 본인 소속 TM실을 선택해주세요.");
	                    medt_Str.setValue("");
	                    slevel = "";
	                } else { 
	                	medt_Str.setValue(arrRst[0]);
						slevel  = arrRst[2]; 
	                }
	            }
            }
        }); 

    } else {	// 조회 기준(사원찾기, 조직 찾기) 콤보가 '취급자코드' 이면  
	     var promise = Promise.resolve();

	    promise = promise.then(function(result) { 
			var popInfo = {
					"width"     : 660,
					"height"    : 430, 
					"callback"  : "",
					"title"     : "사원찾기",
				};
				return wprom.popupOpen("/ui/cbso/CBSO015P.xml", popInfo);			// 사원 찾기 팝업 처리
	    });

	    promise = promise.then(function(result) {
	        var arrRst = result.split("^"); 
			medt_Str.setValue(arrRst[0]);		// 사원코드
	    }); 
    } 
};

//----------------------------------------------------------------------------------------------------
// function 명 : medt_Str_OnCharChanged
// 내용        : 계약자코드조회시   ==> 확인할 것...
//----------------------------------------------------------------------------------------------------
scwin.medt_Str_OnCharChanged = function(obj, strPreText, strPostText) {
	//console.log("[/CBSC111M.xml] [chk_Group_oncheckboxclick()] ==> [계약자코드 변경 이벤트] [TEST_01] [cmb_Gubun.getValue()]"+ cmb_Gubun.getValue() );  

    if (cmb_Gubun.getValue() == 3) {
        if (wconv.Length(medt_Str.getValue()) == 10) {
            if (isBizRegNo(medt_Str.getValue())) {
                medt_Str.setDisplayFormat("###-##-#####");
            } else {
                medt_Str.setDisplayFormat("######-#ZZZZZZ");
            }
        }
    }
};

//----------------------------------------------------------------------------------------------------
// function 명 : chk_Group_oncheckboxclick
// 내용        : 단체check선택시
//----------------------------------------------------------------------------------------------------
scwin.chk_Group_oncheckboxclick = function(index,checked,value) {
	// //console.log("[/CBSC111M.xml] [chk_Group_oncheckboxclick()] ==> [단체check선택시] [TEST_01] [index]"+ index +"[checked]"+ checked +"[value]"+ value );  

	if (checked == false) {			// 체크 박스가 체크 안 되었으면  
        wconv.filter(ds_combo, " id_cd > 0", true);  	// 전체 조회 
    } else { 
     	wconv.filter(ds_combo, " id_cd > 2", true); 
    }

    cmb_Gubun.setSelectedIndex(0);
    Ds_SqrtList.removeAll();
};

//----------------------------------------------------------------------------------------------------
// function 명 : medt_Str_OnKeyDown()
// 내용        : keyDown시 처리
//----------------------------------------------------------------------------------------------------
scwin.medt_Str_onkeydown = function(e) {  
	// console.log("[/CBSC111M.xml] [chk_Group_oncheckboxclick()] ==> [medt key Down 이벤트] [TEST_01]" );  

	if (e.keyCode == 13 ) { 		// 엔터 키를 쳤으면
		 scwin.btn_search_onclick();
	} 
};

//**************************************************************************************************
// 페이징 처리 - 첫 페이지
//**************************************************************************************************
scwin.Btn_PreV_OnClick = function(obj) {
    var ftVal = wconv.toNumber(Ds_Page.getCellData(0, "FILTERCD")) - 1;
    if (wconv.toNumber(maxFilterVal) < wconv.toNumber(ftVal)) {
        wprom.alert("조회의 처음입니다.");
        return;
    } else if (wconv.toNumber(ftVal) < 1) {
        wprom.alert("조회의 처음입니다.");
        return;
    }
    wconv.filter(Ds_Page, "FILTERCD=='" + ftVal + "'", true);
    Cmb_Page.setValue(Ds_Page.getCellData(0, "CODE"));
    scwin.Search(Cmb_Page.getValue());  //수정부분
};

//**************************************************************************************************
// 페이징 처리 - 이전 페이지
//**************************************************************************************************
scwin.Btn_Pre_OnClick = function(obj) {
    var Idx = Cmb_Page.getValue();
    if (wconv.toNumber(Idx) - 1 < 1) {
        wprom.alert("조회의 처음입니다.");
        return;
    }
    scwin.Search(wconv.toString(wconv.toNumber(Idx) - 1));
    if (Ds_Page.getCellData(Ds_Page.firstrow, "CODE") == Idx) {
        wconv.filter(Ds_Page, "FILTERCD=='" + wconv.toString(wconv.toNumber(Ds_Page.getCellData(Ds_Page.firstrow, "FILTERCD")) - 1) + "'");
        Cmb_Page.setValue(Ds_Page.getCellData(Ds_Page.getTotalRow(), "CODE"));
        /*수작업 변환 가이드 (getTotalRow)
            필터 적용이 되어있는 row의 건수는 getRowCount API를 사용합니다.*/
    } else
        Cmb_Page.setValue(wconv.toNumber(Idx) - 1);
};

//**************************************************************************************************
// 페이징 처리 - 다음 페이지
//**************************************************************************************************
scwin.Btn_Next_OnClick = function(obj) {
    var Idx = Cmb_Page.getValue();
    if (wconv.toNumber(maxPage) < wconv.toNumber(Idx) + 1) {
        wprom.alert("더이상 자료가 존재하지 않습니다");
        return;
    }
    scwin.Search(wconv.toString(wconv.toNumber(Idx) + 1));
    if (Ds_Page.getCellData(Tab0_tab1_iv1_Ds_Page.lastrow, "CODE") == Idx) {
        wconv.filter(Ds_Page, "FILTERCD=='" + wconv.toString(wconv.toNumber(Ds_Page.getCellData(Ds_Page.getTotalRow(), "FILTERCD")) + 1
            /*수작업 변환 가이드 (getTotalRow)
                    필터 적용이 되어있는 row의 건수는 getRowCount API를 사용합니다.*/
        ) + "'");
    }
    Cmb_Page.setValue(wconv.toNumber(Idx) + 1);
};

//**************************************************************************************************
// 페이징 처리 - 다음 페이지 리스트
//**************************************************************************************************
scwin.Btn_NextV_OnClick = function(obj) {
    var ftVal = wconv.toNumber(Ds_Page.getCellData(Ds_Page.currow, "FILTERCD")) + 1;
    if (wconv.toNumber(maxFilterVal) < wconv.toNumber(ftVal)) {
        wprom.alert("더이상 자료가 존재하지 않습니다");
        return;
    }
    wconv.filter(Ds_Page, "FILTERCD=='" + ftVal + "'");
    Cmb_Page.setValue(Ds_Page.getCellData(0, "CODE"));
    scwin.Search(Cmb_Page.getValue());
};

