//----------------------------------------------------------------------------------------------------
// function 명 : lfn_InrpsCd_display
// 내용        : 주민번호 "*"처리
//----------------------------------------------------------------------------------------------------
scwin.lfn_InrpsCd_display = function(sInrpsCd) {

    if (sAgncYn == "Y") {	// 대리점 이면 
        smask = "######-#******";
        if (wconv.Length(sInrpsCd) == 10) {
            smask = "###-##-*****";
        }
        return gfn_convert_mask(sInrpsCd, smask);
    } else {
        return sInrpsCd;
    }
};

//----------------------------------------------------------------------------------------------------
// function 명 : chkJudge
// 내용        : 심의상태 판정 체크
//----------------------------------------------------------------------------------------------------
scwin.chkJudge = function(sPlanNo) {
	//console.log("[/CBSC111M.xml] [chkJudge()] ==> [심의상태 판정 체크] [TEST_01] [sPlanNo]"+ sPlanNo );  

//    wconv.clear(DsWs_chkJudge);
//    SOBJ_Judge.WSDL = gds.gWsdlBasis;
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Judge, "com.inswave.longterm.ssr.sst.task.DBB170Task", "chkJudge", "", "");
//    
//    gfn_SetParam(MethodObj, "sPlanNo", sPlanNo);
//    gfn_CallService(SOBJ_Judge, DOBJ_Judge, MethodObj);
   // wconv.copy(Ds_chkJudge, DsWs_chkJudge);

    console.log("\n\n 		[/CBSC111M.xml] [chkJudge()] ==> [01. 심의상태 판정 체크 조회] [TEST_01] #############] =====> ");  

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB170Task", "chkJudge");  	// 01. 심의상태 판정 조 Obj 
	MethodObj.setId("chkJudge")	;		// ID 셋팅
	MethodObj.setParam("sPlanNo", sPlanNo);	// Param 셋팅(회계일자) 
	//MethodObj.setParam("sUserID", Tab0_tab1_Edt_sUserID.getValue() );			// Param 셋팅(수납확정자)  
	MethodObj.setUrl("/contract/CBSC111MController.do");  // URL 설정 
	MethodObj.setXPath("//LTIDA14");		// XPath
	MethodObj.addXPath("//WARNING");	// XPath(No)
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	//console.log("[/CBSC111M.xml] [chkJudge()] ==> [01. 심의상태 판정 체크 조회_완료] [TEST_91]");  

	util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_chkJudge.setJSON(_tranSync.getDsJson("LTIDA14"));		// DS 셋팅(조회 결과)  
	//console.log("[/CBSC111M.xml] [chkJudge()] ==> [DS_갯수]"+ Ds_chkJudge.getRowCount() +"[Ds_chkJudge]"+ WebSquare.xml.serialize(Ds_chkJudge.getAllXML()) ); 

    var sJudgeEmpNo = Ds_chkJudge.getCellData(0, "sJudgeEmpNo");
    var sJudgeEmpName = Ds_chkJudge.getCellData(0, "sJudgeEmpName");
    //console.log("[/CBSC111M.xml] [chkJudge()] ==> [TEST_end] [sJudgeEmpNo]"+ sJudgeEmpNo );  

    if (sJudgeEmpNo == "")
        return "";
    else
        return sJudgeEmpName + " (" + sJudgeEmpNo + ")";
};

//----------------------------------------------------------------------------------------------------
// function 명 : saveJudge
// 내용        : 심의상태 판정 처리
//----------------------------------------------------------------------------------------------------
scwin.saveJudge = function(sPlanNo) {

	//console.log("[/CBSC111M.xml] [saveJudge()] ==> [심의상태 판정] [TEST_01] [sPlanNo]"+ sPlanNo );  

	//console.log("\n\n 		[/CBSC111M.xml] [saveJudge()] ==> 02. 심의상태 판정 처리] [TEST_01] #############] =====> ");  

	var idx = Ds_SaveJudge.insertRow();     
	Ds_SaveJudge.setCellData(0, "sJudgeEmpNo", util.getGdsValue("gUserID") );   
	Ds_SaveJudge.setCellData(0, "sPlanNo", sPlanNo );	 
	//console.log("[/CBSC111M.xml] [saveJudge()] ==> [DS_갯수]"+ Ds_SaveJudge.getRowCount() +"[Ds_SaveJudge]"+ WebSquare.xml.serialize(Ds_SaveJudge.getAllXML()) ); 

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB170Taske", "saveJudge"); 	// 01. 심의상태 판정 처리 Obj 
	MethodObj.setId("chkJudge")	;		// ID 셋팅
	MethodObj.setParam("sPlanNo", sPlanNo);	// Param 셋팅(회계일자)  
	MethodObj.addSingleData("LTIDA14", "LTIDA14", "Ds_SaveJudge");       	// 01. 수납번호 채번   
	MethodObj.setUrl("/contract/CBSC111MController.do");  // URL 설정 
	MethodObj.setXPath("//LTIDA14");		// XPath
	MethodObj.addXPath("//WARNING");	// XPath(No)
	//MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	//console.log("[/CBSC111M.xml] [saveJudge()] ==> [02. 심의상태 판정 처리_완료] [TEST_91]");   
};

