};

//**************************************************************************************************
// lfn_getColumnValue:  칼럼 expression  처리 함수
//**************************************************************************************************
scwin.lfn_getColumnValue = function(data, formattedData, rowIndex, colIndex) { 
	//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [TEST_01] [rowIndex]"+ rowIndex +"[colIndex]"+ colIndex +"[grd_List.getColumnID(colIndex)]"+ grd_List.getColumnID(colIndex) ); 

	if (grd_List.getColumnID(colIndex) == "sCrtorName") {  	// Grid에서 칼럼이 '계약자명' 이면  
		var sCrtorName = Ds_SqrtList.getCellData(rowIndex, "sCrtorName");	 	// 계약자명 
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '계약자명' 이면] [sAuthByflag]"+ sAuthByflag +"[sCrtorName]"+ sCrtorName ); 

		if (sAuthByflag == true) {
			formattedData = sCrtorName; 
		} else {
			formattedData = gfn_changeMask(sCrtorName, 4);  		// 마스크 처리[4. 성명: 2째자리 부터 2자리 * 처리]
		} 
	 	//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '계약자명' 이면] [계약자명]"+ sRslt ); 

	} else if (grd_List.getColumnID(colIndex) == "sCrtorCd") {	// Grid에서 칼럼이 '계악자코드' 이면 		 
		var sCrtorCd = Ds_SqrtList.getCellData(rowIndex, "sCrtorCd");	 	// 계악자코드  
		formattedData = gfn_changeMask(sCrtorCd, 1);  		 // 마스크 처리[1. 주민번호: 뒷번호 6자리]
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '계악자코드' 이면] [sRslt]"+ sRslt );  

	} else if (grd_List.getColumnID(colIndex) == "sInsureTerm") {	// Grid에서 칼럼이 '보험기간' 이면 
		var sInsurStrtdate = Ds_SqrtList.getCellData(rowIndex, "sInsurStrtdate");
		var sInsurEndDate = Ds_SqrtList.getCellData(rowIndex, "sInsurEndDate");  
		//sRslt = sInsurStrtdate +"~"+ sInsurEndDate;	// 보험기간
		formattedData = wconv.substr(sInsurStrtdate, 0, 4) +"."+ wconv.substr(sInsurStrtdate, 4, 2) +"."+  wconv.substr(sInsurStrtdate, 6, 2)
			 		 +"~"+ wconv.substr(sInsurEndDate, 0, 4) +"."+ wconv.substr(sInsurEndDate, 4, 2) +"."+  wconv.substr(sInsurEndDate, 6, 2);			// 보험기간 
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '보험기간' 이면] [sRslt]"+ sRslt ); 

	} else if (grd_List.getColumnID(colIndex) == "sInrpsName") {		// Grid에서 칼럼이 '피보험자명' 이면
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '피보험자명' 이면] [TEST_41]" );  
		var sInrpsName = Ds_SqrtList.getCellData(rowIndex, "sInrpsName");	 	// 피보험자명 
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '피보험자명' 이면] [TEST_43] [권한 코드]"+ sAuthByflag +"[sInrpsName]"+ sInrpsName ); 

		if (sAuthByflag ==  true) {
			formattedData = sInrpsName; 
		} else {
			formattedData = gfn_changeMask(sInrpsName, 4);  // 마스크 처리[4. 성명: 2째자리 부터 2자리 * 처리]
		}	  
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '피보험자명' 이면] [TEST_44] [권한 코드]"+ sAuthByflag +"[formattedData]"+ formattedData ); 

	} else if (grd_List.getColumnID(colIndex) == "sInrpsCd") {		// Grid에서 칼럼이 '피보험자코드' 이면
		var sInrpsCd = Ds_SqrtList.getCellData(rowIndex, "sInrpsCd");   	// 피보험자코드 
		formattedData = gfn_changeMask(sInrpsCd, 1);	// 마스크 처리[1. 주민번호 : 뒷번호 6자리]
		//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [Grid에서 칼럼이 '피보험자코드' 이면] [sRslt]"+ sRslt ); 

	} else if (grd_List.getColumnID(colIndex) == "sScrnName") {	// Grid에서 칼럼이 '심사자(경과시간)' 이면
		var elapsetime = Ds_SqrtList.getCellData(rowIndex, "elapsetime");   		// 경과시간
		var sAppmnName = Ds_SqrtList.getCellData(rowIndex, "sAppmnName");	// 승인자명 

		if (wconv.length(elapsetime) > 0 ) {
			formattedData = sAppmnName +" (" + elapsetime +")"; 
		} else {
			formattedData = sAppmnName;
		}

	} else if (grd_List.getColumnID(colIndex) == "sRqstHms") {	// Grid에서 칼럼이 '요청시간' 이면 
		var sRqstHms = Ds_SqrtList.getCellData(rowIndex, "sRqstHms"); 
		formattedData = wconv.substr(sRqstHms, 0, 2) +"시"+ wconv.substr(sRqstHms, 2, 2) +"분";		// 요청시간 		
	} 

	if (Ds_SqrtList.getCellData(rowIndex, "sEmergencyYn") == "Y") {	// 긴급심사여부 칼럼이 'Y'  이면 
		 grd_List.setCellBackgroundColor( rowIndex , colIndex , "#F3E5E5" ); 	// Grid에서 칼럼 배경색 변경
//	} else {
//		grd_List.setCellBackgroundColor( rowIndex , colIndex , "wheat" );
	}
	if (grd_List.getColumnID(colIndex) == "sCrNo") {
		grd_List.setCellStyle(rowIndex, colIndex, "text-decoration", "underline");
		grd_List.setCellStyle(rowIndex, colIndex, "font-weight", "bold");
	}
	//console.log("[/CBSC111M.xml] [lfn_getColumnValue()] ==> [TEST_end] [rowIndex]"+ rowIndex +"[colIndex]"+ colIndex  ); 

	return formattedData;
};
