head.script:
var applyEnable = "N";  // 설계상태(인수완료)-적용버튼(비활성화)   
var parent_Ds_Prprt = "";		// 부모창 목적물 정보 Ds Set  
var sToday = ""; // 현재일자(서버일자) 

//---------------------------------------------------------------------------
// function 명 : FORM_OnLoadCompleted(obj)
// 내용        : FORM OnLoad
//--------------------------------------------------------------------------- 
scwin.init = function() {	
	console.log("[/CBSC120P.xml] [init()] ==> [폼 로딩] [TEST_01]" );   

	pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>

		var indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata); 	// 부모창에서 받아온 값( 예) inJSON.sMyPlan)

		if (!util.isNull(inJSON.applyEnable)) 	applyEnable = inJSON.applyEnable;	 // 설계상태(인수완료)-적용버튼(비활성화)   

		var pObj = wprom.getParamObject();  
		parent_Ds_Prprt = pObj.Ds_Prprt;	// 부모창 목적물 정보 Ds Set 

		if (applyEnable == "Y")  btn_apply.setDisabled(false);
		//console.log("[/CBSC116P.xml] [init()] ==> [폼 로딩] ==> [적용버튼(비활성화)]"+ applyEnable );    	

		 // 공통코드 조회
		scwin.lfn_getCommonCodes();

		// DataSet 초기화
		scwin.lfn_initDataset(); 

		return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); // =================================================>
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	//pro = pro.catch(function(rj) {
		//util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	//});

	return pro; 
};

//---------------------------------------------------------------------------
// function 명 : lfn_getCommonCodes(obj)
// 내용        : 공통코드 조회
//--------------------------------------------------------------------------
scwin.lfn_getCommonCodes = function() {
	//console.log("[/CBSC120P.xml] [lfn_getCommonCodes()] ==> [공통코드 조회_01] [TEST_01]" );

	var dataSets = "DsWs_ParkEqmtFlagCd";   
	var columns = "S59";   
    var objects = "";
    var titleTypes =  "N"; 		// A:전체, S:선택, N:안넣

	gfn_getCommonCodes(dataSets, columns, objects, titleTypes);     
   // console.log("[/CBSC120P.xml] [lfn_getCommonCodes()] ==> [DsWs_ParkEqmtFlagCd_갯수]"+ DsWs_ParkEqmtFlagCd.getRowCount() +"[DsWs_ParkEqmtFlagCd]"+ WebSquare.xml.serialize(DsWs_ParkEqmtFlagCd.getAllXML()) ); 

	wconv.copy(Ds_ParkEqmtFlagCd, DsWs_ParkEqmtFlagCd); 	  // 주차시설구분코드 Dataset Copy(Ds_ParkEqmtFlagCd 데이타 생성됨)  
  	//console.log("[/CBSC120P.xml] [lfn_getCommonCodes()] ==> [주차시설구분코드 Ds] [Ds_ParkEqmtFlagCd_갯수]"+ Ds_ParkEqmtFlagCd.getRowCount() +"[Ds_ParkEqmtFlagCd]"+ WebSquare.xml.serialize(Ds_ParkEqmtFlagCd.getAllXML()) ); 

	Ds_Prprt_Appl.setRowPosition(0);	 // Row 위치 0번째로 지정 
    //console.log("[/CBSC120P.xml] [lfn_getCommonCodes()] ==> [공통코드 조회] [TEST_end]" );  
};

//---------------------------------------------------------------------------
// function 명 : btn_search_OnClick(obj)
// 내용        : Dataset ClearData
//---------------------------------------------------------------------------
scwin.lfn_initDataset = function() {
	//console.log("[/CBSC120P.xml] [lfn_initDataset()] ==> [DataSet 초기화] [TEST_01]" );

	wconv.copy(Ds_Prprt_Appl, parent_Ds_Prprt);	 // 피보험목적물 Dataset Copy(Ds_Prprt_Appl 데이타 생성됨)

    util.setRowPosition(Ds_Prprt_Appl, Ds_Prprt_Appl.getRowPosition());  	// Row 위치 지정

    Ds_Prprt_Appl.setRowPosition(0);	 // Row 위치 0번째로 지정 
};

//---------------------------------------------------------------------------
// function 명 : btn_Close_OnClick(obj)
// 내용        : 닫기 OnClick
//---------------------------------------------------------------------------
scwin.btn_Close_OnClick = function(obj) {
    wprom.close("");
};

//---------------------------------------------------------------------------
// function 명 : btn_apply_OnClick(obj)
// 내용        : 적용 OnClick
//---------------------------------------------------------------------------
scwin.btn_apply_OnClick = function(obj) {
	//console.log("[/CBSC120P.xml] [btn_apply_OnClick()] ==> [적용] [TEST_01]" );

    if (Ds_Prprt_Appl.getRowCount() < 1) { 
    //if (Ds_Prprt_Appl.getFocusedRowIndex() < 0) {
        wprom.alert("목적물이 입력되지 않았습니다!");
        return;
    }
    if (wconv.trim(cmb_ParkEqmtFlagCd.getValue()) == null || wconv.trim(cmb_ParkEqmtFlagCd.getValue()) == "") {
        wprom.alert("주차시설구분을 선택하세요!");
        //sParkEqmtFlagCd.SetFocus();
		 util.focus(sParkEqmtFlagCd); // 포커스 설정    
        return;
    }
    if (wconv.trim(mke_PrkLtArea.getValue()) == null || wconv.trim(mke_PrkLtArea.getValue()) == "") {
        wprom.alert("주차장면적을 입력하세요!");
        //mke_PrkLtArea.focus(); 
		 util.focus(mke_PrkLtArea); // 포커스 설정    
        return;
    }
    if (wconv.trim(mke_ParkAvaCarNum.getValue()) == null || wconv.trim(mke_ParkAvaCarNum.getValue()) == "") {
        wprom.alert("주차가능차량대수를 입력하세요!");
        /mke_ParkAvaCarNum.focus();
		 util.focus(mke_ParkAvaCarNum); // 포커스 설정    
        return;
    }
    if (wconv.trim(mke_PrkLtMachNum.getValue()) == null || wconv.trim(mke_PrkLtMachNum.getValue()) == "") {
        wprom.alert("주차장기계대수 입력하세요!");
        //mke_PrkLtMachNum.focus();
		util.focus(mke_PrkLtMachNum); // 포커스 설정    
        return;
    }

	wconv.copy(parent_Ds_Prprt, Ds_Prprt_Appl);	  // 보험 목적물 적용 정보 Ds(Ds_Prprt_Appl)을 parent_Ds_Prprt에 복사  
    parent_Ds_Prprt.setRowPosition(Ds_Prprt_Appl.getRowPosition());	 // Row 위치 Ds_Prprt_Appl.getRowPosition() 번째로 지정 
    //console.log("[/CBSC120P.xml] [btn_apply_OnClick()] ==> [적용] [Ds_Prprt_Appl.getRowPosition]"+ Ds_Prprt_Appl.getRowPosition() +"[parent_Ds_Prprt.getRowPosition]"+ parent_Ds_Prprt.getRowPosition() );
    //console.log("[/CBSC120P.xml] [btn_apply_OnClick()] ==> [적용] [parent_Ds_Prprt, 조회수]"+ parent_Ds_Prprt.getRowCount() +"[parent_Ds_Prprt]"+ WebSquare.xml.serialize(parent_Ds_Prprt.getAllXML()) );

    wprom.close("");	 // 창닫기
};

//---------------------------------------------------------------------------
// function 명 : btn_SizeCalc_OnClick(obj)
// 내용        : 평형계산 OnClick
//---------------------------------------------------------------------------
scwin.btn_SizeCalc_OnClick = function(obj) {

 	scwin.lfn_dialogB660_Pop(); 	
};

//---------------------------------------------------------------------------
// function 명 : lfn_dialogB660_Pop
// 내용        : 평형계산 Popup
//---------------------------------------------------------------------------
scwin.lfn_dialogB660_Pop = function() {
	console.log("[/CBSC120P.xml] [lfn_dialogB660_Pop()] ==> [평형계산 Popup] [TEST_01]" );

	var promise = Promise.resolve();   // Promise 설정

    promise = promise.then(function(rtnVal) { 
     	 //팝업창으로 보낼 자료(Parameter)
		var indata  = {};

		var popInfo = {
		 	"width"     : 440,      	// 수정(width, height): 2017.11.04, by 진태만   --> 390, 50 
			"height"    : 80,
			"top"       : 0,
			"left"      : 0,
			"indata"    : indata,		 //indata
			"callback"  : "",
			"title"     : "평형계산[B660_Pop]"
		};
		return wprom.popupOpen("/ui/cbsc/CBSC127P.xml", popInfo);		// 평형계산[B660_Pop]  팝업 처리
    });	 // end of promise()

    promise = promise.then(function(rst) { 
 		console.log("[/CBSC120P.xml] [lfn_dialogB660_Pop()] ==> [평형계산 Popup] [TEST_07] [rst]"+ rst );

        if (wconv.length(rst) != 0) {   
	        if (rst != null && rst != "" && rst != 0) { 
	            Ds_Prprt_Appl.setCellData(Ds_Prprt_Appl.getRowPosition(), "nPrkLtArea", rst[0]);	 // 평형계산 
	        } 
        }
        return;
    });	 // end of promise() 

    return promise;
};
