	        Ds_Pibo.setCellData(0, "nInrpsAge", Ds_Pibo.getCellData(0, "nGroupInrpsAge")); //피보험자연령	 
	        Ds_Pibo.setCellData(0, "sInrpsSexFlag", Ds_Pibo.getCellData(0, "sGroupInrpsSexFlag")); //피보험자성별구분 
	        Ds_Pibo.setCellData(0, "sJobGradCd", Ds_Pibo.getCellData(0, "sGroupInrpsJobGradCd")); //직업급수코드 
	        Ds_Pibo.setCellData(0, "sDrveFlagCd", Ds_Pibo.getCellData(0, "sGroupInrpsDrveFlagCd")); //운전구분코드  
	        Ds_Pibo.setCellData(0, "sInrpsJobName", Ds_Pibo.getCellData(0, "sGroupInrpsJobName")); //직업명 
