		$("#Tap_UdrtkJugm_Tab_step3_Grid0_2_head_table").after($("#Tap_UdrtkJugm_Tab_step3_Grid0_2_gridFooterTDDefault_tbody"));   	// 03. 진단비 Tab Grid 합계 최상단에 배치
		$("#Tap_UdrtkJugm_Tab_step3_Grid0_2_dataLayer").css("bottom", "0px");	  // 기존에 bottom 있든 합계 Clear 처리

		Tap_UdrtkJugm.setSelectedTabIndex(3);	// 3 번째 Tab 설정 
		$("#Tap_UdrtkJugm_Tab_step4_Grid0_3_head_table").after($("#Tap_UdrtkJugm_Tab_step4_Grid0_3_gridFooterTDDefault_tbody"));   	// 04. 운전자 Tab Grid 합계 최상단에 배치
		$("#Tap_UdrtkJugm_Tab_step4_Grid0_3_dataLayer").css("bottom", "0px");	  // 기존에 bottom 있든 합계 Clear 처리

		Tap_UdrtkJugm.setSelectedTabIndex(4);	// 4 번째 Tab 설정
		$("#Tap_UdrtkJugm_Tab_step5_Grid0_4_head_table").after($("#Tap_UdrtkJugm_Tab_step5_Grid0_4_gridFooterTDDefault_tbody"));   	// 05. 기타 Tab Grid 합계 최상단에 배치
		$("#Tap_UdrtkJugm_Tab_step5_Grid0_4_dataLayer").css("bottom", "0px");	  // 기존에 bottom 있든 합계 Clear 처리

