//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼 클릭시 처리
//---------------------------------------------------------------------------
scwin.lfn_Search = function() {
	//console.log("[/CBSC128P.xml] [lfn_Search()] ==> [조회] [TEST_01]" );
 	//console.log("[/CBSC128P.xml] [lfn_Search()] ==> [조회] [I/F 조회_전] [Ds_Pibo_갯수_01]"+ Ds_Pibo.getRowCount() +"[Ds_Pibo]"+ WebSquare.xml.serialize(Ds_Pibo.getAllXML()) );  

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB290Task", "getInrpsAcum");	// 피보험자 누적 조회 Obj
	MethodObj.setId("getInsPlan");	// ID 셋팅  
	MethodObj.addSingleData("LTIDA01", "LTIDA01", "Ds_Pibo:A");  	// Param 셋팅(피보험자 정보 Ds): A(전체), I(등록), U(수정), R  
	MethodObj.setUrl("/contract/CBSC128PController.do");  // 서버단 호출 URL  
	MethodObj.setXPath("//*/LTIEA18");					// XPath(Ds_LTIDA00)  
	MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
	MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
	MethodObj.setHashTable();   	// 전문의 앞이 hashtable로 보낼 때 
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.setXPathDs();
	MethodObj.send();		// 최종 전송 
	//console.log("[/CBSC128P.xml] [lfn_Search()] ==> [01. 피보험자 누적 조회, 전송_완료] [TEST_91]");

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_LTIEA18.setJSON(_tranSync.getDsJson("//*/LTIEA18"));				// 피보험자 Ds 셋팅 
	DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)     
	//console.log("[/CBSC128P.xml] [lfn_Search()] ==> [Ds_LTIEA18, 갯수]"+ Ds_LTIEA18.getRowCount() +"[Ds_LTIEA18]"+ WebSquare.xml.serialize(Ds_LTIEA18.getAllXML()) );  
 	//console.log("[/CBSC128P.xml] [lfn_Search()] ==> [I/F 조회 완료] [Ds_LTIEA18, 갯수]"+ Ds_LTIEA18.getRowCount() );  

  	if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }
    if (Ds_LTIEA18.getRowCount() == 0) {
        wprom.alert("피보험자 누적건이 없습니다.");
    } 

 	//console.log("[/CBSC128P.xml] [lfn_Search()] ==> [TEST_end]" ); 
};

/*-----------------------------------
// function 명 : lfn_DataChangeCheck
// 내용        : dataset 변경여부 체크
-----------------------------------*/
scwin.lfn_DataChangeCheck = function(obj, newObj) {
    newObj.ClearData();
    var cnt = 0;
    for (var i = 0; i < obj.GetCountNF(); i++) {
        if (obj.GetColumnNF(i, "chk") == "1") {
            newObj.InsertRow(cnt);
            for (var j = 0; j < obj.GetColCount(); j++) {
                newObj.SetColumn(cnt, obj.GetColID(j), obj.GetColumnNF(i, obj.GetColID(j)));
            }
            cnt++;
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_OnCellClick
// 내용        : 계약조회화면 링크
//--------------------------------------------------------------------------- 
scwin.lfn_OnCellClick = function(row, col) {
	//console.log("[/CBSC128P.xml] [lfn_OnCellClick()] ==> [계약조회화면 링크] [TEST_01] [col]"+ col ); 

    switch (col) {
        case 0:  
            var sCrNo = Ds_LTIEA18.getCellData(row, "sCrNo"); //계약번호

            if (wconv.length(sCrNo) > 0) { 

   				var oDsJson=[{"PrigId":"CBSC128P"
			                		, "sCrNo": sCrNo
			                		, "sScrnId":  "B010"
			         				}];  		// 필드 Json에 담기(gDs_LinkArg 함수 사용)

		     	//window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");      // 주석 처리  // 수정: 2017.12.12, by 진태만 
		     	gds.gDs_LinkArg.setJSON(oDsJson);  // 수정(Pop up 일때): 2017.12.12, by 진태만
  				console.log("[/CBSC128P.xml] [lfn_OnCellClick()] ==> [gds.gDs_LinkArg_반영]"); 

	   			gfn_OpenLinkPage("CBSC028M");		// 계약사항상세내역(/CBSC028M.xml, /D020.xm) 
	        }
            break;

        default:
            break;
    }
};

