scwin.lfn_dsp_formatter = function(data) {
 	//console.log("[/CBSC128P.xml] [lfn_dsp_formatter()] ==> [TEST_01] [data]"+ data ); 

 	 var nRslt = util.appendComma(wconv.toNumber(data)/10000); // 10000자리 나눔, 숫자에 3자리 수마다 Comma를 적용

 	 //console.log("[/CBSC128P.xml] [lfn_dsp_formatter()] ==> [TEST_91] [nRslt]"+ nRslt +"[data]"+ data ); 

 	 return nRslt;
 }
