var sPlanNoPop = ""; 				// 설계번호 Pop
var sPsnGroupFlgcd = "";		 // 피보험자단체구분코드
var sPsnGroupFlgcdPop = "";	// 피보험자단체구분코드 Pop 
var sAgncYn = "N";					//대리점  여부
var parent_Ds_LTIDA00 = "";		// 부모창 보험 기본사항 정보 Ds Set
var parent_Ds_Pibo = "";			// 부모창 피보험자 정보 Ds Set 
var parent_Ds_Trty = "";			// 부모창 특약사항 Ds Set 

//---------------------------------------------------------------------------
// function 명 : B200_OnLoadCompleted
// 내용        : 화면로딩시 메인으로부터 파라메터값 담음
//---------------------------------------------------------------------------
scwin.init = function() {
	console.log("[/CBSC132P.xml] [init()] ==> [폼 로딩] [TEST_01]" );

	var pro = Promise.resolve();
	window.top.util.winTopShowModalChk = "T";
	util.winTopShowModal();

	pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>

		switch_div.hide();   // switch_div 숨기기

		var pObj = wprom.getParamObject(); 
		parent_Ds_LTIDA00 = pObj.Ds_LTIDA00;		// 부모창 보험 기본사항 정보 Ds Set 
		parent_Ds_Pibo = pObj.Ds_Pibo;				// 부모창 피보험자 정보 Ds Set 
		parent_Ds_Trty = pObj.Ds_Trty;					// 부모창 특약사항 정보 Ds Set  
		//	console.log("[/CBSC132P.xml] [lfn_Search()] ==> [parent_Ds_LTIDA00,갯수_01]"+ parent_Ds_LTIDA00.getRowCount() +"[parent_Ds_LTIDA00]"+ WebSquare.xml.serialize(parent_Ds_LTIDA00.getAllXML()) );  

		wconv.copy(Ds_LTIDA00, parent_Ds_LTIDA00); 	// 기본사항 Ds 복사
		wconv.copy(Ds_Pibo, parent_Ds_Pibo); 	// 피보험자 Ds 복사
		wconv.copy(Ds_Trty, parent_Ds_Trty); 	// 특약사항 정보 Ds 복사

		//단체일경우 변수명이 달라서 개인쪽에 맞춤
		if (Ds_LTIDA00.getCellData(0, "sPsnGroupFlgcd") == "21") { 
			Ds_Pibo.setCellData(0, "sCrtorRelnCd", "20"); //계약자관계구분코드
			Ds_Pibo.setCellData(0, "sInrpsCd", parent_Ds_Pibo.getCellData(0, "sGroupInrpsCd")); //피보험자코드	
			Ds_Pibo.setCellData(0, "sInrpsName", parent_Ds_Pibo.getCellData(0, "sGroupInrpsName")); //피보험자명
			Ds_Pibo.setCellData(0, "nInrpsAge", parent_Ds_Pibo.getCellData(0, "nGroupInrpsAge")); //피보험자연령
			Ds_Pibo.setCellData(0, "sJobCd", parent_Ds_Pibo.getCellData(0, "sGroupInrpsJobCd")); //직업코드
			Ds_Pibo.setCellData(0, "sJobGradCd", parent_Ds_Pibo.getCellData(0, "sGroupInrpsJobGradCd")); //직업급수코드
			Ds_Pibo.setCellData(0, "sDrveFlagCd", parent_Ds_Pibo.getCellData(0, "sGroupInrpsDrveFlagCd")); //운전구분코드
			Ds_Pibo.setCellData(0, "nPaymAge", ""); //납입연령
			Ds_Pibo.setCellData(0, "nEndAge", ""); //만기연령
		}

		scwin.lfn_Search();		// 해지환급금 조회

		return ;		// init 함수를 종료하고자 할때 사용	// =================================================> 
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	return pro;  
};

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 해지환급금 조회
//---------------------------------------------------------------------------
scwin.lfn_Search = function() {
	console.log("[/CBSC132P.xml] [lfn_Search()] ==> [해지환급금 조회] [TEST_01]" );   
	//console.log("[/CBSC132P.xml] [lfn_Search()] ==> [Ds_LTIDA00_갯수_01]"+ Ds_LTIDA00.getRowCount() +"[Ds_LTIDA00]"+ WebSquare.xml.serialize(Ds_LTIDA00.getAllXML()) );  
	//console.log("[/CBSC132P.xml] [lfn_Search()] ==> [Ds_Pibo_갯수_01]"+ Ds_Pibo.getRowCount() +"[Ds_Pibo]"+ WebSquare.xml.serialize(Ds_Pibo.getAllXML()) );  

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB200Task", "getExptRtnAmt");	// 해지환급금 조회 Obj
	MethodObj.setId("getExptRtnAmt");	// ID 셋팅     
	//MethodObj.setUrl("/contract/CBSC132PController_TEST.do");  				// 서버단 호출 URL 
	MethodObj.setUrl("/contract/CBSC132PController.do");  				// 서버단 호출 URL  
	MethodObj.addSingleData("LTIDA00", "LTIDA00", "Ds_LTIDA00:A");		// Param 셋팅(계약기본사항 정보 Ds): A, I, U, R
	MethodObj.addSingleData("LTIDA01", "LTIDA01", "Ds_Pibo:A");  			// Param 셋팅(계피보험자 정보 Ds): A, I, U, R
	MethodObj.addVectorData("vLTIDA02", "LTIDA02", "Ds_Trty:A");			// Param 셋팅(특약사항 정보 Ds) 
	MethodObj.setXPath("//*/AXR961");					// XPath(Ds_LTIDA00) 
	MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
	MethodObj.setHashTable();   	// 전문의 앞이 hashtable로 보낼 때CBSC132P.xml] [lfn_Search()] ==> [Ds_ExptRtnAmt_갯수 
	MethodObj.sync();		// 동기 처리[기본(async:비동기)] 
	MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
	MethodObj.send();		// 최종 전송  
	//console.log("[/CBSC132P.xml] [lfn_SearchData()] ==> [01. 해지환급금 조회, 전송_완료] [TEST_91]");

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_ExptRtnAmt.setJSON(_tranSync.getDsJson("//*/AXR961"));				// 해지환급금 정보 Ds 셋팅   
 	DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)     
	console.log("[/CBSC132P.xml] [lfn_Search()] ==> [I/F 조회 완료] [Ds_ExptRtnAmt_갯수]"+ Ds_ExptRtnAmt.getRowCount() );  
 	//console.log("[/CBSC132P.xml] [lfn_Search()] ==> [I/F 조회 완료] [Ds_ExptRtnAmt_갯수]"+ Ds_ExptRtnAmt.getRowCount() +"[Ds_ExptRtnAmt]"+ WebSquare.xml.serialize(Ds_ExptRtnAmt.getAllXML()) );  

  	if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }

    if (Ds_ExptRtnAmt.getRowCount() == 0) {
  		wprom.alert("조회 건이 없습니다.");
     	return;

    } else { 	 	        
		//console.log("[/CBSC132P.xml] [lfn_Search()] ==> [해지환급금 조회] [TEST_71]" );  

		switch_div.show();   // switch_div 보이기

		if (Ds_ExptRtnAmt.getCellData(0, "sRateCntpYn") == "N") {
                //grd_List.SetFormat("Default1"); 
                switch_div.setSelectedID("sw_div1");		// switch_div sw_div1 보이기 
            	console.log("[/CBSC132P.xml] [lfn_Search()] ==> [해지환급금 조회(2번)] [switch_div sw_div1 보이기][해지환급금 Grid2: 적용이율][6열] [Ds_ExptRtnAmt.sRateCntpYn]"+ Ds_ExptRtnAmt.getCellData(0, "sRateCntpYn") );  
 		} else {
     		if (Ds_ExptRtnAmt.getCellData(0, "sStndRatoUseYn") == "N") {
                 //grd_List.SetFormat("Default2");
                  switch_div.setSelectedID("sw_div2");	// switch_div sw_div2 보이기 
                  console.log("[/CBSC132P.xml] [lfn_Search()] ==> [해지환급금 조회(3번)] [switch_div sw_div2 보이기] [해지환급금 Grid2: 평균공시이율][8열] [Ds_ExptRtnAmt.sStndRatoUseYn]"+ Ds_ExptRtnAmt.getCellData(0, "sStndRatoUseYn") );  
   			} else {
                  //grd_List.SetFormat("Default0");
                  switch_div.setSelectedID("sw_div0");	 // switch_div sw_div0 보이기 
                  console.log("[/CBSC132P.xml] [lfn_Search()] ==> [해지환급금 조회(1번)] [switch_div sw_div0 보이기] [해지환급금 Grid1: 표준이율*1.2][8열] [Ds_ExptRtnAmt.sStndRatoUseYn]"+ Ds_ExptRtnAmt.getCellData(0, "sStndRatoUseYn") );  
     		}
		}
	} 

  //	switch_div.setSelectedID("sw_div1");	// switch_div sw_div2 보이기 

	//console.log("[/CBSC132P.xml] [lfn_Search()] ==> [해지환급금 조회] [TEST_end]" ); 	 
};

//---------------------------------------------------------------------------
// function 명 : lfn_getGdAttrFlag
// 내용        : 상품속성(금리연동여부)을 조회한다.
//---------------------------------------------------------------------------
scwin.lfn_GdAttrFlag_Search = function(sGdCd, sInsurStrtdate) {
    wconv.clear(DsWs_RateCntpYn);
    SOBJ.WSDL = gds.gWsdlBasis; //기본 웹서비스
    var MethodObj = gfn_CreateMethodObj(SOBJ, "com.inswave.longterm.ssr.sst.task.DBB200Task", "getGdAttrFlag", "", "");
    gfn_SetParam(MethodObj, "sGdCd", sGdCd); //상품코드
    gfn_SetParam(MethodObj, "sInsurStrtdate", sInsurStrtdate); //보험시작일
    gfn_CallService(SOBJ, DOBJ, MethodObj);
    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    } else {
        if (DsWs_RateCntpYn.getRowCount() == 0) {
            wprom.alert("해당 데이터가 없습니다.");
        }
    }
};
/*-----------------------------------
// function 명 : lfn_DataChangeCheck
// 내용        : dataset 변경여부 체크
-----------------------------------*/
scwin.lfn_DataChangeCheck = function(obj, newObj) {

    newObj.ClearData();

    var cnt = 0;
    for (var i = 0; i < obj.GetCountNF(); i++) {
        if (obj.GetColumnNF(i, "chk") == "1") {
            newObj.InsertRow(cnt);
            for (var j = 0; j < obj.GetColCount(); j++) {
                newObj.SetColumn(cnt, obj.GetColID(j), obj.GetColumnNF(i, obj.GetColID(j)));
            }
            cnt++;
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_Close_OnClick
// 내용        : 팝업닫기
//---------------------------------------------------------------------------
scwin.lfn_Close_OnClick = function(obj) {
    wprom.close("");
};
