head.script:
//**************************************************************************************************
//  포인트결제 radio 버튼 클릭시, 결제예정액을 계산한다. calcSetTotAmt
//**************************************************************************************************

scwin.sSetPointYN_OnClick = function(info) {
    //포인트결제 '예'인 경우, 결제예정액을 계산한다.
    if (sSetPointYN.getValue() == "Y") {
        var nAvailPoint = 0;

        //2009-09-03 서영지. 비씨카드인 경우 카드승인금액의 20%의 포인트만 사용 가능하다. 
        //단, 잔여포인트가 20%보다 적은 경우, 잔여포인트를 모두 사용한다.
        if (sCardCom.getValue() == "0001") {
            var nTempAmt = Math.floor(wconv.toNumber(nReqAmt.getValue()) * 20 / 100);
            if (wconv.toNumber(sPointScore.getValue()) > nTempAmt) {
                nAvailPoint = nTempAmt;
            } else {
                nAvailPoint = sPointScore.getValue();
            }
        } else {
            //포인트가 승인금액보다 크면 승인금액 = 포인트사용액
            if (wconv.toNumber(sPointScore.getValue()) > wconv.toNumber(nReqAmt.getValue())) {
                nAvailPoint = nReqAmt.getValue();
            } else {
                nAvailPoint = sPointScore.getValue();
            }
        }



        //2008-09-29 서영지 추가. '5천점 단위로 사용'이 '전액사용'으로 변경됨.
        sRemainPoint.setValue(wconv.toNumber(sPointScore.getValue()) - wconv.toNumber(nAvailPoint)); //잔여포인트 = 포인트점수 - 사용가능포인트점수
        sPointReqAmt.setValue(nReqAmt.getValue()); //카드승인액 <- 승인금액
        sSetPointAmt.setValue(nAvailPoint); //포인트사용 <- 사용가능포인트점수	
        sSetCardAmt.setValue(wconv.toNumber(nReqAmt.getValue()) - wconv.toNumber(nAvailPoint)); //결제예정액 <- 승인금액 - 사용가능포인트점수		
    } else if (sSetPointYN.getValue() == "N") {
        sRemainPoint.setValue(sPointScore.getValue());
        sPointReqAmt.setValue(""); //카드승인액
        sSetPointAmt.setValue(""); //포인트사용
        sSetCardAmt.setValue(""); //결제예정액
        sSetPointYN.setValue("N"); //포인트결재여부	
    }


    //2010-11-05 서영지. 하나은행 지원금 관련 추가. 지원금내역 계산
    scwin.calcSupportAmt();
};

//**************************************************************************************************
//  포인트를 조회한다.
