//**************************************************************************************************
//  삼성카드의 경우, 주민번호 변경시 포인트 내역을 clear 한다.
//**************************************************************************************************
scwin.sCardOwnerID_OnChanged = function() {
    scwin.initSavePoint();
    scwin.checkSupportYN(); //2010-11-05 서영지 하나은행지원금 관련 추가.
};

scwin.initSavePoint = function() {
    //2009-08-06 서영지. 현대카드(0008) 추가	
    //2009-09-21 서영지. 신한하이세이브 추가
    //롯데카드 세이브 추가, 2016-02-29 이재정 추가.
    if (sCardCom.getValue() == "0004" || sCardCom.getValue() == "0008" ||         sCardCom.getValue() == "0009" || 
     (((sCardCom.getValue() == "0010"     || sCardCom.getValue() == "0005")) && (sPointCardType.getValue() == "40"))) {
        sSetPointYN.setValue( "N"); //포인트결제
        sPointScore.setValue( ""); //포인트점수
        sRemainPoint.setValue( ""); //잔여포인트점수
        sPointReqAmt.setValue( ""); //카드승인액
        sSetPointAmt.setValue( ""); //포인트사용
        sSetCardAmt.setValue( ""); //결제예정액
        sSetPointYN.setDisable(true);
    }
};

//**************************************************************************************************
//  신한카드인 경우, 포인트구분을 선택할 수 있도록 enable된다. 
//  이때 포인트구분이 변경되는 경우, 이 함수를 호출하여 메세지와 타이틀을 바꾼다.
//**************************************************************************************************
scwin.sPointCardType_OnChanged = function(obj, strCode, strText, nOldIndex, nNewIndex) {
    scwin.changePointCardType();
};

scwin.changePointCardType = function() {
    //메세지 부분 초기화
    sTypeMsg1.setValue("");
    sTypeMsg2.setValue("");
    sTypeMsg3.setValue("");
    sTypeMsg4.setValue("");
    sTypeMsg5.setValue("");

    //포인트부분 초기화
    sEduCardYN.setSelectedIndex( 1 );// .Index = 1; //에듀카드여부
    sSetPointYN.setSelectedIndex( 1 ); //포인트결제
    sPointScore.setValue(""); //포인트점수
    sRemainPoint.setValue(""); //잔여포인트점수
    sCardName.setValue(""); //카드발급사명
    sPointReqAmt.setValue(""); //카드승인액
    sSetPointAmt.setValue(""); //포인트사용
    sSetCardAmt.setValue(""); //결제예정액
    sSetPointYN.setDisable(true);//Enable = false;
    if (sCardCom.getValue() == "0010" || sCardCom.getValue() == "0005") {
        if (sPointCardType.value == "20") {
            sSubTitle.setValue( "마이" + sCardComName.getValue() + "포인트" ); //타이틀
            sTypeMsg1.setValue( "\u203B 마이신한포인트 사용고객은 포인트결제 여부를  반드시 선택해야 합니다.");
            sTypeMsg2.setValue( "\u203B 포인트결제는 1점 이상 단위로 조회된 포인트 점수 모두 사용만 가능합니다.(포인트 일부 사용 선택불가)");
            sTypeMsg3.setValue( "\u203B 마이신한포인트 점수는 1점에 1원으로 적용됩니다.");
            sTypeMsg4.setValue( "\u203B 신한하이세이브와 마이신한포인트는 동시 사용 불가합니다.");
        } else if (sPointCardType.getValue() == "40") {
            sSubTitle.setValue( sCardComName.getValue() + "하이세이브"); //타이틀
            sTypeMsg1.setValue( "\u203B 신한하이세이브 사용고객은 포인트결제 여부를 반드시 선택해야 합니다.");
            sTypeMsg2.setValue( "\u203B 신한카드로 10만원 이상 결제시(신한BC 제외), 당사는 10만원만 세이브 적용 가능합니다.");
            sTypeMsg3.setValue( "   (세이브 적용된 10만원은 3개월 거치 후 9개월간 할부 방식으로 포인트 결제됩니다)");
            sTypeMsg4.setValue( "\u203B 신한카드 세이브이용 보험료 결제 건은 세이브포인트가 적립되지 않습니다.");
            sTypeMsg5.setValue( "\u203B 신한하이세이브와 마이신한포인트는 동시 사용 불가합니다.");
        }
    }
};

//**************************************************************************************************
// 2010-11-05 서영지 하나은행지원금 관련 추가.
// 지원금사용 radio 버튼 클릭시, 지원후결제예정액을 계산한다.
//**************************************************************************************************
scwin.sSupportUseYN_OnClick = function(e) {
    scwin.calcSupportAmt();
};

scwin.calcSupportAmt = function() {
    //지원금사용 '예'인 경우, 지원후결제예정액을 계산한다.
    if (sSupportUseYN.getValue() == "Y") {
        var nReqAmt = wconv.toNumber(nReqAmt.getValue()); //승인요청액
        var sSetPointAmt = wconv.toNumber(sSetPointAmt.getValue()); //포인트사용액
        var sSetCardAmt = wconv.toNumber(sSetCardAmt.getValue()); //결제예정액
        var nSupportAmt = wconv.toNumber(nSupportAmt.getValue()); //지원금액
        var nSetSupportAmt = 0; //지원금사용금액
        var nFinalCardAmt = 0; //지원후결제예정액
        //포인트결제인 경우,
        if (sSetPointYN.value == "Y") {
            if (sSetCardAmt > nSupportAmt) {
                nSetSupportAmt = nSupportAmt; //결제예정액이 지원금보다 많은 경우, 지원금사용금액 = 지원금액
                nFinalCardAmt = eval(sSetCardAmt) - eval(nSetSupportAmt); //지원후결제예정액 = 결제예정액 - 지원금사용금액
            } else {
                nSetSupportAmt = sSetCardAmt; //결제예정액이 지원금보다 적은 경우, 지원금사용금액 = 결제예정액
                nFinalCardAmt = 0;
            }
        } else {
            if (nReqAmt > nSupportAmt) {
                nSetSupportAmt = nSupportAmt; //승인요청액이 지원금보다 많은 경우, 지원금사용금액 = 지원금액
                nFinalCardAmt = eval(nReqAmt) - eval(nSetSupportAmt); //지원후결제예정액 = 승인요청액 - 지원금사용금액
            } else {
                nSetSupportAmt = nReqAmt; //승인요청액이 지원금보다 적은 경우, 지원금사용금액 = 승인요청액
                nFinalCardAmt = 0;
            }
        }
        nSetSupportAmt.setValue( nSetSupportAmt ); //지원금사용금액
        nFinalCardAmt.setValue( nFinalCardAmt); //지원후결제예정액
    } else if (sSupportUseYN.value == "N") {
        nSetSupportAmt.setValue( 0 ); //지원금사용금액 
        nFinalCardAmt.setValue( 0 ); //지원후결제예정액 
    }
};

