scwin.sInsGrp_OnClick = function(obj) {
    //지원금 조회 함수를 호출한다.
    if (obj.getValue() == "0") {
        scwin.checkSupportYN();
    } else {
        scwin.initSupport();
    }


    //상품구분이 장기인 경우 포인트부분을 disable 한다. 2011-01-25 서영지
    if (sInsGrp.getValue() == "2") {
        scwin.initBCPoint();
        sMsgText.setValue( "" );
    }
};

