//	###################################################

var pchkflag = "";
var sAccidentNo = "";
var sHndCover = "";
var sDmgeNo = "";
var targetCode = "";

var parent_cmb_ConfirmType;			// 부모창 결재구분 콤보 Set 
var parent_nInsDcSeq;		// 부모창 getCellData Set
var parent_nPaySeq;		// 부모창 nPaySeq Set 

//**************************************************************************
// 폼 로딩시 초기화
//**************************************************************************
scwin.init = function() {
   console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [TEST_01]" );   
  //console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [parent_nInsDcSeq]"+ parent_nInsDcSeq +"[parent_nPaySeq]"+ parent_nPaySeq );  
  //console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [parent_cmb_ConfirmType]"+ parent_cmb_ConfirmType );  

  pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>
		  console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [TEST_02]" );   

		var indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata); 	// 부모창에서 받아온 값( 예) inJSON.sMyPlan)

		if (!util.isNull(inJSON.sAccidentNo)) 	sAccidentNo = inJSON.sAccidentNo;
		if (!util.isNull(inJSON.sHndCover)) 	sHndCover = inJSON.sHndCover;
		if (!util.isNull(inJSON.sDmgeNo)) 	sDmgeNo = inJSON.sDmgeNo;
		if (!util.isNull(inJSON.targetCode)) 	targetCode = inJSON.targetCode;
		  console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [TEST_03]" );   

		parent_cmb_ConfirmType = wprom.getParent().cmb_ConfirmType.getValue();			// 부모창 결재구분 콤보 Set 
		parent_nInsDcSeq = wprom.getParent().Ds_GENDA32.getCellData(0, "nInsDcSeq");		// 부모창 getCellData Set
		parent_nPaySeq = wprom.getParent().Ds_GENDA32.getCellData(0, "nPaySeq");		// 부모창 nPaySeq Set 
		console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [sAccidentNo]"+ sAccidentNo +"[sHndCover]"+ sHndCover +"[sDmgeNo]"+ sDmgeNo +"[targetCode]"+ targetCode );  

		scwin.form_ReLoad();
		pchkflag = chkflag;

		return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); // =================================================>
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	//pro = pro.catch(function(rj) {
		//util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	//});

	return pro; 
    //console.log("[/CBSC150P.xml] [init()] ==> [폼 로딩] [TEST_end]" );
};

scwin.form_ReLoad = function() {
	//console.log("[/CBSC150P.xml] [form_ReLoad()] ==> [로딩] [TEST_01]" );   

	//Rdo_adjustType.setValue("1");
	//scwin.getAdjustList();
	//console.log("[/CBSC150P.xml] [form_ReLoad()] ==> [로딩] [TEST_02]" );   

	console.log("[/CBSC150P.xml] [targetCode()] ==> [조회] [TEST_01] [targetCode]"+ targetCode ); 

    if (wconv.length(targetCode) == 0) {
        Rdo_adjustType.setValue("2");
        scwin.getAdjustList();

    } else {
        Rdo_adjustType.setValue("1");
        scwin.getAdjustList();
    }
};

/**
 * @tab
 * @author         김현수
 * @function       getAdjustList
 * @desc           정산구분코드에 해당하는 목록을 조회한다.
 * @param			value : 정산구분코드 (1:미지급, 2:예수금)
 **/
 scwin.getAdjustList = function() {
	console.log("[/CBSC150P.xml] [getAdjustList()] ==> [조회] [TEST_01] [Rdo_adjustType]"+ Rdo_adjustType.getValue() ); 

	adType = Rdo_adjustType.getValue();	// 정산구분 Radio

    if (adType == "1") {		// 미지급 이면 ==>  정산구분코드(1:미지급, 2:예수금)
        if (targetCode == "") {
            wprom.alert("지급처 ID를 입력하세요");
            return;
        }

		var MethodObj = new callService("com.inswave.longterm.cla.ins.task.CIC11441200sTask", "getNotPaymentList");		// 정산과목[미지급] 조회 Obj
		MethodObj.setId("getNotPaymentList");	// ID 셋팅  
		MethodObj.setParam("sAccidentNo", sAccidentNo);	// Param 셋팅(설계번호)  
		MethodObj.setParam("sHndCover", sHndCover);	// Param 셋팅(설계번호) 
		MethodObj.setParam("sDmgeNo", sDmgeNo);	// Param 셋팅(설계번호)  
		MethodObj.setParam("targetCode", targetCode);	// Param 셋팅(설계번호) 
		MethodObj.setParam("sApprovalType", parent_cmb_ConfirmType);	// Param 셋팅(결재구분 코드)  
		MethodObj.setParam("sDcNo", parent_nInsDcSeq);	// Param 셋팅(설계번호) 
		MethodObj.setParam("sPayNo", parent_nPaySeq);	// Param 셋팅(설계번호)
		MethodObj.setUrl("/contract/CBSC150PController.do");  // 서버단 호출 URL  
		MethodObj.setXPath("//*/ACCAD03");					// XPath(Ds_LTIDA00)
		MethodObj.addXPath("//*/CLMCE01");		// XPath(Ds_Pibo)  
		MethodObj.addXPath("//*/LTIDA15");		// XPath(Ds_LTIDA15) 	  
		MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
		MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.setXPathDs();
		MethodObj.send();		// 최종 전송 
		console.log("[/CBSC150P.xml] [getAdjustList()] ==> [01. 정산과목[미지급] 조회, 전송_완료] [TEST_91]");

		util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_LTIDA00.setJSON(_tranSync.getDsJson("//*/ACCAD03"));				// 보험 기본사항 Ds 셋팅
		Ds_CLMCE01.setJSON(_tranSync.getDsJson("//*/CLMCE01"));			// 피보험자 정보 Ds 셋팅  
		DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)     
		console.log("[/CBSC150P.xml] [getAdjustList()] ==> [Ds_CLMCE01_갯수]"+ Ds_CLMCE01.getRowCount() +"[Ds_CLMCE01]"+ WebSquare.xml.serialize(Ds_CLMCE01.getAllXML()) );  

	} else if (adType == "2") {		// 예수금 이면
		var MethodObj = new callService("com.inswave.longterm.cla.ins.task.CIC11441200sTask", "getDepositList");		// 정산과목[예수금] 조회 Obj
		MethodObj.setId("getDepositList");	// ID 셋팅   
		MethodObj.setParam("sAccidentNo", sAccidentNo);	// Param 셋팅(설계번호)  
		MethodObj.setParam("sHndCover", sHndCover);	// Param 셋팅(설계번호) 
		MethodObj.setParam("sDmgeNo", sDmgeNo);	// Param 셋팅(설계번호)  
		MethodObj.setParam("targetCode", targetCode);	// Param 셋팅(설계번호)  
		MethodObj.setUrl("/contract/CBSC150PController.do");  // 서버단 호출 URL  
		MethodObj.setXPath("//*/ACCAD03");					// XPath(Ds_LTIDA00)
		//MethodObj.addXPath("//*/CLMCE01");		// XPath(Ds_Pibo)  
		MethodObj.addXPath("//*/LTIDA15");		// XPath(Ds_LTIDA15) 	  
		MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
		MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
		MethodObj.sync();		// 동기 처리[기본(async:비동기)]
		MethodObj.setXPathDs();
		MethodObj.send();		// 최종 전송 
		console.log("[/CBSC150P.xml] [getAdjustList()] ==> [02. 정산과목[예수금] 조회, 전송_완료] [TEST_91]");

		util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		Ds_LTIDA00.setJSON(_tranSync.getDsJson("//*/ACCAD03"));				// 보험 기본사항 Ds 셋팅
		//Ds_CLMCE01.setJSON(_tranSync.getDsJson("//*/CLMCE01"));			// 피보험자 정보 Ds 셋팅  
		DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)     
		console.log("[/CBSC150P.xml] [getAdjustList()] ==> [Ds_LTIDA00_갯수]"+ Ds_LTIDA00.getRowCount() +"[Ds_LTIDA00]"+ WebSquare.xml.serialize(Ds_LTIDA00.getAllXML()) );  

	  	if (Ds_Warning.getRowCount() != 0) {
	        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
	        return;
	    } 

 	} else {	// 강제정산 이면
		scwin.setAdjustItems();
	} 	

	console.log("[/CBSC150P.xml] [getAdjustList()] ==> [조회] [TEST_end]" ); 
};

/**
 * @tab
 * @author         김현수
 * @function       setAdjustItems
 * @desc           정산유형에 해당하는 값을 호출한 페이지에 전달한다.
 **/
scwin.setAdjustItems = function() {

    if (Rdo_adjustType.getValue() == "3") {		// 강제정산 이면
        parent_lfn_setForceAccount(idx);

    } else {
        if (Rdo_adjustType.getValue() == "1") {		// 미지급 이면 ==>  정산구분코드(1:미지급, 2:예수금)
            var check = false;
            for (i = 0; i < Ds_CLMCE01.getRowCount(); i++) {
                if (Ds_CLMCE01.getCellData(i, "adItem") == "1") {
                    check = true;
                }
            }
            if (check) {
                var listCount = Ds_CLMCE01.getRowCount();

                for (var i = 0; i < listCount; i++) {
                    if (Ds_CLMCE01.getCellData(i, "adItem") == "1") {
                        sAccidentNo = Ds_CLMCE01.getCellData(i, "sAccidentNo");
                        sHndCover = Ds_CLMCE01.getCellData(i, "sHndCover");
                        sDmgeNo = Ds_CLMCE01.getCellData(i, "sDmgeNo");
                        sDcNo = Ds_CLMCE01.getCellData(i, "sDcNo");
                        sInputSerNo = Ds_CLMCE01.getCellData(i, "sInputSerNo");
                        sPayNo = Ds_CLMCE01.getCellData(i, "sPayNo");
                        sPaymentTargetName = Ds_CLMCE01.getCellData(i, "sPaymentTargetName");
                        sPaymentTargetCode = Ds_CLMCE01.getCellData(i, "sPaymentTargetCode");
                        nPaymentAmt = Ds_CLMCE01.getCellData(i, "nPaymentAmt");
                        parent_lfn_setNotPaymentList(sDcNo, sInputSerNo, sPayNo, sPaymentTargetName, sPaymentTargetCode, nPaymentAmt, idx);
                    }
                }
            }
        } else if (Rdo_adjustType.getValue() == "2") {	// 예수금 이면 ==>  정산구분코드(1:미지급, 2:예수금)
            var check = false;
            for (i = 0; i < Ds_ACCAD03.getRowCount(); i++) {
                if (Ds_ACCAD03.getCellData(i, "adItem") == "1") {
                    check = true;
                }
            }
            if (check) {
                var sVirtualAcctNo;
                var sBankCode;
                var sDepoSer;
                var nBalAmt;
                var sDealDate;
                var listCount = Ds_ACCAD03.getRowCount();
                for (var i = 0; i < listCount; i++) {
                    if (Ds_ACCAD03.getCellData(i, "adItem") == "1") {
                        sVirtualAcctNo = Ds_ACCAD03.getCellData(i, "sVirtualAcctNo");
                        sBankCode = Ds_ACCAD03.getCellData(i, "sBankCode");
                        sDepoSer = Ds_ACCAD03.getCellData(i, "sDepoSer");
                        nBalAmt = Ds_ACCAD03.getCellData(i, "nBalAmt");
                        sDealDate = Ds_ACCAD03.getCellData(i, "sDealDate");
                    }
                }
                if (eval(nBalAmt) <= 0) {
                    wprom.alert("선택할 수 없습니다.");
                    return;
                }
                parent_lfn_setDepositList(sVirtualAcctNo, sBankCode, sDepoSer, nBalAmt, sDealDate, idx);
            }
        }
    }

    wprom.close("");
};

/************************************************
정산구분 : 미지급의 Grid0 OnCellClick 이벤트 
***********************************************/
scwin.Grid0_OnCellClick = function(obj, nRow, nCell, nX, nY, nPivotIndex) {

    if (nCell != 0)
        return;
    if (nCell == 0) {
        for (var i = 0; i < Ds_CLMCE01.getRowCount(); i++) {
            if (nRow == i) {
                Ds_CLMCE01.setCellData(i, "adItem", "Y");
            } else if (nRow != i) {
                Ds_CLMCE01.setCellData(i, "adItem", "");
            }
        }
    } else {
        return;
    }
};

/************************************************
정산구분 : 예수금의 Grid1 OnCellClick 이벤트 
***********************************************/
scwin.Grid1_OnCellClick = function(obj, nRow, nCell, nX, nY, nPivotIndex) {

    if (nCell != 0)
        return;
    if (nCell == 0) {
        for (var i = 0; i < Ds_ACCAD03.getRowCount(); i++) {
            if (nRow == i) {
                Ds_ACCAD03.setCellData(i, "adItem", "Y");
            } else if (nRow != i) {
                Ds_ACCAD03.setCellData(i, "adItem", "");
            }
        }
    } else {
        return;
    }
};
