var fv_args;

//---------------------------------------------------------------------------
// function 명 : B160_OnLoadCompleted()
// 내용        : 화면오픈시 처리
//---------------------------------------------------------------------------
scwin.init = function() {
	wprom.windowOpenInit(B300_Pop);
	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
		//var parentWin = wprom.getParamObject(); 
	} catch(e) {}

    gfn_getCommonCodes( "DsWs_SsrtFlgcd DsWs_DgnsKindCd DsWs_DgnsFlagCd DsWs_HospCd"
                                    , "SsrtFlgcd DgnsKindCd DgnsFlagCd HospCd"
                                    , " "
                                    , " ");
    medt_PlanNo.setValue(fv_args.sPlanNo);
    //medt_PlanNo.Value = "M20160000113";
    if (!mfn_IsEmpty(medt_PlanNo.getValue())) {
        scwin.lfn_Search();
    } else {
        medt_PlanNo.focus();
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_search()
// 내용        : 진단대상건조회
//---------------------------------------------------------------------------
//비동기 호출 
scwin.lfn_Search = function() {
	util.eventAsyncCall(this, scwin.lfn_Search_sync, arguments);
}
scwin.lfn_Search_sync = function() {
    var sStr;

    //데이터셋 초기화
    wconv.clear(DsWs_LTIDA03);
    wconv.clear(DsWs_Warning);
    /*
    SOBJ_reqMain.WSDL = gds.gWsdlBasis; //기본 웹서비스
    var MethodObj = gfn_CreateMethodObj(SOBJ_reqMain, "com.inswave.longterm.ssr.sst.task.DBB300Task", "selectDgns", "", "");
    //gfn_SetParam(MethodObj, "gubun", "8");	
    sStr = medt_PlanNo.getValue();
    if (mfn_IsEmpty(sStr)) {
        wprom.alert("청약번호를 입력하세요");
        return;
    }
    gfn_SetParam(MethodObj, "sPlanNo", sStr);
    var result = gfn_CallService(SOBJ_reqMain, DOBJ_reqMain, MethodObj);
    */

	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB300Task", "selectDgns");
	MethodObj.setId("selectDgns");
    sStr = medt_PlanNo.getValue();
    if (mfn_IsEmpty(sStr)) {
        wprom.alert("청약번호를 입력하세요");
        return;
    }
    MethodObj.setParam( "sPlanNo", sStr);	
	MethodObj.setUrl("/contract/CBSC155PController.do");
	MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA03List']/vector/data/LTIDA03");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	util.jSonDsCreate(_tranSync.getDsJson("LTIDA03"), "DsWs_LTIDA03");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
	//util.setCompBindRefresh("Ds_LTIDA13");
	util.setRowPosition(DsWs_LTIDA03, 0);

    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    } else {
        if (DsWs_LTIDA03.getRowCount() == 0) {
            wprom.alert("진단대상건이 없습니다.");
        } else {
            wconv.copy(Ds_LTIDA13, DsWs_LTIDA03);
			util.setRowPosition(Ds_LTIDA13, 0);
            console.log("sDgnsProgStndCd : " + Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd"));
            if (Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "" || Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "00") {
                Ds_LTIDA13.setCellData(0, "sRqstYn", "1");
                Ds_LTIDA13.setCellData(0, "sSsrtFlgcd", "01");
                Ds_LTIDA13.setCellData(0, "sDgnsKindCd", "01");
                Ds_LTIDA13.setCellData(0, "sDgnsFlagCd", "01");
                Ds_LTIDA13.setCellData(0, "sHospCd", "01");
            }


            //진단요청건일경우 요청버튼 Disable
            if (Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "" || Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "00") {
                //신규,취소
                btn_DgnsRqst.setDisabled(false);
                btn_RqstCncl.setDisabled(true);
            } else if (Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "60") {
                //신청완료
                btn_DgnsRqst.setDisabled(true);
                btn_RqstCncl.setDisabled(true);
            } else {
                btn_DgnsRqst.setDisabled(true);
                if (Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "99") {
                    //업체미신청상태일때만 취소가능
                    //장기보험 인수권한
                    if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "22040")) {
                        btn_RqstCncl.setDisabled(false);
                    } else {
                        btn_RqstCncl.setDisabled(true);
                    }
                } else {
                    btn_RqstCncl.setDisabled(true);
                }
            }
        }
    }
	//goo 그리드 색깔 변경
    scwin.lf_grdColorChanged();
};
//그리드 색깔 변경
scwin.lf_grdColorChanged = function()
{
    gidAddInfo.setColumnBackgroundColor(0,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(3,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(9,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(13,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(16,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(22,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(26,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(29,"#E3F0FF");
    gidAddInfo.setColumnBackgroundColor(35,"#E3F0FF");
    grdDgns.setColumnBackgroundColor(0,"#E3F0FF");
    grdDgns.setColumnBackgroundColor(2,"#E3F0FF");
    grdDgns.setColumnBackgroundColor(4,"#E3F0FF");
    grdDgns.setColumnBackgroundColor(6,"#E3F0FF");
    grdDgns.setColumnBackgroundColor(8,"#E3F0FF");
};
