//---------------------------------------------------------------------------
// function 명 : btn_DgnsRqst_OnClick()
// 내용        : 진단요청버튼 클릭시 처리
//---------------------------------------------------------------------------
scwin.btn_DgnsRqst_OnClick = function(e) {
console.log("===============================btn_DgnsRqst_OnClick");
console.log(e);
    //wconv.trace(Ds_LTIDA13.getCellData(0, "sHomeChk"));
    //wconv.trace(Ds_LTIDA13.getCellData(0, "sWpcChk"));
    //wconv.trace(Ds_LTIDA13.getCellData(0, "sEtcChk"));
    var sDgnsProgStndCd = Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd");
    if (sDgnsProgStndCd != "" && sDgnsProgStndCd != "99" && sDgnsProgStndCd != "00" && sDgnsProgStndCd != "60") {
        wprom.alert("진단진행중인 대상입니다.");
        return false;
    }
    var sPiChk = false;
    for (var i = 0; i < Ds_LTIDA13.getRowCount(); i++) {
        if (Ds_LTIDA13.getCellData(i, "sRqstYn") == "1") {
            sPiChk = true;
        }
    }
    if (sPiChk == false) {
        wprom.alert("피보험자를 선택하세요.");
        return false;
    }
    if (Ds_LTIDA13.getCellData(0, "sHomeChk") == "0" && Ds_LTIDA13.getCellData(0, "sWpcChk") == "0" && Ds_LTIDA13.getCellData(0, "sEtcChk") == "0") {
        wprom.alert("주소를 선택하세요.");
        return false;
    }
    /*
    SOBJ_Save.WSDL = gds.gWsdlBasis;
    DsWs_Warning.DataObject = "DOBJ_Save";
    var MethodObj = SOBJ_Save.CreateMethodObj("perform");
	*/
    util.insertColumn(Ds_LTIDA13, [        [            "sStatus",            "sStatus",            "text"        ]    ]);
    for (var i = 0; i < Ds_LTIDA13.getRowCount(); i++) {
        if (Ds_LTIDA13.getOriginalCellData(i, "sRqstYn") == "0" && Ds_LTIDA13.getCellData(i, "sRqstYn") == "1") {
            Ds_LTIDA13.setCellData(i, "sStatus", "I");
            Ds_LTIDA13.setCellData(i, "sRqstDate", wconv.substr(gfn_GetSysTime(), 0, 8));
            Ds_LTIDA13.setCellData(i, "sDgnsProgStndCd", "99");
            Ds_LTIDA13.setCellData(i, "sInptDler", util.getGdsValue("gUserID"));
            Ds_LTIDA13.setCellData(i, "sMdfcDler", util.getGdsValue("gUserID"));
        } else if (Ds_LTIDA13.getRowStatus(i) == "update" || Ds_LTIDA13.getCellData(0, "sDgnsProgStndCd") == "00") {
            //취소건 재신청
            Ds_LTIDA13.setCellData(i, "sStatus", "U");
            Ds_LTIDA13.setCellData(i, "sRqstDate", wconv.substr(gfn_GetSysTime(), 0, 8));
            Ds_LTIDA13.setCellData(i, "sDgnsProgStndCd", "99");
            Ds_LTIDA13.setCellData(i, "sMdfcDler", util.getGdsValue("gUserID"));
        } else {
            Ds_LTIDA13.setCellData(i, "sStatus", "N");
        }
    }
    /*
    setHeader("hashtable", "com.inswave.longterm.ssr.sst.task.DBB300Task", "insertDgns");
    addVectorData("LTIDA13", "LTIDA13", Ds_LTIDA13);
    setTail("hashtable");
    SOBJ_Save.CreateObj("in0");
    MethodObj.in0 = insertStr;
    var result = gfn_CallService(SOBJ_Save, DOBJ_Save, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB300Task", "insertDgns");
	MethodObj.setId("insertDgns");
	MethodObj.addVectorData("LTIDA13", "LTIDA13", "Ds_LTIDA13");
	MethodObj.setUrl("/contract/CBSC155PController.do");  	// 서버단 호출 URL (수납 확정)    
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.setHashTable();   	//  전문의 앞이 hashtable로 보낼 때
	MethodObj.sync();					// 동기 처리[기본(async:비동기)]
	MethodObj.send(); 					// 최종 전송 		// TEST @@@ ===>
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return false;
    } else {
        wprom.alert("진단요청처리 되었습니다.");
        wprom.close(); //"진단요청 신청이 완료되었습니다."
    }
};

