//---------------------------------------------------------------------------
// function 명 : Ds_LTIDA13_OnColumnChanged()
// 내용        : 
//---------------------------------------------------------------------------
scwin.Ds_LTIDA13_OnColumnChanged = function(info) {
console.log(info);
	var nRow = info.rowIndex;

    if (info.colID == "sHomeChk" && info.newValue == "1") {
        Ds_LTIDA13.setCellData(nRow, "sWpcChk", "0");
        Ds_LTIDA13.setCellData(nRow, "sEtcChk", "0");
        Ds_LTIDA13.setCellData(nRow, "sAddrFlgcd", "1");
    }
    if (info.colID == "sWpcChk" && info.newValue == "1") {
        Ds_LTIDA13.setCellData(nRow, "sHomeChk", "0");
        Ds_LTIDA13.setCellData(nRow, "sEtcChk", "0");
        Ds_LTIDA13.setCellData(nRow, "sAddrFlgcd", "2");
    }
    if (info.colID == "sEtcChk" && info.newValue == "1") {
        Ds_LTIDA13.setCellData(nRow, "sHomeChk", "0");
        Ds_LTIDA13.setCellData(nRow, "sWpcChk", "0");
        Ds_LTIDA13.setCellData(nRow, "sAddrFlgcd", "3");
    }
};

//---------------------------------------------------------------------------
// function 명 : gidAddInfo_OnCellClick()
// 내용        : 
//---------------------------------------------------------------------------
scwin.gidAddInfo_OnCellClick = function( nRow, nCell) {
    switch (nCell) {
        case 9:
            if (Ds_LTIDA13.getCellData(nRow, "sHomeZip") == "") {
                //gidAddInfo.SetCellProp("Body", nCell, "Edit", "none");
                gidAddInfo.setColumnDisabled(nCell,true);
		        Ds_LTIDA13.setCellData(nRow, "sHomeChk", "0");
            } else {
                //gidAddInfo.SetCellProp("Body", nCell, "Edit", "checkbox");
                gidAddInfo.setColumnDisabled(nCell,false);
            }
            break;
        case 21:
            if (Ds_LTIDA13.getCellData(nRow, "sWpcZip") == "") {
                //gidAddInfo.SetCellProp("Body", nCell, "Edit", "none");
                gidAddInfo.setColumnDisabled(nCell,true);
		        Ds_LTIDA13.setCellData(nRow, "sWpcChk", "0");
            } else {
                //gidAddInfo.SetCellProp("Body", nCell, "Edit", "checkbox");
                gidAddInfo.setColumnDisabled(nCell,false);
            }
            break;
        case 34:
            if (Ds_LTIDA13.getCellData(nRow, "sEtcZip") == "") {
                //gidAddInfo.SetCellProp("Body", nCell, "Edit", "none");
                gidAddInfo.setColumnDisabled(nCell,true);
		        Ds_LTIDA13.setCellData(nRow, "sEtcChk", "0");
            } else {
                //gidAddInfo.SetCellProp("Body", nCell, "Edit", "checkbox");
                gidAddInfo.setColumnDisabled(nCell,false);
            }
            break;
        default:
            break;
    }
};

