/****************************************************************************
 * function 명 : lfn_Check_Validation
 * 내용        : 필수 입렵 체크
 ****************************************************************************/
scwin.lfn_Check_Validation = function() {
    var bResult = true;
    var sBunsMetdDocuClsfCd = parent.Ds_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd");
    var sBunsMetdClsfCd = parent.Ds_LTIDA00.getCellData(0, "sBunsMetdClsfCd");
    if (sBunsMetdDocuClsfCd == "3" || sBunsMetdClsfCd == "1") {
        wprom.alert("재물보험이 존재합니다.\n재물보험Tap에서 인수승인 가능합니다.");
        bResult = false;
    } else if (txa_approval.getValue() == "" || txa_approval.getValue() == null) {
        wprom.alert("승인자의견을 입력하여 주십시요");
        bResult = false;
    } else if (rdo_ConuResult.getValue() == "11" || util.isNull(rdo_ConuResult.getValue()) ) {
        wprom.alert("승인여부를 입력하여 주십시요");
        bResult = false;
    } else if (rdo_ConuResult.getValue() == "03" && util.isNull(cmb_UdrtkRjResn.getValue()) || 
                rdo_ConuResult.getValue() == "03" && util.isNull(cmb_UdrtkRjResn.getValue()) ) {
        wprom.alert("거절사유를 선택하여 주십시오.");
        bResult = false;
    }
    return bResult;
};

/****************************************************************************
 * function 명 : lfn_Grd_PrprtList_OnCellClick
 * 내용        : 그리드 클릭 이벤트. 
 ****************************************************************************/
scwin.lfn_Grd_PsnList_OnCellClick = function( nRow, nCell) {
    var promise = Promise.resolve();
    if (nCell == "0" || nCell == "1" || nCell == "2" || nCell == "3") {
        scwin.lfn_search(nRow);
    }

    //피보험자고지사항
    if (nCell == "4") {
        var arg = {};
        arg.sRqstYn=Ds_LTIDA00.getCellData(nRow, "sRqstYn"); //고지사항입력여부
        if (wconv.length(parent.DsWs_PrdAttr.getCellData(0, "NotcGdFlag")) > 0) //
        {
            arg.sNotcGdFlag=parent.DsWs_PrdAttr.getCellData(0, "NotcGdFlag");
        } else {
            arg.sNotcGdFlag="00"; //고지상품구분(운전구분을 고지상품구분으로 대체)
        }

        arg.sInrpsCd=Ds_LTIDA00.getCellData(nRow, "sInrpsCd"); //피보험자코드
        arg.sInrpsName=Ds_LTIDA00.getCellData(nRow, "sInrpsName"); //성명
        arg.sCrtorRelnName=grd_PsnList.getCellDisplayData("Body", nRow, 5); //관계명
        //arg += " sCrtorRelnName="+quote(Ds_LTIDA00.getCellData(nRow, "sMinsrRelnCd"));//관계명
        arg.sPlanNo=parent.medt_PlanNo.getValue(); //설계번호
        arg.sPsnGroupFlgcd=Ds_LTIDA00.getCellData(0, "sPsnGroupFlgcd"); //개인단체구분
        arg.nPsnGroupSeqno=Ds_LTIDA00.getCellData(nRow, "nInrpsSeqno"); //seq
        arg.sSsrtDate=Ds_LTIDA00.getCellData(0, "sSsrtDate"); //청약일자
        arg.sDrveFlagCd=Ds_LTIDA00.getCellData(nRow, "sDrveFlagCd"); //운전구분
        arg.sJobName=Ds_LTIDA00.getCellData(nRow, "sJobName"); //운전구분
        arg.sOnlySrarch = sOnlySrarch;
        //저축상품 여부
        var sSvtYn = "N";
        if (parent.DsWs_PrdAttr.getCellData(0, "GdFlgcd") == "2") {
            sSvtYn = "Y";
        }
        arg.sSvtYn=sSvtYn;
console.log(    arg);
        var pObj = {
        	"Ds_LTIDA00": Ds_LTIDA00,
        	"Ds_Pibo":Ds_Pibo        	
        };
        promise = promise.then(function(rtnVal) {
        	var popInfo = {
       			title : "피보험자고지사항",
				width : 875,
				height : 869,        	
        		indata : arg,
        		"parentObject":pObj
        	};
            return wprom.windowOpen("/ui/cbsc/CBSC129P.xml", popInfo);
            //return wprom.popupOpen("/ui/cbsc/CBSC129P.xml", popInfo);
        });
        promise = promise.then(function(result) {
        	return;
        /*
            if (!mfn_IsEmpty(result)) {
                Ds_LTIDA00.setCellData(nRow, "sRqstYn", result);
                if (wconv.length(result) > 0) {
                    parent.Ds_LTIDA00.setCellData(0, "sPlanProgRankCd", "20");
                }
            }
        */    
        });
    }
    return promise;
};

