        wprom.alert("비밀번호 를 입력하세요.");
        edt_userPwd.focus();
        flag_login = false;        
    }
    /*
    SOBJ_LOGIN.WSDL = gds.gWsdlAdmin;
    var MethodObj = gfn_CreateMethodObj(SOBJ_LOGIN, "LoginTask", "checkApprovalPwd", "", "");

    //gfn_SetParam(MethodObj, "sEmpNo"   , "5140021");
    gfn_SetParam(MethodObj, "sEmpNo", util.getGdsValue("gUserID"));
    gfn_SetParam(MethodObj, "sPassword", edt_userPwd.getValue());
    gfn_CallService(SOBJ_LOGIN, DOBJ_LOGIN, MethodObj);
    */
	var MethodObj = new callService("com.inswave.admin.task.LoginTask", "checkApprovalPwd");
	MethodObj.setId("checkApprovalPwd");
	//MethodObj.setParam("sAgentCode", gUserID);
    MethodObj.setParam( "sEmpNo", util.getGdsValue("gUserID"));
    MethodObj.setParam( "sPassword", edt_userPwd.getValue());

	MethodObj.setUrl("/contract/CBSC158SController.do");
	MethodObj.setXPath("//*/PWD");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
	util.jSonDsCreate(_tranSync.getDsJson("PWD"), "DsWs_Login");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "WARNING");
	util.setRowPosition(DsWs_Login, 0);

    if (DsWs_Login.getCellData(0, "sPass") == "Y") {
        edt_userPwd.setValue("");
        flag_login = true;
    } else {
        wprom.alert("비밀번호가 틀렸습니다.");
        edt_userPwd.setValue("");
        edt_userPwd.focus();
		return;
    }
    return flag_login;
};

/****************************************************************************
 * function 명 : lfn_ConuResult_Radio_OnChanged
 * 내용        : 승인사유 라디오번튼 이벤트.
 ****************************************************************************/
scwin.lfn_ConuResult_Radio_OnChanged = function(info) {
    if ( info.value == "03") {
        cmb_UdrtkRjResn.setDisabled(false);
    } else {
        cmb_UdrtkRjResn.setDisabled(true);
        cmb_UdrtkRjResn.setValue("");
    }
};

/****************************************************************************
 * function 명 : btn_Accd_OnClick
 * 내용        : 당사사고조회 버튼.
 ****************************************************************************/
scwin.btn_Accd_OnClick = function(e) {
	wprom.alert("추후개발예정");
	return;

    var promise = Promise.resolve();
    var pi_nRow = Ds_LTIDA00.getRowPosition();
    var arg;
    if (pi_nRow < 0) {
        pi_nRow = 0;
    }
    arg = "sInrpsId=" + wconv.quote(Ds_LTIDA00.getCellData(pi_nRow, "sOwnrDscrNo"));
    promise = promise.then(function(rtnVal) {
        // 주민번호 
        return wprom.popupOpen("/CMC00461400s.xml", arg, 620, 288); 
        //	 
    });
    return promise;
};
//---------------------------------------------------------------------------
// function 명 : grd_UdrtkLmitList_OnMouseOver(obj)
// 내용        : 인수제한사유 그리드 OnMouseOver(ToolTipText)
//---------------------------------------------------------------------------
scwin.grd_UdrtkLmitList_OnMouseOver = function(obj, nPosX, nPosY, nRow, nCell, nPivotIndex) {
    if (nCell == 2 && grd_UdrtkLmitList.getCellData(nRow, nCell) != null) {
        grd_UdrtkLmitList.ToolTipText = grd_UdrtkLmitList.getCellDisplayData("Body", nRow, nCell);
    }
};

/****************************************************************
 * function 명 : lfn_EnterKey_OnKeyDown
 * 내용        : 조회조건 엔터 이벤트
 ****************************************************************/
scwin.lfn_EnterKey_OnKeyDown = function(e) {
    if ((e.keyCode == 13) && (sOnlySrarch != "1")) {
        scwin.lfn_save();
    }
};

