/****************************************************************
 * function 명 : getUserInfo
 * 내용        : 승인요청자 정보 조회
 ****************************************************************/
scwin.getAplcInfo = function(sAplc) {
    var sCelPhone = "";
    //취급자정보조회
    wconv.clear(DsWs_ADMAB01);
    /*
    SOBJ_User.WSDL = gds.gWsdlClaim;
    var MethodObj = gfn_CreateMethodObj(SOBJ_User, "ClaimAdminTask", "selUserInfo", "", "");
    gfn_SetParam(MethodObj, "sUserID", sAplc);
    gfn_CallService(SOBJ_User, DOBJ_User, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.claim.task.ClaimAdminTask", "selUserInfo");
	MethodObj.setId("selUserInfo");
	MethodObj.setParam("sUserID", sAplc);

	MethodObj.setUrl("/contract/CBSC158SController.do");
	MethodObj.setXPath("//*/ADMAB01");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "DsWs_ADMAB01");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "WARNING");

    //trace(DsWs_ADMAB01.SaveXML());
    sCelPhone = DsWs_ADMAB01.getCellData(0, "sCellPhone1") + DsWs_ADMAB01.getCellData(0, "sCellPhone2") + DsWs_ADMAB01.getCellData(0, "sCellPhone3");
    return sCelPhone;
};

//expression
scwin.fn_exprJobGradCd = function(data, formattedData, rowIndex, colIndex) {
	return Ds_LTIDA00.getCellData(rowIndex,"sJobGradCd")+"급 /"+Ds_LTIDA00.getCellData(rowIndex,"sJobNm");
};
//expression
scwin.fn_exprInrpsCd = function(data, formattedData, rowIndex, colIndex) {
	return gfn_changeMask(Ds_LTIDA00.getCellData(rowIndex,"sInrpsCd"), 1);
};
//expression
scwin.fn_exprDate = function(data, formattedData, rowIndex, colIndex) {
	var sStrtDate = Ds_LTIEA00_01.getCellData( rowIndex , "sInsurStrtdate" );
	var sEndDate = Ds_LTIEA00_01.getCellData( rowIndex , "sInsurEndDate" );
	return sStrtDate.substr(0,4)+"-"+sStrtDate.substr(4,2)+"-"+sStrtDate.substr(6,2)+"~"+sEndDate.substr(0,4)+"-"+sEndDate.substr(4,2)+"-"+sEndDate.substr(6,2);
};
