var sInrpsCd;
var sCrtorCd;
var fv_args;
var parentWin;
/****************************************************************************
 * function 명 : O210_Pop2_OnLoadCompleted  
 * 내용        : 화면오픈시 처리.
 ****************************************************************************/
scwin.init = function() {
	wprom.windowOpenInit(O210_Pop2);
	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
	} catch(e) {}

    sInrpsCd = fv_args.sInrpsCd;
    sCrtorCd = fv_args.sCrtorCd;
	//N1711
	wconv.clear(Ds_MinsrRelnCd);//.ClearData();
	wconv.clear(DsWs_AqamtChnlCd);//.ClearData();
	gfn_getCommonCodes("Ds_MinsrRelnCd DsWs_AqamtChnlCd", "MinsrRelnCd AqamtChnlCd", "", "");
	//취득경로
	//DsWs_AqamtChnlCd.InsertRow(0);
	//DsWs_AqamtChnlCd.SetColumn(0, "sCd", "");
	//DsWs_AqamtChnlCd.SetColumn(0, "sHnglCdName", "- 선택 -");
	//cmb_AqamtChnlCd.index = 0;
	parentWin  = wprom.getParamObject();
	//Ds_MinsrRelnCd.copy(DsWs_MinsrRelnCd);
	wconv.copy(Ds_LTIDA00 ,parentWin.Ds_LTIDA00);
	wconv.copy(Ds_Pibo ,parentWin.Ds_Pibo);

	//trace("Ds_LTIDA00:"+Ds_MinsrRelnCd.SaveXML());

	//
    scwin.setCombo();
    scwin.lfn_search();
};

/****************************************************************************
 * function 명 : lfn_search  
 * 내용        : 인수심사결재 - 피보험자 정보 및 심사정보 조회
 ****************************************************************************/
 //비동기 호출 
scwin.lfn_search = function() {
	util.eventAsyncCall(this, scwin.lfn_search_sync, arguments);
};

scwin.lfn_search_sync = function() {
    DsWs_LTIEA00.removeAll();
    Ds_LTIEA00.removeAll();
    DsWs_LTIEA00_Detail.removeAll();
    Ds_LTIEA00_Detail.removeAll();
    Ds_LTIDA02.removeAll();
    /*
    SOBJ_List.WSDL = gds.gWsdlBasis; //기본 웹서비스
    var MethodObj = gfn_CreateMethodObj(SOBJ_List, "com.inswave.longterm.ssr.sst.task.DBB170Task", "getJugmCarrList", "", "");
    if (cmb_SearchCd.getValue() == "01") {
        gfn_SetParam(MethodObj, "sInrpsCd", sInrpsCd); //피보험자주민번호
    } else if (cmb_SearchCd.getValue() == "02") {
        gfn_SetParam(MethodObj, "sCrtorCd", sCrtorCd); //소유자주민번호
    }
    gfn_SetParam(MethodObj, "sConuResltCd", rdo_ConuResult.getValue()); //심사결과코드
    var result = gfn_CallService(SOBJ_List, DOBJ_List, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB170Task", "getJugmCarrList");
	MethodObj.setId("getJugmCarrList");
    if (cmb_SearchCd.getValue() == "01") {
        MethodObj.setParam( "sInrpsCd", sInrpsCd); //피보험자주민번호
    } else if (cmb_SearchCd.getValue() == "02") {
        MethodObj.setParam("sCrtorCd", sCrtorCd); //소유자주민번호
    }
    MethodObj.setParam("sConuResltCd", rdo_ConuResult.getValue()); //심사결과코드
	MethodObj.setUrl("/contract/CBSC160PController.do");
	MethodObj.setXPath("//hashtable/data[@hashkey='lTIEA00ListDoc']/vector/data/LTIEA00_14");
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA15ListDoc']/vector/data/LTIDA15");	
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_LTIEA00.setJSON (_tranSync.getDsJson("LTIEA00_14")); // dataset 데이터
	DsWs_LTIEA00_Detail.setJSON (_tranSync.getDsJson("LTIDA15")); // dataset 데이터

    //오류처리
    if (WARNING.getRowCount() != 0) {
        wprom.alert(WARNING.getCellData(0, "msg"));
        return;
    } else {
        if (DsWs_LTIEA00.getRowCount() == 0) {
            wprom.alert("조회건이 없습니다.");
        } else {
            wconv.copy(Ds_LTIEA00, DsWs_LTIEA00);
            scwin.lfn_searchDetail(0);
        }
    }
};

/****************************************************************************
 * function 명 : lfn_searchDetail  
 * 내용        : 인수심사결재 - 각 청약번호에 해당하는 상세내역 조회.
 ****************************************************************************/
scwin.lfn_searchDetail = function(nRow) {
	/*orign
    SOBJ_List.WSDL = gds.gWsdlBasis; //기본 웹서비스
    var MethodObj = gfn_CreateMethodObj(SOBJ_List, "com.inswave.longterm.ssr.sst.task.DBB170Task", "getJugmCarrDetailList", "", "");
    gfn_SetParam(MethodObj, "sPlanNo", Ds_LTIEA00.getCellData(nRow, "sPlanNo"));
    var result = gfn_CallService(SOBJ_List, DOBJ_List, MethodObj);
	*/
	wconv.clear(WARNING);
	var MethodObj = new callService("com.inswave.longterm.ssr.sst.task.DBB170Task", "getJugmCarrDetailList");
	MethodObj.setId("getJugmCarrDetailList");
	if( cmb_SearchCd.getValue() == "01" ) {
		MethodObj.setParam( "sCcndResno", sInrpsCd);
	} else if( cmb_SearchCd.getValue() == "02" ) {
		MethodObj.setParam( "sCcndResno", sCrtorCd);
	}	
    MethodObj.setParam("sPlanNo", Ds_LTIEA00.getCellData(nRow, "sPlanNo"));
	MethodObj.setUrl("/contract/CBSC160PController.do");
	//MethodObj.setXPath("//hashtable/data[@hashkey='lTIEA00ListDoc']/vector/data/LTIEA00_14");
	MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA15ListDoc']/vector/data/LTIDA15");
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA02ListDoc']/vector/data/LTIDA02");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_LTIEA00_Detail.setJSON (_tranSync.getDsJson("LTIDA15")); // dataset 데이터
	Ds_LTIDA02.setJSON (_tranSync.getDsJson("LTIDA02")); // dataset 데이터

    //	오류처리
    if (WARNING.getRowCount() != 0) {
        wprom.alert(WARNING.getCellData(0, "msg"));
        return;
    } else {
        wconv.copy(Ds_LTIEA00_Detail, DsWs_LTIEA00_Detail); // Ds_LTIEA00_Detail.AddColumn("sInrpsName");
        Ds_LTIEA00_Detail.setRowPosition(0);	
		wconv.filter(Ds_LTIDA02,"wconv.pos(sScrnIndcYn, 'Y') >= 0",true);
		cmb_AqamtChnlCd.setValue( Ds_LTIDA02.getCellData(0, "sAqamtChnlCd"));
		//alert(Ds_LTIDA02.getCellData(0, "sAqamtChnlCd"));

        // if( cmb_SearchCd.Value == "01" ){	
        // Ds_LTIEA00_Detail.SetColumn(0,"sInrpsName",sInrpsName);
        // } else if( cmb_SearchCd.Value == "02" ) {
        // Ds_LTIEA00_Detail.SetColumn(0,"sInrpsName",sCrtorName);
        // }
    }
};

/****************************************************************************
 * function 명 : setCombo  
 * 내용        : 주민번호콤보 셋팅
 ****************************************************************************/
scwin.setCombo = function() {
    if (cmb_SearchCd.getValue() == "01") {
        medt_SearchNm.setValue(sInrpsCd);
    } else if (cmb_SearchCd.getValue() == "02") {
        medt_SearchNm.setValue(sCrtorCd);
    }
};

/****************************************************************************
 * function 명 : grd_ConuResltList_OnCellClick  
 * 내용        : 그리드 클릭시 상세내역 조회.
 ****************************************************************************/
scwin.grd_ConuResltList_OnCellClick = function(nRow,nCell){//obj, nRow, nCell, nX, nY, nPivotIndex) {
    //scwin.lfn_searchDetail(row);
	//lfn_searchDetail(nRow); 
	//var nSeq = parent.nSeq.Text;
	if(nCell == "7") {
		var arg = {};
		arg.sRqstYn=Ds_LTIEA00.getCellData(nRow, "sRqstYn");//고지사항입력여부

		if(Ds_LTIEA00.getCellData(nRow, "sRqstYn") == "") {
			arg.sNotcGdFlag="00"; //고지상품구분(운전구분을 고지상품구분으로 대체)
		} else {
			arg.sNotcGdFlag=Ds_LTIEA00.getCellData(nRow, "NotcGdFlag");
		}
		arg.sInrpsCd=Ds_LTIEA00.getCellData(nRow, "sInrpsCd");//피보험자코드
		arg.sInrpsName=Ds_LTIEA00.getCellData(nRow, "sInrpsName");//성명 
		arg.sCrtorRelnName=grd_ConuResltList.getCellDisplayData(nRow,8);//관계명 sMinsrRelnCd
		arg.sPlanNo=Ds_LTIEA00.getCellData(nRow, "sPlanNo");//설계번호
		arg.sPsnGroupFlgcd=Ds_LTIEA00.getCellData(nRow, "sPsnGroupFlgcd");//개인단체구분
		arg.nPsnGroupSeqno=Ds_LTIEA00.getCellData(nRow, "nInrpsSeqno");//seq
		arg.sSsrtDate=Ds_LTIEA00.getCellData(nRow,"sSsrtDate");//청약일자
		arg.sDrveFlagCd=Ds_LTIEA00.getCellData(nRow, "sDrveFlagCd");//운전구분
		arg.sJobName=Ds_LTIEA00.getCellData(nRow, "sJobName");//운전구분
		arg.sOnlySrarch = fv_args.sOnlySrarch;
		//alert("고지사항입력여부:"+Ds_LTIEA00.getCellData(nRow, "sRqstYn"));
		//alert("고지상품구분:"+Ds_LTIEA00.getCellData(nRow, "NotcGdFlag"));
		//alert("피보험자코드:"+Ds_LTIEA00.getCellData(nRow, "sInrpsCd"));
		//alert("성명:"+Ds_LTIEA00.getCellData(nRow, "sInrpsName"));
		//alert("관계명:"+grd_ConuResltList.GetCellText("Body",nRow,8));
		//alert("설계번호:"+Ds_LTIEA00.getCellData(nRow, "sPlanNo"));

		//저축상품 여부
		var sSvtYn = "N";
		if(parentWin.DsWs_PrdAttr.getCellData(0,"GdFlgcd") == "2") {
			sSvtYn = "Y";
		}
		arg.sSvtYn = sSvtYn;
console.log(arg);		
        var pObj = {
        	"Ds_LTIDA00": Ds_LTIDA00,
        	"Ds_Pibo":Ds_Pibo        	
        };
       	var popInfo = {
      		title : "피보험자고지사항",
			width : 875,
			height : 869,        	
       		indata : arg,
       		"parentObject":pObj
       	};
        wprom.windowOpen("/ui/cbsc/CBSC129P.xml", popInfo);
	} else {
		scwin.lfn_searchDetail(nRow);
	}    
};

/****************************************************************************
 * function 명 : lfn_Btn_Close_OnClick  
