scwin.init = function(){
};
/****************************************************************************
 * function 명 : lfn_Btn_Confirm_OnClick  
 * 내용        : 팝업닫기.
 ****************************************************************************/
scwin.lfn_Btn_Confirm_OnClick = function() {
    var sArg = edt_icpsId.getValue();
    sArg += "^" + edt_icpsPw.getValue();
    //SetReg("UserDefICPSInfo", sArg);
    wprom.close(sArg);
};

/****************************************************************
 * function 명 : lfn_EnterKey_OnKeyDown
 * 내용        : 조회조건 엔터 이벤트
 ****************************************************************/
scwin.lfn_EnterKey_OnKeyDown = function(e){//obj, nChar, bShift, bCtrl, bAlt, LLParam, HLParam) {
    if (e.keyCode == 13) {
        var sArg = edt_icpsId.getValue();
        sArg += "^" + edt_icpsPw.getValue();
        //SetReg("UserDefICPSInfo", sArg);
        wprom.close(sArg);
    }
};
