//---------------------------------------------------------------------------
// function 명 : lfn_RadioOnChanged
// 내용        : 예/아니오 선택시 아니오일 경우 연관객체 초기화 시킴
//--------------------------------------------------------------------------- 
lfn_RadioOnChanged = function(info) {
	//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_01] [this.getID()] "+ this.getID() );

    var comp;
    var arrList;
    var arrListTmp;  
	arrList = this.getID().split("_");	 // split 처리
    var compCnt = B230_Div1.getChildrenCount();		// Components 총 수 
  	//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_02] [this.getID()] "+ this.getID() +"[this.getValue()]"+ this.getValue() +"[compCnt]"+ compCnt ); 

	for (i = 0; i < compCnt; i++) {	 //정렬할 배열 담기 
   		comp = B230_Div1.getAllChildren()[i];		// Components 담기 
		//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_02_1] [i_번째]"+ i +"[this.getID()]"+ this.getID() );

   		 if (util.isNull(comp)) {		// comp가 널 이면 다음 i번째로 진행
        	continue;
        }
 		if (comp.id.indexOf("Static") > -1 || comp.id.indexOf("static") > -1) {	     // static 존재하면(제외 시킴)  
 	  		continue;
        }   
        if (comp.id.indexOf("group") > -1) {	     // group 존재하면(제외 시킴) ;
 	  		continue;
        }          
		if (comp.id.indexOf("Shape") > -1) {	     // Shape 존재하면(제외 시킴)  
 	  		continue;
        }         
        if (comp.id.indexOf("wq_uuid") > -1) {	     // wq_uuid 존재하면(제외 시킴)  
 	  		continue;
        }  

    	var comp2 = {};  
    	comp2.id = comp.id;	// Component ID 
     	var tmpObj = WebSquare.util.getComponentById(comp2.id);   
     	comp2.getType = tmpObj.getPluginName();		// Component 타입
    	comp2.value = tmpObj.getValue();			// Component value  
    	comp2.enable = !tmpObj.getDisabled();	 		// Component disabled  Obj.getValue()
    	comp2.Obj   = tmpObj; 

		if (comp2.id.indexOf(parent.sDrveType) > 0) {	   // 같은 sDrveType이 존재하면
     		//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_M_21] [i_번째]"+ i +"[comp2.id]"+ comp2.id +"[arrList[1]]"+ arrList[1] +"[substr(arrList[1], 0, 4)]"+ wconv.substr(arrList[1], 0, 4)  +"[comp2.id.indexOf(wconv.substr(arrList[1], 0, 4))]"+ comp2.id.indexOf(wconv.substr(arrList[1], 0, 4)) );

			 if (comp2.id.indexOf(wconv.substr(arrList[1], 0, 4)) > 0) {	 // 4자리 같은 질의코드가 존재하면
            	arrListTmp = comp2.id.split("_");	 // split 처리
          		//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_M_51] [i_번째]"+ i +"[comp2.id]"+ comp2.id +"[arrList[1]]"+ arrList[1] +"[this.getID()]"+ this.getID() +"[this.getValue()]"+ this.getValue() );

				if (CBSC129P.sSvtYn != "Y" && arrListTmp[1] == "B0703") {	 	// 저축보험용은 비활성화  
	      			comp2.Obj.setValue("");   // 해당 컴포넌트와 관련된 하위 컴포넌트 널 처리   
	           		comp2.enable = false;
	           		comp2.Obj.setDisabled(true);		//  비활성화 처리 
					comp2.Obj.setReadOnly(true);		// 비 활성화 처리(ReadOnly)
	            	//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_97] [비활성화 처리] [i_번째]"+ i +"[comp2.id]"+ comp2.id +"[this.getID()] "+ this.getID() +"[this.getValue()]"+ this.getValue() );
	   		 	 }  // end of if ==> 저축보험용은 비활성화  

				if (wconv.substr(arrListTmp[1], 4, 1) != "0" && ((arrListTmp[1] >= "B0704" && arrListTmp[1] <= "B0706"))) {	   //예/아니오 객체는 빼고 나머지만 초기화 
					if (rad_B0900_1_1_0.getValue() == "N" && rad_B0700_1_1_0.getValue() == "N") {
						if (this.getValue() == "N") {		//  아니오 이면   
							comp2.Obj.setValue("");   // 해당 컴포넌트와 관련된 하위 컴포넌트 널 처리  
							comp2.enable = false;
							comp2.Obj.setDisabled(true);		// 비활성화 처리 
							comp2.Obj.setReadOnly(true);		// 비 활성화 처리(ReadOnly)
							//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_91_2] [비활성화 처리] [i_번째]"+ i +"[comp2.id]"+ comp2.id +"[this.getID()] "+ this.getID() +"[this.getValue()]"+ this.getValue() );
						} else { 
							comp2.enable = true;
							comp2.Obj.setDisabled(false);		// 활성화 처리
							comp2.Obj.setReadOnly(false);	// 활성화 처리(ReadOnly)
						}
					} else { 
						comp2.enable = true;
						comp2.Obj.setDisabled(false);		// 활성화 처리
						comp2.Obj.setReadOnly(false);	// 활성화 처리(ReadOnly)
					}
				} else if (wconv.substr(arrListTmp[1], 4, 1) != "0" && arrListTmp[1] != "B0600") { 
					if (this.getValue() == "N") {		//  아니오 이면    
						comp2.Obj.setValue("");   // 해당 컴포넌트와 관련된 하위 컴포넌트 널 처리  
						comp2.enable = false;
						comp2.Obj.setDisabled(true);		// 비활성화 처리  
						comp2.Obj.setReadOnly(true);		// 비 활성화 처리(ReadOnly)
					} else {  
						comp2.enable = true;
						comp2.Obj.setDisabled(false);		// 활성화 처리
						comp2.Obj.setReadOnly(false);	// 활성화 처리(ReadOnly)

					}		
				} // end of if ==>   //예/아니오 객체는 빼고 나머지만 초기화 
            }  //  end of if ==> 4자리 같은 질의코드가 존재하면

           if (this.getID().indexOf( "B070") > 0) {		//질의 B090, B070 인 경우 예외 처리(5.최근5년내 치료 radio, 6.10대질병 radio)
				arrListTmp = this.getID().split("_");	 // split 처리

                if (wconv.substr(arrListTmp[1], 4, 1) != "0" && ((arrListTmp[1] >= "B0704" && arrListTmp[1] <= "B0706"))) {  //  
                    if (rad_B0900_1_1_0.getValue() == "N" && rad_B0700_1_1_0.getValue() == "N") {
						if (this.getValue() == "N") {		//  아니오 이면   
							comp2.Obj.setValue("");   // 해당 컴포넌트와 관련된 하위 컴포넌트 널 처리  
							comp2.enable = false;
							comp2.Obj.setDisabled(true);		// 비활성화 처리
							comp2.Obj.setReadOnly(true);		// 비 활성화 처리(ReadOnly)							
						} else { 
							comp2.enable = true;
							comp2.Obj.setDisabled(false);		// 활성화 처리
							comp2.Obj.setReadOnly(false);	// 활성화 처리(ReadOnly)
						}
					} else { 
						comp2.enable = true;
						comp2.Obj.setDisabled(false);		// 활성화 처리
						comp2.Obj.setReadOnly(false);	// 활성화 처리(ReadOnly)
					}
                }
            }  // end of if ==>  //질의 B090, B070 인 경우 예외 처리 

		}  	// end of if ==> 같은 sDrveType이 존재하면
		// console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_12_end] [같은 질의코드를 찾음][i_번째]"+ i +"[comp2.id]"+ comp2.id +"[comp2.Obj.getValue()]"+ comp2.Obj.getValue() +"[parent.sDrveType]"+ parent.sDrveType );
	}	// end of for() 

	// console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_end]" );
};

//---------------------------------------------------------------------------
// function 명 : lfn_cal_OnCharChanged
// 내용        : 년월일 변경시 셋팅처리
//---------------------------------------------------------------------------
scwin.lfn_cal_OnCharChanged = function(info) { 	 // onviewchange
	//console.log("[/CBSC169S.xml] [lfn_cal_OnCharChanged()] ==> [년월일 변경시 셋팅처리] [TEST_01] [this.getID()]"+ this.getID() );

    var fromDate = ""; 
    var toDate = ""; 
    var carePriod = ""; 
    var newValue = info.newValue;   //선택 된 값
    //console.log("[/CBSC169S.xml] [lfn_cal_OnCharChanged()] ==> [년월일 변경시 셋팅처리] [TEST_01] [this.getID()]"+ this.getID() +"[newValue]"+ newValue );

    if (wconv.length(newValue) == 8) {  
        fromDate = wconv.substr(this.getID(), 0, 17) + "7";
        //dateText = "시작년월";
        toDate = wconv.substr(this.getID(), 0, 17) + "8";
        //toDateText = "종료년월";
        carePriod = "edt" + wconv.substr(this.getID(), 3, 14) + "2";
        //console.log("[/CBSC169S.xml] [lfn_cal_OnCharChange()] ==> [TEST_04] [this.getID()]"+ this.getID() +"[newValue]"+ newValue +"[carePriod]"+ carePriod +"[fromDate]"+ fromDate +"[toDate]"+ toDate );		

        eval(carePriod).setValue(eval(fromDate).getValue() + "~" + eval(toDate).getValue());   
		//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [년월일 변경시 셋팅처리] [TEST_end] [eval(carePriod).getValue()]"+ eval(carePriod).getValue() );
		//console.log("[/CBSC169S.xml] [lfn_RadioOnChanged()] ==> [년월일 변경시 셋팅처리] [TEST_end] [edt_B0704_2_2_2_02.getValue()]"+ edt_B0704_2_2_2_02.getValue() );
 	} 
};

//---------------------------------------------------------------------------
// function 명 : lfn_FromToCheck
// 내용        : 날짜 기간 입력시 시작/종료 체크
//---------------------------------------------------------------------------
var check = false;

//이벤트 중복발생 방지 
lfn_FromToCheck = function(obj) {
	//console.log("[/CBSC171S.xml] [lfn_FromToCheck()] ==> [날짜 기간 입력시 시작/종료 체크] [TEST_01] [obj.id]"+ obj.id );

    var fromDate = "";
    var dateText = "";
    var toDate = "";
    var toDateText = "";
    var fFlag = false;

    //시작일자
    if (wconv.substr(obj.id, 17, 1) == "7") {
        fromDate = wconv.substr(obj.id, 0, 17) + "7";
        dateText = "시작년월";
        toDate = wconv.substr(obj.id, 0, 17) + "8";
        toDateText = "종료년월";
    }

    //시작 종료 체크
    if (eval(fromDate).getValue() > 0) {
        if (wconv.length(wconv.replace(eval(fromDate).getValue(), "-", "")) != 8) {
            if (wconv.length(wconv.replace(eval(fromDate).getValue(), "-", "")) != 6) {
                wprom.alert(dateText + "을 정확히 입력해 주세요");
                eval(fromDate).setValue("");
                eval(fromDate).focus();
                fFlag = true;
            }
        } else if (wconv.length(wconv.replace(eval(toDate).getValue(), "-", "")) != 8) {
            if (wconv.length(wconv.replace(eval(toDate).getValue(), "-", "")) != 6) {
                wprom.alert(toDateText + "을 정확히 입력해 주세요");
                eval(toDate).setValue("");
                eval(toDate).focus();
                fFlag = true;
            }
        } else if (wconv.toNumber(wconv.replace(eval(fromDate).getValue(), "-", "")) > wconv.toNumber(wconv.replace(eval(toDate).getValue(), "-", ""))) {
            wprom.alert(dateText + "이 더 큽니다.");
            obj.setValue("");
            obj.focus();
            fFlag = true;
        }
    } else {}

    //console.log("[/CBSC171S.xml] [lfn_FromToCheck()] ==> [날짜 기간 입력시 시작/종료 체크] [TEST_end] [fFlag]"+fFlag);

    return fFlag;
};
