     					}
                    } else {
                        comp2.enable = true;
            			comp2.Obj.setDisabled(false);		// 활성화 처리
						comp2.Obj.setReadOnly(false);	// 활성화 처리(ReadOnly)
            			//console.log("[/CBSC172S.xml] [lfn_RadioOnChanged()] ==> [Radio 변경 이벤트 처리] [TEST_22] [활성화 처리] [i_번째]"+ i +"[comp2.id]"+ comp2.id +"[comp2.Obj.getValue()]"+ comp2.Obj.getValue() +"[comp2.enable)]"+ comp2.enable )

