//---------------------------------------------------------------------------
// function 명 : lfn_HeightCheck
// 내용        : BMI 계산
//---------------------------------------------------------------------------
scwin.lfn_HeightCheck = function(obj, strText) {
	//console.log("[/CBSC172S.xml] [lfn_HeightCheck()] ==> [BMI 계산] [TEST_01]" );

     if (this.id == "medt_A1901_2_3_0") {	// 몸무게  
	  	 //if (wconv.length(medt_A1902_2_3_0.getValue()) == 0) {	// 몸무게가 널이면
		if(util.isNull(medt_A1902_2_3_0.getValue())) { 		// 몸무게가 널이면
	  		//console.log("[/CBSC172S.xml] [lfn_HeightCheck()] ==> [BMI 계산] [TEST_05]" ); 
	  		//wprom.alert("몸무게를 입력하세요.");
			medt_A1902_2_3_0.focus();
			return false;
		}

    } else {	// 키 
    	  // if (wconv.length(medt_A1901_2_3_0.getValue()) == 0) {	// 몸무게가 널이면
     	if(util.isNull(medt_A1901_2_3_0.getValue())) { 		//  키가 널이면
     		//wprom.alert("키를 입력하세요.");
        	medt_A1901_2_3_0.focus();
          return false;
        }  
    } 

	var sHeight =  WebSquare.bigDecimal(wconv.toNumber(medt_A1901_2_3_0.getValue()) /100.0);	 // 키 
	var sWeight =  wconv.toNumber(medt_A1902_2_3_0.getValue());		// 몸무게  
	var sHeight_pow = sHeight.pow(2);  // sHeight의 제곱승	// 몸무게   ==> nBMI: 25.61(170, 74) 
	var nBMI2 =  sWeight / sHeight_pow;  	//  BMI 계산(현재체중(키 의 제곱 
 	var nBMI = Math.round(nBMI2 * 100, 2) /100;  	// BMI 계산(현재체중(키 의 제곱): 소수점 2째자리 반올림 처리

	medt_A1903_2_3_0.setValue(nBMI);	 // BMI input 박스
	//console.log("[/CBSC172S.xml] [lfn_HeightCheck()] ==> [BMI 계산] [TEST_92] ===> [nBMI]"+ nBMI +"[[nBMI2]"+ nBMI2 +"[키_sHeight]"+ medt_A1901_2_3_0.getValue() +"[몸무게_sWeight]"+ sWeight );
};

//---------------------------------------------------------------------------
// function 명 : btn_Pop_Search1_OnClick
// 내용        :  년월선택 POPUP
//---------------------------------------------------------------------------
scwin.btn_Pop_Search1_OnClick = function(obj) {
    nX = ClientToScreenX(obj, 0) - 57;
    nY = ClientToScreenY(obj, 0);
    var ret;
    if (nX < 0) {
        nX = 0;
    }
    ret = PopDiv_Calendar.TrackPopup(nX, nY, obj.Width, obj.Height);
    if (obj.id == "btnA1501_100") {
        if (medt_A1501_2_3_0.Enable)
            medt_A1501_2_3_0 = wconv.wconv.replace(ret, "-", "");
    } else if (obj.id == "btnA1502_100") {
        if (medt_A1502_2_3_0.Enable)
            medt_A1502_2_3_0 = wconv.wconv.replace(ret, "-", "");
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_FromToCheck
// 내용        : 날짜 기간 입력시 시작/종료 체크
//---------------------------------------------------------------------------
var check = false;

lfn_FromToCheck = function(obj) {
	//console.log("[/CBSC172S.xml] [lfn_FromToCheck()] ==> [날짜 기간  체크] [TEST_01] [obj.id]"+ obj.id );

    var fromDate = "";
    var dateText = "";
    var toDate = "";
    var toDateText = "";
    var fFlag = false;

    //시작일자
    if (wconv.substr(obj.id, 17, 1) == "2") {
        fromDate = wconv.substr(obj.id, 0, 17) + "2";
        dateText = "시작년월";
        toDate = wconv.substr(obj.id, 0, 17) + "3";
        toDateText = "종료년월";
    } else if (wconv.substr(obj.id, 18, 1) == "7") {
        fromDate = wconv.substr(obj.id, 0, 18) + "7";
        dateText = "시작년월";
        toDate = wconv.substr(obj.id, 0, 18) + "8";
        toDateText = "종료년월";
    } else if (wconv.substr(obj.id, 4, 5) == "A1501") {
        fromDate = obj.id;
		dateText = "시작년월";
       // toDate = replace(obj.id, "A1501", "A1502");
      	toDate = wconv.replace(obj.id,  "A1501", "A1502");	// replace 처리
        toDateText = "종료년월";
    }

    //시작 종료 체크
    if (eval(fromDate).getValue() > 0) {
        if (wconv.length(wconv.replace(eval(fromDate).getValue(), "-", "")) != 8) {
            if (wconv.length(wconv.replace(eval(fromDate).getValue(), "-", "")) != 6) {
                wprom.alert(dateText + "을 정확히 입력해 주세요");
                eval(fromDate).setValue("");
                eval(fromDate).focus();
                fFlag = true;
            }
        } else if (wconv.length(wconv.replace(eval(toDate).getValue(), "-", "")) != 8) {
            if (wconv.length(wconv.replace(eval(toDate).getValue(), "-", "")) != 6) {
                wprom.alert(toDateText + "을 정확히 입력해 주세요");
                eval(toDate).setValue("");
                eval(toDate).focus();
                fFlag = true;
            }
        } else if (wconv.toNumber(wconv.replace(eval(fromDate).getValue(), "-", "")) > wconv.toNumber(wconv.replace(eval(toDate).getValue(), "-", ""))) {
            wprom.alert(dateText + "이 더 큽니다.");
            obj.setValue("");
            obj.focus();
            fFlag = true;
        }
    } else {}

	//console.log("[/CBSC172S.xml] [lfn_FromToCheck()] ==> [날짜 기간  체크] [TEST_end] [obj.id]"+ obj.id +"[fFlag]"+ fFlag);

    return fFlag;
};

//---------------------------------------------------------------------------
// function 명 : lfn_ValidationCheck
// 내용        :  밸리데이션체크 ==> 피보험자고지사항[B230_Pop] (/CBSC129P.xml)에서 호출함
//---------------------------------------------------------------------------
lfn_ValidationCheck = function(obj) {
	//console.log("[/CBSC172S.xml] [lfn_ValidationCheck()] ==> [V 체크] [TEST_01]" );

    var chflag = true;

    if (wconv.length(medt_A1600_2_3_0.getValue()) == 0 && medt_A1600_2_3_0.getDisabled() == false) {  // 월소득이 null 이거나 활성화이면  
        wprom.alert("고지사항을 입력하세요[F_01]");
        //medt_A1600_2_3_0.focus();
        //medt_A1600_2_3_0.BkColor = "yellow";
    	medt_A1600_2_3_0.focus();		  // 포커스 설정 	
		medt_A1600_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 

        chflag = false;

    } else if (wconv.length(medt_A1701_2_3_0.getValue()) == 0) {		// 음주횟수가 0  이면
        wprom.alert("고지사항을 입력하세요");
//        medt_A1701_2_3_0.focus();
//        medt_A1701_2_3_0.BkColor = "yellow";
        medt_A1701_2_3_0.focus();		  // 포커스 설정 	
		medt_A1701_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 
        chflag = false;
    } else if (wconv.length(medt_A1702_2_3_0.getValue()) == 0) {		// 음주량이  0  이면
        wprom.alert("고지사항을 입력하세요");
        medt_A1702_2_3_0.focus();
        medt_A1702_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 
        //medt_A1702_2_3_0.BkColor = "yellow";
        chflag = false;
    } else if (wconv.length(medt_A1801_2_3_0.getValue()) == 0) {
        wprom.alert("고지사항을 입력하세요");
        medt_A1801_2_3_0.focus();
        //medt_A1801_2_3_0.BkColor = "yellow";
        medt_A1801_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 
        chflag = false;
    } else if (wconv.length(medt_A1802_2_3_0.getValue()) == 0) {
        wprom.alert("고지사항을 입력하세요");
        medt_A1802_2_3_0.focus();
        //medt_A1802_2_3_0.BkColor = "yellow";
        medt_A1802_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 
        chflag = false;
    } else if (wconv.length(medt_A1902_2_3_0.getValue()) == 0) {
        wprom.alert("고지사항을 입력하세요");
        medt_A1902_2_3_0.focus();
        //medt_A1902_2_3_0.BkColor = "yellow";
        medt_A1902_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 
        chflag = false;
    } else if (wconv.length(medt_A1901_2_3_0.getValue()) == 0) {
        wprom.alert("고지사항을 입력하세요");
        medt_A1901_2_3_0.focus();
        //medt_A1901_2_3_0.BkColor = "yellow";
        medt_A1901_2_3_0.setStyle("background-color","yellow");		// 배경색 설정 
        chflag = false;
    }

    return chflag;
};

scwin.btn_sDlOccrDate1_OnClick = function(obj) {
    nX = ClientToScreenX(obj, 0) - 57;
    nY = ClientToScreenY(obj, 0);
    var ret;
    if (nX < 0) {
        nX = 0;
    }
    ret = PopDiv_Calendar.TrackPopup(nX, nY, obj.Width, obj.Height);
    if (ret != "" && ret != null) {
        msk_sDlOccrDate1.Text = ret;
    }
};
