			scwin.search(custNo, seqNo, sAddrType, nAddrSeq, nTelSeq, sSendType, nSendSeq); // trace("enabletype====" + EnableType);
		} else {
			//search(custNo, seqNo);
			wprom.alert("검색 조건이 없습니다. 확인후 다시 시도해 주세요.");
